// src/components/AudioTranscriber.jsx
import React, { useState, useRef, useEffect } from 'react';
import { useRecorder } from '../hooks/useRecorder';

export default function AudioTranscriber() {
  // 录音状态
  const [isRecording, setIsRecording] = useState(false);
  // 转写文本内容
  const [transcription, setTranscription] = useState('');
  // 语言参数，默认 auto
  const [lang, setLang] = useState('auto');
  // 是否开启声纹识别
  const [speakerVerification, setSpeakerVerification] = useState(false);

  // 自定义录音 Hook，管理录音功能
  const recorder = useRecorder();

  // WebSocket 实例引用
  const wsRef = useRef(null);
  // 定时发送音频的定时器引用
  const intervalRef = useRef(null);

  /**
   * 发送文本消息到 LLM 服务
   * @param {string} text - 要发送的文本
   */
  const sendTextMessage = async (text) => {
    if (!text.trim()) return;

    try {
      const response = await fetch('https://192.168.1.70:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text }),
      });

      if (!response.ok) {
        throw new Error('网络响应失败，状态码: ' + response.status);
      }

      const data = await response.json();
      console.log('文本发送成功，服务器返回：', data);
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  // 组件卸载时清理资源
  useEffect(() => {
    return () => {
      stopRecording();
    };
  }, []);

  /**
   * 启动录音并建立 WebSocket 连接
   */
  function startRecording() {
    if (isRecording) return;

    // 根据语言和声纹识别参数拼接 WebSocket URL 查询参数
    let params = [];
    if (lang) params.push(`lang=${encodeURIComponent(lang)}`);
    if (speakerVerification) params.push('sv=1'); // 声纹识别参数，勾选时添加
    const queryStr = params.length > 0 ? `?${params.join('&')}` : '';

    // 请根据实际服务器地址修改此处 ws 地址和端口
    const wsUrl = `ws://192.168.168.77:6007/ws/transcribe${queryStr}`;
    const ws = new WebSocket(wsUrl);
    ws.binaryType = 'arraybuffer';

    ws.onopen = () => {
      console.log('WebSocket 已连接');

      // 启动录音
      recorder.start().catch((e) => {
        alert('启动录音失败: ' + e.message);
        ws.close();
      });

      // 定时发送音频数据（每 500ms 发送一次）
      intervalRef.current = setInterval(() => {
        if (ws.readyState === 1) {
          const audioBlob = recorder.getAudioBlob();
          if (audioBlob.size > 0) {
            console.log('发送音频数据，大小：', audioBlob.size);
            ws.send(audioBlob);
            recorder.clearBuffer();
          }
        }
      }, 500);
    };

    ws.onmessage = (evt) => {
      try {
        const resJson = JSON.parse(evt.data);
        if (resJson.code === 0) {
          // 收到转写正确结果
          const recognizedText = resJson.data || '无识别结果';

          // 更新转写文本展示
          setTranscription((prev) => prev + '\n' + recognizedText);

          // 识别到有效文本时，发送给后端 LLM 服务
          if (recognizedText && recognizedText.trim() && recognizedText !== '无识别结果') {
            sendTextMessage(recognizedText);
          }
        } else {
          // 其他情况输出原始数据
          setTranscription((prev) => prev + '\n' + evt.data);
        }
      } catch (e) {
        console.error('解析转写消息失败', e);
        setTranscription((prev) => prev + '\n' + evt.data);
      }
    };

    ws.onclose = () => {
      console.log('WebSocket 已关闭');
      stopRecording();
    };

    ws.onerror = (e) => {
      console.error('WebSocket 错误', e);
    };

    wsRef.current = ws;
    setIsRecording(true);
  }

  /**
   * 停止录音并关闭 WebSocket 连接
   */
  function stopRecording() {
    if (!isRecording) return;

    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }
    recorder.stop();

    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }

    setIsRecording(false);
  }

  return (
    <div style={{ maxWidth: 700, margin: '20px auto', fontFamily: 'Arial, sans-serif' }}>
      {/* 录音控制按钮，改用本地圆形麦克风图片 */}
      <button
        onClick={() => (isRecording ? stopRecording() : startRecording())}
        style={{
          background: 'none',
          border: 'none',
          padding: 0,
          cursor: 'pointer',
          marginBottom: 12,
          width: 48,
          height: 48,
          outline: 'none',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          filter: isRecording ? 'drop-shadow(0 0 5px red)' : 'none',
          transition: 'filter 0.3s ease',
        }}
        aria-label={isRecording ? 'Stop Recording' : 'Start Recording'}
      >
        <img
          src="/core/client/web/ai-butler/image/mic-icon.png"
          alt="麦克风"
          className="mic-icon"
          id="start"
          style={{ width: 32, height: 32, userSelect: 'none', pointerEvents: 'none' }}
          draggable={false}
        />
      </button>

      {/* 语言输入 */}
      <div style={{ marginBottom: 12 }}>
        <label htmlFor="langInput" style={{ marginRight: 8, color: 'white' }}>
          Language:
        </label>
        <input
          id="langInput"
          type="text"
          value={lang}
          onChange={(e) => setLang(e.target.value)}
          disabled={isRecording}
          style={{
            padding: 4,
            fontSize: 14,
            width: 120,
            backgroundColor: '#333',
            border: '1px solid #666',
            borderRadius: 3,
            color: 'white',
          }}
          placeholder="auto"
        />
      </div>

      {/* 声纹识别开关 */}
      <div style={{ marginBottom: 20, color: 'white' }}>
        <label>
          <input
            type="checkbox"
            checked={speakerVerification}
            disabled={isRecording}
            onChange={(e) => setSpeakerVerification(e.target.checked)}
            style={{ marginRight: 6 }}
          />
          Speaker Verification
        </label>
      </div>

      {/* 转写结果展示区域 */}
      <div
        id="transcriptionResult"
        style={{
          whiteSpace: 'pre-wrap',
          backgroundColor: 'transparent', // 背景透明
          padding: 10,
          border: '1px solid transparent', // 边框透明
          borderRadius: 5,
          minHeight: 150,
          fontFamily: 'monospace',
          fontSize: 14,
          overflowY: 'auto',
          maxHeight: 300,
          userSelect: 'text',
          // 字体颜色要求：
          // 默认半透明灰色显示提示文字，识别文字用白色
          // 这里通过判断 transcription 是否有内容，来动态设置颜色
          color: transcription ? 'white' : 'rgba(255, 255, 255, 0.5)',
        }}
      >
        {transcription || '识别文字展示区...'}
      </div>
    </div>
  );
}
