// src/components/OldWebRTC.jsx
import React, { useEffect, useRef, useState } from 'react';

export default function OldWebRTC({ renderVideo = true, keepConnection = false }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);
  const pcRef = useRef(null);
  const textareaRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const animationFrameIdRef = useRef(null);
  
  const containerRef = useRef(null);

  const [useStun, setUseStun] = useState(false);
  const [started, setStarted] = useState(false);
  const [zoomWidth, setZoomWidth] = useState(600);
  const [videoReady, setVideoReady] = useState(false);
  const [alphaReady, setAlphaReady] = useState(false);
  const [processingMode, setProcessingMode] = useState('rgba');
  const [errorMessage, setErrorMessage] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [resourceCheckCount, setResourceCheckCount] = useState(0);
  const [maxResourceCheckCount] = useState(30);
  // 检测浏览器是否支持WebCodecs
  const [supportsWebCodecs, setSupportsWebCodecs] = useState(
    typeof window.VideoDecoder !== 'undefined' && 
    typeof window.MediaStreamTrackProcessor !== 'undefined'
  );

  // 立即更新的标志
  const isStartedRef = useRef(false);
  const isVideoReadyRef = useRef(false);
  const videoTracksRef = useRef([]);

  const initialWidth = 600;
  const initialHeight = 450;
  const SIGNALING_URL = '/offer';
  const zoomStep = 50;

  const [chatPos, setChatPos] = useState(() => {
    try {
      const saved = localStorage.getItem('chatbox-position');
      if (saved) {
        const pos = JSON.parse(saved);
        if (typeof pos.left === 'number' && typeof pos.top === 'number') {
          return pos;
        }
      }
    } catch {}
    return { left: 20, top: 20 };
  });

  const draggingRef = useRef(false);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  function onDragStart(e) {
    if (e.target.tagName.toLowerCase() === 'textarea') {
      draggingRef.current = false;
      return;
    }
    
    draggingRef.current = true;
    const targetRect = e.currentTarget.getBoundingClientRect();
    if (e.type === 'touchstart' && e.touches?.[0]) {
      dragOffsetRef.current = {
        x: e.touches[0].clientX - targetRect.left,
        y: e.touches[0].clientY - targetRect.top,
      };
    } else {
      dragOffsetRef.current = {
        x: e.clientX - targetRect.left,
        y: e.clientY - targetRect.top,
      };
    }
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragMove(e) {
    if (!draggingRef.current) return;
  
    let clientX, clientY;
    if (e.type === 'touchmove' && e.touches?.[0]) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else if (e.type === 'mousemove') {
      clientX = e.clientX;
      clientY = e.clientY;
    } else {
      return;
    }
  
    if (!containerRef.current || !e.currentTarget) return;
  
    const containerRect = containerRef.current.getBoundingClientRect();
    const chatRect = e.currentTarget.getBoundingClientRect();
    const chatWidth = chatRect.width;
    const chatHeight = chatRect.height;
  
    let newLeft = clientX - containerRect.left - dragOffsetRef.current.x;
    let newTop = clientY - containerRect.top - dragOffsetRef.current.y;
  
    newLeft = Math.min(Math.max(0, newLeft), containerRect.width - chatWidth);
    newTop = Math.min(Math.max(0, newTop), containerRect.height - chatHeight);
  
    setChatPos({ left: newLeft, top: newTop });
  
    e.preventDefault();
    e.stopPropagation();
  }
  
  function onDragEnd(e) {
    if (draggingRef.current) {
      draggingRef.current = false;
      localStorage.setItem('chatbox-position', JSON.stringify(chatPos));
      e.preventDefault();
      e.stopPropagation();
    }
  }

  // 计算透明度百分比
  function calculateTransparencyPercentage(data) {
    let transparentPixels = 0;
    
    for (let i = 3; i < data.length; i += 4) {
      if (data[i] === 0) {
        transparentPixels++;
      }
    }
    
    return (transparentPixels / (data.length / 4) * 100).toFixed(2);
  }

  // 使用WebCodecs API处理视频帧
  async function processVideoFrames(track) {
    try {
      if (!supportsWebCodecs) {
        console.log('WebCodecs不支持，尝试降级方案');
        fallbackToCanvasDrawing();
        return;
      }
      
      const canvas = canvasRef.current;
      const ctx = ctxRef.current;
      
      if (!canvas || !ctx) {
        console.error('缺少Canvas或上下文');
        return;
      }
      
      // 创建MediaStreamTrackProcessor来处理视频轨道
      const processor = new MediaStreamTrackProcessor({ track });
      const reader = processor.readable.getReader();
      
      // 处理每一帧
      while (true) {
        const { value: frame, done } = await reader.read();
        if (done) break;
        
        try {
          // 确保Canvas尺寸正确
          if (canvas.width !== frame.displayWidth || canvas.height !== frame.displayHeight) {
            console.log(`设置Canvas尺寸为 ${frame.displayWidth}x${frame.displayHeight}`);
            canvas.width = frame.displayWidth;
            canvas.height = frame.displayHeight;
          }
          
          // 清除画布，确保透明背景
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          
          // 使用drawImage直接绘制带Alpha通道的帧
          ctx.drawImage(frame, 0, 0, canvas.width, canvas.height);
          
          // 计算并显示透明度信息
          if (frame.format === 'RGBA' || frame.format === 'BGRA') {
            const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const transparencyPercentage = calculateTransparencyPercentage(imageData.data);
            setDebugInfo(`视频帧格式: ${frame.format} | 透明度: ${transparencyPercentage}%`);
            setAlphaReady(true);
          } else {
            setDebugInfo(`视频帧格式: ${frame.format} | 可能不包含Alpha通道`);
            setAlphaReady(false);
          }
        } finally {
          // 释放帧资源
          frame.close();
        }
      }
    } catch (error) {
      console.error('处理视频帧时出错:', error);
      setErrorMessage(`视频处理错误: ${error.message}`);
      // 出错时尝试降级方案
      fallbackToCanvasDrawing();
    }
  }

  // 降级方案：使用Canvas直接绘制视频
  function fallbackToCanvasDrawing() {
    console.log('使用Canvas直接绘制视频帧');
    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
    }
    processVideoFrame();
  }

  function processVideoFrame() {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    
    if (!video || !canvas || !ctx || !isStartedRef.current || !isVideoReadyRef.current) {
      const checkCount = resourceCheckCount + 1;
      setResourceCheckCount(checkCount);
      
      if (checkCount >= maxResourceCheckCount) {
        setErrorMessage('视频资源加载超时，请检查连接或刷新页面');
        console.error('视频资源加载超时，停止尝试');
        return;
      }
      
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    if (video.paused || video.ended) {
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    try {
      if (canvas.width !== video.videoWidth || canvas.height !== video.videoHeight) {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
      }

      // 清除画布，确保透明背景
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // 直接绘制视频到Canvas，依赖服务器处理的Alpha通道
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      
      // 尝试获取图像数据并分析透明度
      try {
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
        const transparencyPercentage = calculateTransparencyPercentage(imageData.data);
        setDebugInfo(`视频帧处理 | 透明度: ${transparencyPercentage}%`);
        
        if (transparencyPercentage > 1) {
          setAlphaReady(true);
        }
      } catch (e) {
        console.warn('无法获取图像数据:', e.message);
        setDebugInfo('无法分析透明度，可能是浏览器安全限制');
      }
    } catch (e) {
      console.error('处理视频帧时出错:', e);
      setErrorMessage(`视频渲染错误: ${e.message}`);
    }

    animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
  }

  async function negotiate() {
    console.log('开始建立WebRTC连接');
    const pc = pcRef.current;
    if (!pc) return;

    pc.addTransceiver('video', { direction: 'recvonly' });
    pc.addTransceiver('audio', { direction: 'recvonly' });

    try {
      const offer = await pc.createOffer();
      console.log('创建Offer成功');
      await pc.setLocalDescription(offer);
      console.log('设置本地描述成功');

      await new Promise((resolve) => {
        if (pc.iceGatheringState === 'complete') {
          console.log('ICE收集已完成');
          resolve();
        } else {
          const checkState = () => {
            if (pc.iceGatheringState === 'complete') {
              pc.removeEventListener('icegatheringstatechange', checkState);
              console.log('ICE收集完成');
              resolve();
            }
          };
          pc.addEventListener('icegatheringstatechange', checkState);
          console.log('正在收集ICE候选...');
        }
      });

      console.log('发送Offer到服务器:', SIGNALING_URL);
      const response = await fetch(SIGNALING_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sdp: pc.localDescription.sdp,
          type: pc.localDescription.type,
        }),
      });
      
      if (!response.ok) {
        throw new Error(`服务器响应错误: ${response.status}`);
      }
      
      const answer = await response.json();
      console.log('收到服务器Answer');
      await pc.setRemoteDescription(answer);
      console.log('设置远程描述成功，WebRTC连接建立完成');
    } catch (e) {
      console.error('协商失败:', e);
      alert('协商失败: ' + e.message);
      setErrorMessage(`WebRTC连接失败: ${e.message}`);
    }
  }

  function start() {
    console.log('启动WebRTC连接');
    if (isStartedRef.current) return;

    const config = { sdpSemantics: 'unified-plan' };
    const pc = new RTCPeerConnection(config);
    pcRef.current = pc;

    // 立即更新标志
    isStartedRef.current = true;
    
    // 监听连接状态变化
    pc.addEventListener('connectionstatechange', () => {
      console.log(`WebRTC连接状态变化: ${pc.connectionState}`);
      if (pc.connectionState === 'failed') {
        setErrorMessage('WebRTC连接失败');
      }
    });

    pc.addEventListener('track', (evt) => {
      console.log('收到新的媒体轨道:', evt.track.kind, evt.streams[0].id);
      
      if (evt.track.kind === 'video') {
        videoTracksRef.current.push(evt.track);
        console.log(`已存储视频轨道，总数: ${videoTracksRef.current.length}`);
        
        if (videoRef.current) {
          videoRef.current.srcObject = evt.streams[0];
          
          videoRef.current.addEventListener('loadedmetadata', () => {
            console.log('RGB视频元数据加载完成，尺寸:', videoRef.current.videoWidth, videoRef.current.videoHeight);
            
            if (videoRef.current.videoWidth > 0 && videoRef.current.videoHeight > 0) {
              isVideoReadyRef.current = true;
              setVideoReady(true);
              
              const canvas = canvasRef.current;
              if (canvas) {
                const ctx = canvas.getContext('2d', { 
                  willReadFrequently: true,
                  alpha: true // 明确请求支持透明度
                });
                
                if (!ctx) {
                  console.error('无法获取Canvas 2D上下文');
                  setErrorMessage('无法初始化视频渲染上下文');
                  return;
                }
                
                ctxRef.current = ctx;
                ctx.globalCompositeOperation = 'source-over';
                
                canvas.width = videoRef.current.videoWidth;
                canvas.height = videoRef.current.videoHeight;
                
                console.log('Canvas初始化完成，尺寸:', canvas.width, canvas.height);
                
                // 尝试使用WebCodecs处理视频帧
                if (supportsWebCodecs) {
                  console.log('使用WebCodecs处理视频帧');
                  processVideoFrames(evt.track);
                } else {
                  console.log('WebCodecs不支持，使用Canvas定时绘制方法');
                  processVideoFrame();
                }
              }
            } else {
              console.warn('视频尺寸无效，宽度:', videoRef.current.videoWidth, '高度:', videoRef.current.videoHeight);
            }
          }, { once: true });
          
          videoRef.current.addEventListener('canplay', () => {
            console.log('RGB视频可以播放，readyState:', videoRef.current.readyState);
            
            if (!started) {
              setStarted(true);
            }
            
            if (!supportsWebCodecs && isVideoReadyRef.current && ctxRef.current && !animationFrameIdRef.current) {
              processVideoFrame();
            }
          }, { once: true });
          
          videoRef.current.addEventListener('error', (e) => {
            console.error('RGB视频错误:', e.target.error);
            setErrorMessage(`RGB视频错误: ${e.target.error.message}`);
          });
        }
      } else if (evt.track.kind === 'audio') {
        console.log('收到音频流');
        if (audioRef.current) {
          audioRef.current.srcObject = evt.streams[0];
        }
      }
    });

    Promise.resolve().then(() => {
      setStarted(true);
      setErrorMessage('');
      negotiate();
      setZoomWidth(initialWidth);
    });
  }

  function stop() {
    console.log('停止WebRTC连接');
    if (!isStartedRef.current) return;

    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
      animationFrameIdRef.current = null;
    }

    isStartedRef.current = false;
    isVideoReadyRef.current = false;
    
    setStarted(false);
    setVideoReady(false);
    setAlphaReady(false);
    setProcessingMode('rgba');
    setErrorMessage('');
    setDebugInfo('');
    setResourceCheckCount(0);

    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => {
        track.stop();
      });
      videoRef.current.srcObject = null;
    }
    if (audioRef.current?.srcObject) {
      audioRef.current.srcObject.getTracks().forEach(track => {
        track.stop();
      });
      audioRef.current.srcObject = null;
    }

    if (!keepConnection) {
      setTimeout(() => {
        if (pcRef.current) {
          pcRef.current.close();
          pcRef.current = null;
        }
      }, 500);
    }
  }

  function zoomIn() {
    setZoomWidth(w => w + zoomStep);
  }
  function zoomOut() {
    setZoomWidth(w => (w - zoomStep > 0 ? w - zoomStep : w));
  }

  const [message, setMessage] = useState('');
  const [imageFile, setImageFile] = useState(null);

  const sendTextMessage = async () => {
    if (!message.trim()) return;
    try {
      console.log('发送文本消息:', message);
      const response = await fetch('https://192.168.168.77:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: message }),
      });
      if (!response.ok) throw new Error('状态码: ' + response.status);
      console.log('文本发送成功');
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  const sendMultiModal = async () => {
    if (!imageFile) return;
    try {
      console.log('发送多模态消息，包含图片');
      const reader = new FileReader();
      reader.onloadend = async () => {
        const response = await fetch('https://192.168.1.70:6010/multimodal', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: message.trim(), image: reader.result }),
        });
        if (!response.ok) throw new Error('状态码: ' + response.status);
        console.log('多模态发送成功');
        setImageFile(null);
        setMessage('');
      };
      reader.readAsDataURL(imageFile);
    } catch (error) {
      console.error('多模态发送失败：', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (imageFile) {
      await sendMultiModal();
    } else {
      await sendTextMessage();
      setMessage('');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  useEffect(() => {
    return () => {
      if (animationFrameIdRef.current) {
        cancelAnimationFrame(animationFrameIdRef.current);
      }
    };
  }, []);

  const frostedGlassBtnStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    color: 'white',
    fontWeight: 'bold',
    padding: '8px 16px',
    cursor: 'pointer',
    borderRadius: 4,
    outline: 'none',
    transition: 'all 0.3s',
    fontSize: 14,
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
  };

  const disabledBtnStyle = {
    ...frostedGlassBtnStyle,
    opacity: 0.4,
    cursor: 'not-allowed',
    boxShadow: 'none',
  };

  const getBtnHoverStyle = (disabled) => {
    if (disabled) return {};
    return {
      backgroundColor: 'rgba(255, 255, 255, 0.2)',
      boxShadow: '0 6px 20px rgba(255, 255, 255, 0.3)',
      transform: 'scale(1.05)',
    };
  };

  const [startBtnHover, setStartBtnHover] = useState(false);
  const [stopBtnHover, setStopBtnHover] = useState(false);
  const [zoomInHover, setZoomInHover] = useState(false);
  const [zoomOutHover, setZoomOutHover] = useState(false);
  const [interruptBtnHover, setInterruptBtnHover] = useState(false);

  const handleInterruptClick = async () => {
    try {
      console.log('发送打断请求');
      const resp = await fetch('http://192.168.168.77:6010/api/interrupt_speaking', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!resp.ok) throw new Error('状态码: ' + resp.status);
      console.log('打断请求发送成功');
    } catch (e) {
      console.error('打断接口异常:', e);
    }
  };

  return (
    <div ref={containerRef} style={{ maxWidth: 1280, position: 'relative', minHeight: '100vh' }}>
      <div className="option" style={{ marginBottom: 8 }}>
        <input
          id="use-stun"
          type="checkbox"
          checked={useStun}
          onChange={(e) => setUseStun(e.target.checked)}
          style={{ accentColor: 'white' }}
          disabled={started}
          hidden
        />
      </div>

      <div style={{ marginBottom: 8 }}>
        <button
          onClick={zoomIn}
          style={{
            ...frostedGlassBtnStyle,
            marginRight: 8,
            ...(zoomInHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomInHover(true)}
          onMouseLeave={() => setZoomInHover(false)}
          type="button"
        >
          放大
        </button>
        <button
          onClick={zoomOut}
          style={{
            ...frostedGlassBtnStyle,
            ...(zoomOutHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomOutHover(true)}
          onMouseLeave={() => setZoomOutHover(false)}
          type="button"
        >
          缩小
        </button>
      </div>

      <button
        onClick={start}
        disabled={isStartedRef.current}
        style={{
          ...(isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(startBtnHover && !isStartedRef.current ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStartBtnHover(true)}
        onMouseLeave={() => setStartBtnHover(false)}
      >
        启动
      </button>
      <button
        onClick={stop}
        disabled={!isStartedRef.current}
        style={{
          ...(!isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(stopBtnHover && isStartedRef.current ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStopBtnHover(true)}
        onMouseLeave={() => setStopBtnHover(false)}
      >
        停止
      </button>

      {/* 显示当前状态和错误信息 */}
      {errorMessage && (
        <div style={{ color: 'red', margin: '10px 0', padding: '10px', background: 'rgba(0,0,0,0.5)', borderRadius: 4 }}>
          <strong>错误:</strong> {errorMessage}
        </div>
      )}
      
      <div style={{ color: 'white', margin: '10px 0' }}>
        <strong>状态:</strong> {started ? '已连接' : '未连接'} | 
        <strong>模式:</strong> {processingMode} | 
        <strong>视频:</strong> {videoReady ? '就绪' : '加载中'} | 
        <strong>Alpha:</strong> {alphaReady ? '就绪' : '未检测到'} |
        <strong>WebCodecs:</strong> {supportsWebCodecs ? '支持' : '不支持'}
      </div>
      
      {/* 显示调试信息 */}
      {debugInfo && (
        <div style={{ color: 'yellow', margin: '10px 0', padding: '10px', background: 'rgba(0,0,0,0.5)', borderRadius: 4, whiteSpace: 'pre-wrap' }}>
          <strong>调试信息:</strong> {debugInfo}
        </div>
      )}

      {renderVideo && (
        <div id="media" style={{ marginTop: 20 }}>
          <h2 style={{ color: 'white' }}>管家</h2>

          <audio ref={audioRef} autoPlay />
          
          {/* 原视频标签（仅用于调试，设置为透明） */}
          <video
            ref={videoRef}
            style={{
              display: 'block',
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 10,
              zIndex: 100,
              border: '2px solid red',
              opacity: 0.3, // 设置为部分透明以便观察
            }}
            autoPlay
            playsInline
          />
          
          {/* Canvas（主显示区域） */}
          <canvas
            ref={canvasRef}
            style={{
              width: zoomWidth,
              height: (zoomWidth * initialHeight) / initialWidth,
              display: 'block',
              position: 'relative',
              zIndex: 10,
              border: '2px solid white',
              backgroundColor: 'transparent', // 确保Canvas背景透明
              mixBlendMode: 'normal', // 确保混合模式正确
            }}
          />
        </div>
      )}

      <div
        style={{
          position: 'absolute',
          left: chatPos.left,
          top: chatPos.top,
          width: 320,
          borderRadius: 6,
          padding: 12,
          color: 'white',
          userSelect: 'none',
          cursor: draggingRef.current ? 'grabbing' : 'grab',
          zIndex: 9999,
          backdropFilter: 'blur(10px)',
          backgroundColor: 'rgba(0,0,0,0.5)',
        }}
        onMouseDown={onDragStart}
        onMouseMove={onDragMove}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={onDragStart}
        onTouchMove={onDragMove}
        onTouchEnd={onDragEnd}
      >
        <form
          id="echo-form"
          onSubmit={handleSubmit}
          style={{ display: 'flex', flexDirection: 'column', gap: 8 }}
        >
          <textarea
            ref={textareaRef}
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            style={{
              width: '100%',
              resize: 'vertical',
              padding: 8,
              borderRadius: 8,
              border: '1px solid rgba(255,255,255,0.3)',
              backgroundColor: 'rgba(255,255,255,0.1)',
              color: 'white',
              outline: 'none',
            }}
            placeholder="请输入文本..."
            onClick={(e) => e.stopPropagation()}
          />
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
            <button
              type="button"
              onClick={handleInterruptClick}
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 16px',
                fontSize: 14,
              }}
              onMouseEnter={() => setInterruptBtnHover(true)}
              onMouseLeave={() => setInterruptBtnHover(false)}
            >
              打断说话
            </button>
            <label
              htmlFor="image-upload"
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 12px',
                cursor: 'pointer',
              }}
            >
              上传图片
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleImageChange}
            />
            <button
              type="submit"
              style={{ ...frostedGlassBtnStyle, padding: '6px 16px' }}
            >
              发送
            </button>
          </div>
          {imageFile && (
            <div style={{ margin: '10px 0', padding: 8, border: '1px solid rgba(255,255,255,0.3)', borderRadius: 4, backgroundColor: 'rgba(255,255,255,0.1)' }}>
              <p style={{ margin: '0 0 8px 0' }}>已选择图片: {imageFile.name}</p>
              <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={() => setImageFile(null)}
                  style={{
                    backgroundColor: 'rgba(255, 0, 0, 0.3)',
                    border: '1px solid rgba(255, 0, 0, 0.5)',
                    color: 'white',
                    padding: '4px 8px',
                    cursor: 'pointer',
                    borderRadius: 4,
                    outline: 'none',
                    fontSize: 12,
                  }}
                >
                  移除
                </button>
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}
