// D:\XM\AI-Sphere-Butler\core\client\src\components\AudioTranscriber.jsx
import React, { useState, useRef, useEffect } from 'react';
import { useRecorderContext } from '../contexts/RecorderProvider'; // 注意路径根据实际调整

export default function AudioTranscriber() {
  // 从全局录音上下文中取状态和操作
  const {
    isRecording,
    transcription,
    lang,
    speakerVerification,
    wakeWordEnabled,
    wakeWords,
    setLang,
    setSpeakerVerification,
    setWakeWordEnabled,
    setWakeWords,
    startRecording,
    stopRecording,
    setTranscription,
    // 新增：获取全局ASR面板可见性状态及更新方法
    asrPanelVisible,
    setAsrPanelVisible
  } = useRecorderContext();

  // 新增：面板显示/隐藏状态管理
  const [panelVisible, setPanelVisible] = useState(asrPanelVisible); // 初始值从全局状态获取
  const [isMinimized, setIsMinimized] = useState(() => {
    try {
      const saved = localStorage.getItem('audioTranscriberMinimized');
      return saved ? JSON.parse(saved) : false;
    } catch {
      return false;
    }
  });
  const [position, setPosition] = useState(() => {
    try {
      const saved = localStorage.getItem('audioTranscriberPosition');
      if (saved) {
        const pos = JSON.parse(saved);
        if (typeof pos.x === 'number' && typeof pos.y === 'number') {
          return pos;
        }
      }
    } catch {}
    // 默认位置：靠近左侧，垂直居中偏上
    return { x: 20, y: window.innerHeight / 3 };
  });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [isDoubleClick, setIsDoubleClick] = useState(false);
  const doubleClickTimerRef = useRef(null);
  const panelStateKey = 'audioTranscriberPanelVisible';

  // 拖拽相关引用
  const micButtonRef = useRef(null);
  const isDraggingRef = useRef(false);
  const offsetXRef = useRef(0);
  const offsetYRef = useRef(0);

  // localStorage 保存位置的 key
  const storageKey = 'audioTranscriberMicButtonPosition';

  // 新增声纹识别录音状态和上传状态（保持本组件私有状态）
  const [isSpeakerRecording, setIsSpeakerRecording] = useState(false);
  const [speakerUploadStatus, setSpeakerUploadStatus] = useState('');
  const speakerRecorderRef = useRef(null);
  const speakerAudioChunksRef = useRef([]);

  // *** 修改开始：定义一个函数来获取动态配置的 URLs ***
  const getDynamicUrls = () => {
    let serverUrls = {};
    try {
      const savedUrls = localStorage.getItem('serverUrls');
      if (savedUrls) {
        serverUrls = JSON.parse(savedUrls);
      }
    } catch (e) {
      console.error("AudioTranscriber: Failed to parse serverUrls from localStorage", e);
    }
    // 优先使用配置项，如果没有则 fallback 到环境变量或默认值
    // 注意：这里假设 ServerUrlConfig.jsx 中配置项的键名是 audioTranscriberQwenerUrl 和 audioTranscriberUploadSpeakerUrl
    // 如果键名不同，请相应调整
    const qwenerUrl = serverUrls.audioTranscriberQwenerUrl || 'https://192.168.168.77:9010/qwener';
    const uploadSpeakerUrl = serverUrls.audioTranscriberUploadSpeakerUrl || 'http://192.168.168.77:6007/api/uploadSpeaker';

    return {
      qwenerUrl: qwenerUrl,
      uploadSpeakerUrl: uploadSpeakerUrl
    };
  };
  // *** 修改结束 ***

  // 发送文本消息到 LLM 服务
  const sendTextMessage = async (text) => {
    if (!text.trim()) return;

    // *** 修改：动态获取 Qwener URL ***
    const { qwenerUrl } = getDynamicUrls();

    try {
      // const response = await fetch('https://192.168.168.77:9010/qwener', {
      const response = await fetch(qwenerUrl, { // 使用动态 URL
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text }),
      });
      if (!response.ok) {
        throw new Error('网络响应失败，状态码: ' + response.status);
      }
      const data = await response.json();
      console.log('文本发送成功，服务器返回：', data);
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  // 组件卸载时清理资源
  useEffect(() => {
    return () => {
      stopRecording();
      stopSpeakerRecording();
      if (doubleClickTimerRef.current) {
        clearTimeout(doubleClickTimerRef.current);
      }
    };
  }, []);

  // 监听全局面板可见性状态变化并同步到本地
  useEffect(() => {
    setPanelVisible(asrPanelVisible);
  }, [asrPanelVisible]);

  // 保存面板可见性状态到 localStorage 并同步到全局状态
  useEffect(() => {
    try {
      localStorage.setItem(panelStateKey, JSON.stringify(panelVisible));
    } catch {}
    setAsrPanelVisible(panelVisible);
  }, [panelVisible, setAsrPanelVisible]);

  // 保存最小化状态到 localStorage
  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberMinimized', JSON.stringify(isMinimized));
    } catch {}
  }, [isMinimized]);

  // 保存位置到 localStorage
  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberPosition', JSON.stringify(position));
    } catch {}
  }, [position]);

  // 监听语言和声纹识别开关的变化，保存状态到 localStorage，确保切换页时记忆选择
  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberLang', lang);
    } catch {}
  }, [lang]);

  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberSpeakerVerification', speakerVerification.toString());
    } catch {}
  }, [speakerVerification]);

  // 监听语音唤醒开关和唤醒词变化，保存到 localStorage
  useEffect(() => {
    try {
      localStorage.setItem('wakeWordEnabled', wakeWordEnabled.toString());
    } catch {}
  }, [wakeWordEnabled]);

  useEffect(() => {
    try {
      localStorage.setItem('wakeWords', wakeWords);
    } catch {}
  }, [wakeWords]);

  // 新增：加载面板显示状态（改为从localStorage加载，同时同步到全局状态）
  useEffect(() => {
    try {
      const savedVisible = localStorage.getItem(panelStateKey);
      if (savedVisible !== null) {
        const isVisible = JSON.parse(savedVisible);
        setPanelVisible(isVisible);
        setAsrPanelVisible(isVisible); // 同步到全局状态
      }
    } catch (e) {
      console.warn('读取面板状态失败:', e);
    }
  }, [setAsrPanelVisible]);

  // 拖拽开始
  const handleDragStart = (clientX, clientY) => {
    if (!micButtonRef.current) return;
    const rect = micButtonRef.current.getBoundingClientRect();
    offsetXRef.current = clientX - rect.left;
    offsetYRef.current = clientY - rect.top;
    isDraggingRef.current = true;
    setIsDragging(true);
  };

  // 拖拽移动
  const handleDragMove = (clientX, clientY) => {
    if (!isDraggingRef.current || !micButtonRef.current) return;
    const newX = clientX - offsetXRef.current;
    const newY = clientY - offsetYRef.current;
    // 限制拖拽范围在视窗内
    const maxX = window.innerWidth - micButtonRef.current.offsetWidth;
    const maxY = window.innerHeight - micButtonRef.current.offsetHeight;
    setPosition({
      x: Math.max(0, Math.min(newX, maxX)),
      y: Math.max(0, Math.min(newY, maxY))
    });
  };

  // 拖拽结束
  const handleDragEnd = () => {
    if (isDraggingRef.current) {
      isDraggingRef.current = false;
      setIsDragging(false);
    }
  };

  // 鼠标事件处理
  const onMouseDown = (e) => {
    // 防止拖拽时选中文本
    e.preventDefault();
    handleDragStart(e.clientX, e.clientY);
  };

  const onMouseMove = (e) => {
    handleDragMove(e.clientX, e.clientY);
  };

  const onMouseUp = () => {
    handleDragEnd();
  };

  // 触摸事件处理（移动端兼容）
  const onTouchStart = (e) => {
    if (e.touches.length === 1) {
      const touch = e.touches[0];
      handleDragStart(touch.clientX, touch.clientY);
    }
  };

  const onTouchMove = (e) => {
    if (e.touches.length === 1) {
      const touch = e.touches[0];
      handleDragMove(touch.clientX, touch.clientY);
    }
  };

  const onTouchEnd = () => {
    handleDragEnd();
  };

  // 绑定全局鼠标/触摸移动和结束事件，确保在任意位置都能拖动和释放
  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', onMouseMove);
      document.addEventListener('mouseup', onMouseUp);
      document.addEventListener('touchmove', onTouchMove, { passive: false });
      document.addEventListener('touchend', onTouchEnd);
    } else {
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);
      document.removeEventListener('touchmove', onTouchMove);
      document.removeEventListener('touchend', onTouchEnd);
    }

    return () => {
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);
      document.removeEventListener('touchmove', onTouchMove);
      document.removeEventListener('touchend', onTouchEnd);
    };
  }, [isDragging]);

  // 处理麦克风按钮点击（单击/双击）
  const handleMicClick = () => {
    if (isDoubleClick) {
      // 双击：切换面板显示/隐藏
      setPanelVisible(v => !v);
      setIsDoubleClick(false);
      if (doubleClickTimerRef.current) {
        clearTimeout(doubleClickTimerRef.current);
        doubleClickTimerRef.current = null;
      }
      return;
    }

    // 单击：开始录音（如果面板隐藏则显示面板）
    if (!panelVisible) {
      setPanelVisible(true);
    }

    if (!isRecording) {
      startRecording();
    } else {
      stopRecording();
    }
  };

  // 处理麦克风按钮双击
  const handleMicDoubleClick = () => {
    setIsDoubleClick(true);
    if (doubleClickTimerRef.current) {
      clearTimeout(doubleClickTimerRef.current);
    }
    doubleClickTimerRef.current = setTimeout(() => {
      setIsDoubleClick(false);
    }, 300); // 300ms 内的第二次点击视为双击
  };

  // 切换语言
  const toggleLang = () => {
    setLang(prev => prev === 'zh' ? 'en' : 'zh');
  };

  // 切换声纹识别
  const toggleSpeakerVerification = () => {
    setSpeakerVerification(prev => !prev);
  };

  // 切换语音唤醒
  const toggleWakeWord = () => {
    setWakeWordEnabled(prev => !prev);
  };

  // 更新唤醒词
  const updateWakeWords = (e) => {
    setWakeWords(e.target.value);
  };

  // 开始声纹识别录音
  const startSpeakerRecording = async () => {
    if (isSpeakerRecording) return;
    setSpeakerUploadStatus('正在请求录音权限...');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      speakerRecorderRef.current = mediaRecorder;
      speakerAudioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          speakerAudioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        setSpeakerUploadStatus('录音结束，准备上传...');
        const blob = new Blob(speakerAudioChunksRef.current, { type: 'audio/wav' });
        try {
          const formData = new FormData();
          formData.append('file', blob, 'speaker.wav');

          // *** 修改：动态获取上传 Speaker URL ***
          const { uploadSpeakerUrl } = getDynamicUrls();

          // const uploadUrl = 'http://192.168.168.77:6007/api/uploadSpeaker';
          const res = await fetch(uploadSpeakerUrl, { // 使用动态 URL
            method: 'POST',
            body: formData,
          });
          if (!res.ok) {
            throw new Error(`上传失败，状态码: ${res.status}`);
          }
          const result = await res.json();
          setSpeakerUploadStatus(result.message || '上传成功');
          console.log('声纹识别上传成功:', result);
        } catch (err) {
          console.error('声纹识别上传失败:', err);
          setSpeakerUploadStatus(`上传失败: ${err.message}`);
        }
      };

      mediaRecorder.start();
      setIsSpeakerRecording(true);
      setSpeakerUploadStatus('正在录音...');
      console.log('声纹识别录音已开始');
    } catch (err) {
      console.error('无法访问麦克风进行声纹识别:', err);
      setSpeakerUploadStatus(`录音失败: ${err.message}`);
    }
  };

  // 停止声纹识别录音
  const stopSpeakerRecording = () => {
    if (speakerRecorderRef.current && isSpeakerRecording) {
      speakerRecorderRef.current.stop();
      const tracks = speakerRecorderRef.current.stream.getTracks();
      tracks.forEach(track => track.stop());
      setIsSpeakerRecording(false);
      console.log('声纹识别录音已停止');
    }
  };

  // 清空识别结果
  const clearTranscription = () => {
    setTranscription('');
  };

  // 麦克风按钮样式
  const micButtonStyle = {
    position: 'fixed',
    left: `${position.x}px`,
    top: `${position.y}px`,
    width: 60,
    height: 60,
    borderRadius: '50%',
    backgroundColor: isRecording ? '#f44336' : 'rgba(255, 255, 255, 0.2)',
    border: '2px solid rgba(255, 255, 255, 0.5)',
    color: 'white',
    fontSize: 24,
    fontWeight: 'bold',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1001,
    userSelect: 'none',
    transition: 'background-color 0.3s, transform 0.2s',
    transform: isDragging ? 'scale(1.1)' : 'scale(1)',
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)',
  };

  // 面板容器样式
  const panelContainerStyle = {
    position: 'fixed',
    left: `${position.x + 70}px`, // 紧挨着麦克风按钮
    top: `${position.y}px`,
    width: 320,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    borderRadius: 10,
    padding: 16,
    zIndex: 1000,
    color: 'white',
    fontFamily: 'Arial, sans-serif',
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.5)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    transform: isMinimized ? 'translateX(-100%)' : 'translateX(0)',
    transition: 'transform 0.3s ease-in-out',
  };

  // 面板头部样式
  const panelHeaderStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
    paddingBottom: 8,
    borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
  };

  // 按钮基础样式
  const baseButtonStyle = {
    padding: '6px 12px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    color: 'white',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    borderRadius: 4,
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: 12,
    transition: 'background-color 0.2s',
  };

  // 按钮悬停样式
  const buttonHoverStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
  };

  // 开关容器样式
  const switchContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: 10,
    fontSize: 14,
  };

  // 文本区域样式
  const textareaStyle = {
    width: '100%',
    minHeight: 100,
    padding: 10,
    borderRadius: 6,
    border: '1px solid rgba(255, 255, 255, 0.3)',
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    color: 'white',
    fontSize: 14,
    resize: 'vertical',
    marginBottom: 12,
    outline: 'none',
  };

  return (
    <>
      {/* 悬浮麦克风按钮 */}
      <div
        ref={micButtonRef}
        style={micButtonStyle}
        onMouseDown={onMouseDown}
        onClick={handleMicClick}
        onDoubleClick={handleMicDoubleClick}
        onTouchStart={onTouchStart}
        onTouchEnd={(e) => {
          // 防止触摸结束时触发点击事件
          e.preventDefault();
          handleMicClick();
        }}
        aria-label={isRecording ? "停止录音" : "开始录音"}
        role="button"
        tabIndex={0}
      >
        {isRecording ? '⏹' : '🎤'}
      </div>

      {/* 识别面板 */}
      {panelVisible && (
        <div style={panelContainerStyle}>
          <div style={panelHeaderStyle}>
            <h3 style={{ margin: 0, fontSize: 16 }}>语音识别面板</h3>
            <div>
              <button
                onClick={() => setIsMinimized(!isMinimized)}
                style={baseButtonStyle}
                aria-label={isMinimized ? "展开面板" : "收起面板"}
              >
                {isMinimized ? '◀' : '▶'}
              </button>
              <button
                onClick={() => setPanelVisible(false)}
                style={baseButtonStyle}
                aria-label="关闭面板"
              >
                ×
              </button>
            </div>
          </div>

          {!isMinimized && (
            <>
              {/* 语言切换和声纹识别开关 */}
              <div style={switchContainerStyle}>
                <span>语言: {lang === 'zh' ? '中文' : 'English'}</span>
                <button
                  onClick={toggleLang}
                  style={baseButtonStyle}
                  aria-label={`切换到${lang === 'zh' ? '英文' : '中文'}`}
                >
                  切换
                </button>
              </div>

              <div style={switchContainerStyle}>
                <span>声纹识别: {speakerVerification ? '开' : '关'}</span>
                <button
                  onClick={toggleSpeakerVerification}
                  style={{
                    ...baseButtonStyle,
                    backgroundColor: speakerVerification ? 'rgba(76, 175, 80, 0.3)' : baseButtonStyle.backgroundColor,
                    borderColor: speakerVerification ? '#4CAF50' : baseButtonStyle.borderColor,
                  }}
                  aria-label={speakerVerification ? "关闭声纹识别" : "开启声纹识别"}
                >
                  {speakerVerification ? '开' : '关'}
                </button>
              </div>

              {/* 语音唤醒开关和输入框 */}
              <div style={switchContainerStyle}>
                <span>语音唤醒: {wakeWordEnabled ? '开' : '关'}</span>
                <button
                  onClick={toggleWakeWord}
                  style={{
                    ...baseButtonStyle,
                    backgroundColor: wakeWordEnabled ? 'rgba(76, 175, 80, 0.3)' : baseButtonStyle.backgroundColor,
                    borderColor: wakeWordEnabled ? '#4CAF50' : baseButtonStyle.borderColor,
                  }}
                  aria-label={wakeWordEnabled ? "关闭语音唤醒" : "开启语音唤醒"}
                >
                  {wakeWordEnabled ? '开' : '关'}
                </button>
              </div>
              {wakeWordEnabled && (
                <div style={{ marginBottom: 12 }}>
                  <label style={{ display: 'block', marginBottom: 4, fontSize: 12 }}>
                    唤醒词 (用逗号分隔):
                  </label>
                  <input
                    type="text"
                    value={wakeWords}
                    onChange={updateWakeWords}
                    placeholder="例如: 你好,助手"
                    style={{
                      width: '100%',
                      padding: 6,
                      borderRadius: 4,
                      border: '1px solid rgba(255, 255, 255, 0.3)',
                      backgroundColor: 'rgba(255, 255, 255, 0.05)',
                      color: 'white',
                      fontSize: 12,
                    }}
                    aria-label="输入唤醒词"
                  />
                </div>
              )}

              {/* 声纹识别录音区域 */}
              {speakerVerification && (
                <div style={{ marginBottom: 12 }}>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 6 }}>
                    <span style={{ fontSize: 14 }}>声纹识别训练:</span>
                    <button
                      onClick={isSpeakerRecording ? stopSpeakerRecording : startSpeakerRecording}
                      style={{
                        ...baseButtonStyle,
                        backgroundColor: isSpeakerRecording ? 'rgba(244, 67, 54, 0.3)' : baseButtonStyle.backgroundColor,
                        borderColor: isSpeakerRecording ? '#f44336' : baseButtonStyle.borderColor,
                      }}
                      aria-label={isSpeakerRecording ? "停止录音" : "开始录音"}
                    >
                      {isSpeakerRecording ? '⏹ 停止' : '⏺ 录音'}
                    </button>
                  </div>
                  {speakerUploadStatus && (
                    <div style={{ fontSize: 12, color: speakerUploadStatus.includes('失败') ? '#ff6666' : '#66ff66', marginTop: 4 }}>
                      {speakerUploadStatus}
                    </div>
                  )}
                </div>
              )}

              {/* 识别结果显示区域 */}
              <div style={{ marginBottom: 12 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 6 }}>
                  <span style={{ fontSize: 14 }}>识别结果:</span>
                  <button
                    onClick={clearTranscription}
                    style={baseButtonStyle}
                    aria-label="清空识别结果"
                  >
                    清空
                  </button>
                </div>
                <textarea
                  value={transcription}
                  readOnly
                  style={textareaStyle}
                  placeholder="语音识别结果将显示在这里..."
                  aria-label="语音识别结果"
                />
              </div>
            </>
          )}
        </div>
      )}

      {/* 内嵌样式 */}
      <style>{`
        /* 基础样式 */
        #audio-transcriber-container {
          font-family: Arial, sans-serif;
          user-select: none;
          pointer-events: auto;
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 4px;
        }
        #listening-text {
          font-size: 12px;
          color: rgba(128, 128, 128, 0.7);
          user-select: none;
          pointer-events: none;
          font-weight: 500;
          opacity: 0;
          transition: opacity 0.3s ease;
          height: 16px;
          line-height: 16px;
        }
        #listening-text.visible {
          opacity: 1;
        }
        #mic-button {
          background: none;
          border: none;
          padding: 0;
          outline: none;
          display: flex;
          align-items: center;
          justify-content: center;
          filter: none;
          transition: filter 0.3s ease;
          width: 68px;
          height: 68px;
          cursor: grab;
        }
        #mic-button.recording {
          filter: drop-shadow(0 0 5px red);
        }
        #mic-button:hover {
          filter: brightness(1.2);
        }
        #mic-button:active {
          cursor: grabbing;
        }
        #mic-icon {
          width: 100%;
          height: 100%;
          object-fit: contain;
          pointer-events: none;
        }
        #recording-indicator {
          position: absolute;
          top: -4px;
          right: -4px;
          width: 16px;
          height: 16px;
          background-color: #f44336;
          border-radius: 50%;
          border: 2px solid white;
          animation: pulse 1.5s infinite;
          pointer-events: none;
        }
        @keyframes pulse {
          0% { transform: scale(0.95); box-shadow: 0 0 0 0 rgba(244, 67, 54, 0.7); }
          70% { transform: scale(1); box-shadow: 0 0 0 10px rgba(244, 67, 54, 0); }
          100% { transform: scale(0.95); box-shadow: 0 0 0 0 rgba(244, 67, 54, 0); }
        }
        #audio-panel {
          position: fixed;
          left: 90px;
          top: 20px;
          width: 320px;
          background-color: rgba(0, 0, 0, 0.7);
          border-radius: 10px;
          padding: 16px;
          z-index: 1000;
          color: white;
          font-family: Arial, sans-serif;
          backdrop-filter: blur(10px);
          WebkitBackdropFilter: blur(10px);
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.5);
          border: 1px solid rgba(255, 255, 255, 0.2);
          transform: translateX(0);
          transition: transform 0.3s ease-in-out;
        }
        #audio-panel.hidden {
          transform: translateX(-100%);
        }
        #audio-panel-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 12px;
          padding-bottom: 8px;
          border-bottom: 1px solid rgba(255, 255, 255, 0.2);
        }
        #audio-panel-title {
          margin: 0;
          font-size: 16px;
        }
        #audio-panel-close-btn {
          padding: 4px 8px;
          background: none;
          border: 1px solid rgba(255, 255, 255, 0.3);
          color: white;
          border-radius: 4px;
          cursor: pointer;
          font-weight: bold;
          font-size: 14px;
          transition: background-color 0.2s;
        }
        #audio-panel-close-btn:hover {
          background-color: rgba(255, 255, 255, 0.2);
        }
        #controls-container {
          display: flex;
          flex-direction: column;
          gap: 10px;
          margin-bottom: 12px;
        }
        #controls-container label {
          display: flex;
          align-items: center;
          gap: 6px;
          font-size: 14px;
        }
        #controls-container input[type="text"],
        #controls-container input[type="checkbox"] {
          margin: 0;
        }
        #controls-container input[type="text"] {
          padding: 4px 8px;
          border-radius: 4px;
          border: 1px solid rgba(255, 255, 255, 0.3);
          background-color: rgba(255, 255, 255, 0.05);
          color: white;
          font-size: 12px;
        }
        #controls-container input[type="checkbox"] {
          width: 16px;
          height: 16px;
          accent-color: #4CAF50;
        }
        #speaker-record-container {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-bottom: 12px;
        }
        #speaker-record-button {
          position: relative;
          padding: 8px 16px;
          border: none;
          border-radius: 6px;
          background: linear-gradient(135deg, rgba(255,255,255,0.15) 0%, rgba(255,255,255,0.03) 100%);
          color: white;
          font-weight: bold;
          font-size: 14px;
          cursor: pointer;
          user-select: none;
          transition: all 0.3s ease;
          backdrop-filter: blur(10px);
          WebkitBackdropFilter: blur(10px);
          box-shadow: 0 4px 20px rgba(255, 255, 255, 0.15);
          border: 1px solid rgba(255, 255, 255, 0.3);
          display: flex;
          align-items: center;
          justify-content: center;
          outline: none;
          border-image: none;
        }
        #speaker-record-button:hover:not(:disabled) {
          background: rgba(255, 255, 255, 0.2);
          box-shadow: 0 8px 40px rgba(255, 255, 255, 0.3);
          transform: scale(1.05);
          filter: brightness(1.1);
        }
        #speaker-record-button:disabled {
          opacity: 0.4;
          cursor: not-allowed;
          box-shadow: none;
          background: rgba(255, 255, 255, 0.05);
          filter: none;
          transform: none;
        }
        /* 额外光泽层 */
        #speaker-record-button::before {
          content: '';
          position: absolute;
          top: -100%;
          left: -100%;
          width: 100%;
          height: 100%;
          background: radial-gradient(circle at 10% 10%, rgba(255, 255, 255, 0.3), transparent 10%);
          pointer-events: none;
          filter: blur(10px);
          opacity: 0.6;
          transition: opacity 0.3s ease;
          border-radius: inherit;
          z-index: 0;
        }
        /* 保留波纹效果的样式 */
        .ripple {
          position: absolute;
          border-radius: 150%;
          background: rgba(91, 155, 255, 0.7);
          animation: rippleEffect 1s ease-out;
          pointer-events: none;
          transform: scale(0);
          opacity: 0.75;
          z-index: 1;
        }
        @keyframes rippleEffect {
          to {
            transform: scale(4);
            opacity: 0;
          }
        }
        /* 新增：面板显示/隐藏控制 */
        #audio-panel-content {
          transition: all 0.3s ease;
        }
        #audio-panel-content.hidden {
          display: none;
        }
      `}</style>
    </>
  );
}
