import React, { useState, useRef } from 'react';

export default function TTSPanel() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadMessage, setUploadMessage] = useState('');
  const [chatText, setChatText] = useState('');
  const [chatResponse, setChatResponse] = useState('');
  const [chatLoading, setChatLoading] = useState(false);
  const fileInputRef = useRef(null);

  // 磨砂玻璃按钮通用样式
  const glassButtonStyle = {
    borderRadius: '8px',
    border: '2px solid #00e5ff',
    backgroundColor: 'rgba(0, 229, 255, 0.15)',
    color: '#00e5ff',
    fontWeight: 'bold',
    fontSize: 14,
    padding: '8px 16px',
    cursor: 'pointer',
    userSelect: 'none',
    outline: 'none',
    transition: 'background-color 0.3s, color 0.3s, box-shadow 0.3s',
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 20px rgba(0, 229, 255, 0.4)',
  };

  const glassButtonDisabledStyle = {
    ...glassButtonStyle,
    cursor: 'not-allowed',
    opacity: 0.5,
    boxShadow: 'none',
    color: '#00a5bf',
    borderColor: '#00a5bf',
    backgroundColor: 'rgba(0, 165, 191, 0.15)',
  };

  // 选择文件事件
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setUploadMessage('');
  };

  // 上传音频文件
  const handleUpload = async () => {
    if (!selectedFile) {
      setUploadMessage('请选择一个音频文件');
      return;
    }
    setUploading(true);
    setUploadMessage('');
    try {
      const uploadApi = process.env.REACT_APP_TTS_UPLOAD_API || 'http://192.168.168.77:6008/api/uploadSpeaker';
      const formData = new FormData();
      formData.append('file', selectedFile);

      const resp = await fetch(uploadApi, {
        method: 'POST',
        body: formData,
      });

      if (!resp.ok) {
        throw new Error(`上传失败，状态码: ${resp.status}`);
      }

      const data = await resp.json();
      setUploadMessage(data.message || '上传成功，服务器已接收音频');
      // 清空文件选择
      setSelectedFile(null);
      if (fileInputRef.current) fileInputRef.current.value = null;
    } catch (e) {
      setUploadMessage(`上传失败: ${e.message}`);
    } finally {
      setUploading(false);
    }
  };

  // 发送聊天文本到后端合成接口
  const handleSendChat = async () => {
    if (!chatText.trim()) return;
    setChatLoading(true);
    setChatResponse('');
    try {
      const chatApi = process.env.REACT_APP_TTS_CHAT_API || 'http://192.168.168.77:6010/say';
      const resp = await fetch(chatApi, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: chatText.trim() }),
      });
      if (!resp.ok) {
        throw new Error(`请求失败，状态码: ${resp.status}`);
      }
      const data = await resp.json();
      setChatResponse(data.message || '已发送到合成服务');
      setChatText('');
    } catch (e) {
      setChatResponse(`发送失败: ${e.message}`);
    } finally {
      setChatLoading(false);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%', fontSize: 14, color: '#ddd' }}>
      <section style={{ marginBottom: 20 }}>
        <h3>上传克隆参考音频</h3>

        {/* 选择文件按钮，隐藏原生input，使用label模拟按钮 */}
        <label
          htmlFor="file-upload"
          style={{
            ...glassButtonStyle,
            display: 'inline-block',
            marginBottom: 12,
            userSelect: 'none',
            width: 'fit-content',
            cursor: uploading ? 'not-allowed' : 'pointer',
            opacity: uploading ? 0.5 : 1,
          }}
        >
          选择上传文件
        </label>
        <input
          id="file-upload"
          type="file"
          accept="audio/*"
          onChange={handleFileChange}
          ref={fileInputRef}
          style={{ display: 'none' }}
          disabled={uploading}
        />

        <div>
          <button
            onClick={handleUpload}
            disabled={uploading || !selectedFile}
            style={uploading || !selectedFile ? glassButtonDisabledStyle : glassButtonStyle}
          >
            {uploading ? '上传中...' : '上传音频'}
          </button>
        </div>

        {uploadMessage && (
          <div
            style={{
              marginTop: 8,
              color: uploadMessage.startsWith('上传成功') ? '#80ff80' : '#ff8080',
              whiteSpace: 'pre-wrap',
              userSelect: 'text',
            }}
          >
            {uploadMessage}
          </div>
        )}
      </section>

      <section style={{ flexGrow: 1 }}>
        <h3>文本聊天合成</h3>
        <textarea
          value={chatText}
          onChange={(e) => setChatText(e.target.value)}
          placeholder="输入聊天文本，发送后将合成语音"
          rows={4}
          style={{
            width: '100%',
            resize: 'vertical',
            padding: 8,
            borderRadius: 8,
            border: '1px solid rgba(0,229,255,0.3)',
            backgroundColor: 'rgba(0,0,0,0.3)',
            color: '#eee',
            marginBottom: 8,
            fontSize: 14,
            fontFamily: 'inherit',
            outline: 'none',
            userSelect: 'text',
            boxShadow: '0 0 5px rgba(0,229,255,0.5)',
          }}
          disabled={chatLoading}
        />

        <button
          onClick={handleSendChat}
          disabled={chatLoading || !chatText.trim()}
          style={chatLoading || !chatText.trim() ? glassButtonDisabledStyle : glassButtonStyle}
        >
          {chatLoading ? '发送中...' : '发送文本'}
        </button>

        {chatResponse && (
          <div
            style={{
              marginTop: 8,
              color: chatResponse.startsWith('发送失败') ? '#ff8080' : '#80ff80',
              whiteSpace: 'pre-wrap',
              userSelect: 'text',
            }}
          >
            {chatResponse}
          </div>
        )}
      </section>
    </div>
  );
}
