import React, { useState, useRef, useEffect } from 'react';

// 示例管家数据，全部是视频，id唯一
const butlers = [
  {
    id: 'butler1',
    name: '管家禅师',
    type: 'video',
    src: '/video/豆包禅师.mp4', // 请确保视频文件放在 public/video/目录内
  },
  {
    id: 'butler2',
    name: '管家小粒',
    type: 'video',
    src: '/video/小粒.mp4',
  },
  {
    id: 'butler3',
    name: '管家小贾',
    type: 'video',
    src: '/video/小贾.mp4',
  },
  {
    id: 'butler4',
    name: '管家星期天',
    type: 'video',
    src: '/video/星期天.mp4',
  },
];

export default function ButlerShowcase() {
  // 当前激活管家ID
  const [activeButlerId, setActiveButlerId] = useState(butlers[0].id);
  // 鼠标悬停管家ID，用于动画和提示（非播放控制）
  const [hoveredId, setHoveredId] = useState(null);
  // video 元素 refs
  const videoRefs = useRef({});

  // 鼠标移入管家项，设置hoveredId（不启动播放）
  function handleMouseEnter(id) {
    setHoveredId(id);
  }

  // 鼠标离开管家项，清空hoveredId
  function handleMouseLeave() {
    setHoveredId(null);
  }

  // 双击切换激活管家
  function handleDoubleClick(id) {
    if (id !== activeButlerId) {
      setActiveButlerId(id);
    }
  }

  // 控制视频播放和暂停，及灰白滤镜
  useEffect(() => {
    Object.entries(videoRefs.current).forEach(([id, video]) => {
      if (!video) return;
      if (id === activeButlerId) {
        // 激活的管家播放
        if (video.paused) {
          video.play().catch(() => {
            // 播放失败静默处理（浏览器策略限制）
          });
        }
        video.style.filter = 'none';
      } else {
        // 非激活管家暂停并加灰白滤镜
        if (!video.paused) {
          video.pause();
          video.currentTime = 0;
        }
        video.style.filter = 'grayscale(100%) brightness(70%)';
      }
    });
  }, [activeButlerId]);

  return (
    <>
      <div
        className="steward-gallery"
        style={{
          fontFamily: 'Arial, sans-serif',
          backgroundColor: 'transparent',
          padding: 40,
          minHeight: '100vh',
          userSelect: 'none',
        }}
      >
        <div
          className="display-case"
          style={{
            position: 'relative',
            maxWidth: 1000,
            margin: '0 auto',
            borderRadius: 8,
            overflow: 'visible',
            backgroundColor: 'transparent',
            boxShadow: 'none',
          }}
        >
          <div
            className="case-top"
            style={{
              height: 20,
              backgroundColor: 'transparent',
              boxShadow: 'none',
            }}
          />
          <div
            className="case-bottom"
            style={{
              height: 20,
              backgroundColor: 'transparent',
              boxShadow: 'none',
            }}
          />

          <div
            className="stewards-container"
            style={{
              display: 'flex',
              justifyContent: 'center',
              backgroundColor: 'transparent',
              padding: '30px 0',
              gap: 0,
            }}
          >
            {butlers.map(({ id, name, type, src }, index) => {
              const isActive = id === activeButlerId;
              const isHovered = id === hoveredId;

              let themeBg = '#333';
              if (id === 'butler1') themeBg = '#333';
              else if (id === 'butler2' || id === 'butler3' || id === 'butler4')
                themeBg = '#d32f2f';

              return (
                <div
                  key={id}
                  className={`steward ${isActive ? 'active' : ''} ${
                    isHovered ? 'hovered' : ''
                  }`}
                  onMouseEnter={() => handleMouseEnter(id)}
                  onMouseLeave={handleMouseLeave}
                  onDoubleClick={() => handleDoubleClick(id)}
                  style={{
                    position: 'relative',
                    width: 240,
                    height: 480,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease',
                    transform: isHovered ? 'translateY(-10px)' : 'translateY(0)',
                    userSelect: 'none',
                    marginRight: index !== butlers.length - 1 ? 0 : 0,
                    zIndex: 1,
                  }}
                  title={
                    isActive
                      ? `当前激活管家：${name}`
                      : `双击切换到管家 ${name}`
                  }
                >
                  <div
                    style={{
                      width: 160,
                      height: 320,
                      backgroundColor: themeBg,
                      borderRadius: 12,
                      marginBottom: 20,
                      filter: 'drop-shadow(0 0 12px rgba(0,255,255,0.7))',
                      position: 'relative',
                      overflow: 'hidden',
                      zIndex: 1,
                    }}
                  >
                    {type === 'image' ? (
                      <img
                        src={src}
                        alt={name}
                        draggable={false}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                        }}
                      />
                    ) : (
                      <video
                        ref={(el) => (videoRefs.current[id] = el)}
                        src={src}
                        autoPlay={false} // 全部由 useEffect 控制
                        loop
                        muted
                        playsInline
                        preload="auto"
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                          pointerEvents: 'none',
                          filter: isActive
                            ? 'none'
                            : 'grayscale(100%) brightness(70%)',
                        }}
                      />
                    )}
                  </div>

                  {/* 底部横杠，默认灰色，激活或悬停时亮灯 */}
                  <div
                    style={{
                      width: 120,
                      height: 6,
                      borderRadius: 3,
                      backgroundColor:
                        isActive || isHovered ? '#00e5ff' : '#666',
                      boxShadow:
                        isActive || isHovered
                          ? '0 0 12px #00e5ff'
                          : 'none',
                      marginBottom: 6,
                      transition: 'background-color 0.3s, box-shadow 0.3s',
                      zIndex: 1,
                    }}
                  />

                  {/* 底座圆点，亮灯逻辑和横杠一致 */}
                  <div
                    className="indicator-light"
                    style={{
                      width: 20,
                      height: 20,
                      borderRadius: '50%',
                      backgroundColor:
                        isActive || isHovered ? '#00e5ff' : '#555',
                      boxShadow:
                        isActive || isHovered
                          ? '0 0 20px #00e5ff'
                          : 'none',
                      marginTop: 6,
                      transition: 'background-color 0.3s, box-shadow 0.3s',
                      zIndex: 1,
                    }}
                  />

                  {/* 名字 */}
                  <div
                    style={{
                      color: isActive ? '#0ff' : '#ccc',
                      fontWeight: 'bold',
                      textAlign: 'center',
                      userSelect: 'none',
                      textShadow: isActive ? '0 0 8px #0ff' : 'none',
                      whiteSpace: 'nowrap',
                      fontSize: 18,
                      marginTop: 12,
                      zIndex: 1,
                    }}
                  >
                    {name}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* 动画样式 */}
      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateX(-50%) translateY(10px);}
          to { opacity: 1; transform: translateX(-50%) translateY(0);}
        }
      `}</style>
    </>
  );
}
