import React, { useState, useRef, useEffect } from 'react';
import { useRecorderContext } from '../contexts/RecorderProvider'; // 注意路径根据实际调整

export default function AudioTranscriber() {
  // 从全局录音上下文中取状态和操作
  const {
    isRecording,
    transcription,
    lang,
    speakerVerification,
    wakeWordEnabled,
    wakeWords,
    setLang,
    setSpeakerVerification,
    setWakeWordEnabled,
    setWakeWords,
    startRecording,
    stopRecording,
    setTranscription,
  } = useRecorderContext();

  // 控制面板展开/折叠状态，初始为折叠（只显示麦克风按钮）
  const [expanded, setExpanded] = useState(false);

  // 拖拽相关引用，仅用于麦克风按钮（折叠态）
  const micButtonRef = useRef(null);
  const wrapperRef = useRef(null);
  const isDraggingRef = useRef(false);
  const offsetXRef = useRef(0);
  const offsetYRef = useRef(0);

  // 面板拖拽相关引用（展开态）
  const panelRef = useRef(null);
  const panelDragState = useRef({
    isDragging: false,
    offsetX: 0,
    offsetY: 0,
  });

  // localStorage 保存位置的 key
  const MIC_STORAGE_KEY = 'audioTranscriberMicButtonPosition';
  const PANEL_STORAGE_KEY = 'audioTranscriberPanelPosition';

  // 新增声纹识别录音状态和上传状态（保持本组件私有状态）
  const [isSpeakerRecording, setIsSpeakerRecording] = useState(false);
  const [speakerUploadStatus, setSpeakerUploadStatus] = useState('');
  const speakerRecorderRef = useRef(null);
  const speakerAudioChunksRef = useRef([]);

  // 面板位置和大小（展开时）
  const [panelPosition, setPanelPosition] = useState(() => {
    try {
      const saved = localStorage.getItem(PANEL_STORAGE_KEY);
      if (saved) return JSON.parse(saved);
    } catch {}
    return { left: 100, top: 100, width: 360, height: 280 };
  });

  // 麦克风按钮位置（折叠时）
  const [micPosition, setMicPosition] = useState(() => {
    try {
      const saved = localStorage.getItem(MIC_STORAGE_KEY);
      if (saved) return JSON.parse(saved);
    } catch {}
    return { left: 26, top: 26 };
  });

  // 发送文本消息到 LLM 服务（保持不变）
  const sendTextMessage = async (text) => {
    if (!text.trim()) return;
    try {
      const response = await fetch('https://192.168.1.70:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text }),
      });
      if (!response.ok) {
        throw new Error('网络响应失败，状态码: ' + response.status);
      }
      const data = await response.json();
      console.log('文本发送成功，服务器返回：', data);
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  // 组件卸载清理
  useEffect(() => {
    return () => {
      stopRecording();
      stopSpeakerRecording();
    };
  }, []);

  // 监听语言和声纹识别开关变化存localStorage（保持不变）
  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberLang', lang);
    } catch {}
  }, [lang]);

  useEffect(() => {
    try {
      localStorage.setItem('audioTranscriberSpeakerVerification', speakerVerification.toString());
    } catch {}
  }, [speakerVerification]);

  useEffect(() => {
    try {
      localStorage.setItem('wakeWordEnabled', wakeWordEnabled.toString());
    } catch {}
  }, [wakeWordEnabled]);

  useEffect(() => {
    try {
      localStorage.setItem('wakeWords', wakeWords);
    } catch {}
  }, [wakeWords]);

  // 录制声纹相关函数保持不变
  function startSpeakerRecording() {
    if (isSpeakerRecording) return;
    setSpeakerUploadStatus('请求麦克风权限...');
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        setSpeakerUploadStatus('开始录音2秒...');
        speakerAudioChunksRef.current = [];
        let mediaRecorder;
        try {
          mediaRecorder = new MediaRecorder(stream, { mimeType: 'audio/wav' });
        } catch (e) {
          mediaRecorder = new MediaRecorder(stream);
        }
        speakerRecorderRef.current = mediaRecorder;
        mediaRecorder.ondataavailable = (event) => {
          if (event.data && event.data.size > 0) {
            speakerAudioChunksRef.current.push(event.data);
          }
        };
        mediaRecorder.onstop = async () => {
          setSpeakerUploadStatus('录音结束，准备上传...');
          const blob = new Blob(speakerAudioChunksRef.current, { type: 'audio/wav' });
          try {
            const formData = new FormData();
            formData.append('file', blob, 'speaker.wav');
            const uploadUrl = 'http://192.168.168.77:6007/api/uploadSpeaker';
            const res = await fetch(uploadUrl, {
              method: 'POST',
              body: formData,
            });
            if (!res.ok) throw new Error(`上传失败，状态码: ${res.status}`);
            const result = await res.json();
            setSpeakerUploadStatus('上传成功');
            console.log('上传成功，服务器返回：', result);
          } catch (err) {
            setSpeakerUploadStatus('上传失败: ' + err.message);
            console.error('上传失败', err);
          }
          stream.getTracks().forEach(track => track.stop());
          setTimeout(() => setSpeakerUploadStatus(''), 3000);
          setIsSpeakerRecording(false);
        };
        mediaRecorder.start();
        setIsSpeakerRecording(true);
        setTimeout(() => {
          if (speakerRecorderRef.current && speakerRecorderRef.current.state !== 'inactive') {
            speakerRecorderRef.current.stop();
          }
        }, 2000);
      })
      .catch((err) => {
        setSpeakerUploadStatus('获取麦克风权限失败: ' + err.message);
        console.error('获取麦克风权限失败', err);
      });
  }
  function stopSpeakerRecording() {
    if (speakerRecorderRef.current && speakerRecorderRef.current.state !== 'inactive') {
      speakerRecorderRef.current.stop();
      setIsSpeakerRecording(false);
    }
  }

  // 保存位置到 localStorage
  const saveMicPosition = (pos) => {
    setMicPosition(pos);
    try {
      localStorage.setItem(MIC_STORAGE_KEY, JSON.stringify(pos));
    } catch {}
  };
  const savePanelPosition = (pos) => {
    setPanelPosition(pos);
    try {
      localStorage.setItem(PANEL_STORAGE_KEY, JSON.stringify(pos));
    } catch {}
  };

  // 拖拽逻辑 - 麦克风按钮（折叠态）
  useEffect(() => {
    if (!micButtonRef.current || !wrapperRef.current) return;
    const button = micButtonRef.current;
    const wrapper = wrapperRef.current;

    // 初始化按钮位置
    let left = micPosition.left;
    let top = micPosition.top;

    button.style.position = 'absolute';
    button.style.left = `${left}px`;
    button.style.top = `${top}px`;
    button.style.zIndex = '10000';
    button.style.cursor = 'grab';

    isDraggingRef.current = false;
    offsetXRef.current = 0;
    offsetYRef.current = 0;

    // 鼠标事件
    const onMouseDown = (e) => {
      if (e.button !== 0) return;
      isDraggingRef.current = true;
      const wrapperRect = wrapper.getBoundingClientRect();
      offsetXRef.current = e.clientX - wrapperRect.left - button.offsetLeft;
      offsetYRef.current = e.clientY - wrapperRect.top - button.offsetTop;
      button.style.cursor = 'grabbing';
      e.preventDefault();
    };
    const onMouseMove = (e) => {
      if (!isDraggingRef.current) return;
      const wrapperRect = wrapper.getBoundingClientRect();
      let newLeft = e.clientX - wrapperRect.left - offsetXRef.current;
      let newTop = e.clientY - wrapperRect.top - offsetYRef.current;

      // 限制范围在面板内
      newLeft = Math.min(Math.max(0, newLeft), wrapper.clientWidth - button.offsetWidth);
      newTop = Math.min(Math.max(0, newTop), wrapper.clientHeight - button.offsetHeight);

      button.style.left = `${newLeft}px`;
      button.style.top = `${newTop}px`;
    };
    const onMouseUp = () => {
      if (!isDraggingRef.current) return;
      isDraggingRef.current = false;
      button.style.cursor = 'grab';

      localStorage.setItem(
        MIC_STORAGE_KEY,
        JSON.stringify({ left: button.offsetLeft, top: button.offsetTop })
      );
      setMicPosition({ left: button.offsetLeft, top: button.offsetTop });
    };

    // 触摸事件
    const onTouchStart = (e) => {
      if (e.touches.length === 0) return;
      isDraggingRef.current = true;
      const wrapperRect = wrapper.getBoundingClientRect();
      const touch = e.touches[0];
      offsetXRef.current = touch.clientX - wrapperRect.left - button.offsetLeft;
      offsetYRef.current = touch.clientY - wrapperRect.top - button.offsetTop;
      button.style.cursor = 'grabbing';
    };
    const onTouchMove = (e) => {
      if (!isDraggingRef.current || e.touches.length === 0) return;
      const wrapperRect = wrapper.getBoundingClientRect();
      const touch = e.touches[0];
      let newLeft = touch.clientX - wrapperRect.left - offsetXRef.current;
      let newTop = touch.clientY - wrapperRect.top - offsetYRef.current;

      newLeft = Math.min(Math.max(0, newLeft), wrapper.clientWidth - button.offsetWidth);
      newTop = Math.min(Math.max(0, newTop), wrapper.clientHeight - button.offsetHeight);

      button.style.left = `${newLeft}px`;
      button.style.top = `${newTop}px`;

      e.preventDefault();
    };
    const onTouchEnd = () => {
      if (!isDraggingRef.current) return;
      isDraggingRef.current = false;
      button.style.cursor = 'grab';

      localStorage.setItem(
        MIC_STORAGE_KEY,
        JSON.stringify({ left: button.offsetLeft, top: button.offsetTop })
      );
      setMicPosition({ left: button.offsetLeft, top: button.offsetTop });
    };

    button.addEventListener('mousedown', onMouseDown);
    document.addEventListener('mousemove', onMouseMove);
    document.addEventListener('mouseup', onMouseUp);

    button.addEventListener('touchstart', onTouchStart, { passive: false });
    document.addEventListener('touchmove', onTouchMove, { passive: false });
    document.addEventListener('touchend', onTouchEnd);

    // 清理事件监听
    return () => {
      button.removeEventListener('mousedown', onMouseDown);
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);

      button.removeEventListener('touchstart', onTouchStart);
      document.removeEventListener('touchmove', onTouchMove);
      document.removeEventListener('touchend', onTouchEnd);
    };
  }, [micPosition]);

  // 拖拽逻辑 - 面板（展开态）
  useEffect(() => {
    if (!expanded || !panelRef.current) return;
    const panel = panelRef.current;

    panel.style.position = 'fixed';
    panel.style.left = `${panelPosition.left}px`;
    panel.style.top = `${panelPosition.top}px`;
    panel.style.width = `${panelPosition.width}px`;
    panel.style.height = `${panelPosition.height}px`;
    panel.style.zIndex = 9999;
    panel.style.cursor = 'grab';

    const onMouseDown = (e) => {
      if (e.button !== 0) return;
      if (!e.target.closest('.panel-header')) return;

      panelDragState.current.isDragging = true;
      panelDragState.current.offsetX = e.clientX - panel.offsetLeft;
      panelDragState.current.offsetY = e.clientY - panel.offsetTop;
      panel.style.cursor = 'grabbing';
      e.preventDefault();
    };
    const onMouseMove = (e) => {
      if (!panelDragState.current.isDragging) return;
      e.preventDefault();
      let newLeft = e.clientX - panelDragState.current.offsetX;
      let newTop = e.clientY - panelDragState.current.offsetY;

      const maxLeft = window.innerWidth - panel.offsetWidth;
      const maxTop = window.innerHeight - panel.offsetHeight;
      newLeft = Math.min(Math.max(0, newLeft), maxLeft);
      newTop = Math.min(Math.max(0, newTop), maxTop);

      panel.style.left = `${newLeft}px`;
      panel.style.top = `${newTop}px`;
    };
    const onMouseUp = () => {
      if (!panelDragState.current.isDragging) return;
      panelDragState.current.isDragging = false;
      panel.style.cursor = 'grab';

      savePanelPosition({
        left: panel.offsetLeft,
        top: panel.offsetTop,
        width: panel.offsetWidth,
        height: panel.offsetHeight,
      });
    };

    // 触摸事件
    const onTouchStart = (e) => {
      if (e.touches.length === 0) return;
      if (!e.target.closest('.panel-header')) return;
      panelDragState.current.isDragging = true;
      const touch = e.touches[0];
      panelDragState.current.offsetX = touch.clientX - panel.offsetLeft;
      panelDragState.current.offsetY = touch.clientY - panel.offsetTop;
      panel.style.cursor = 'grabbing';
      e.preventDefault();
    };
    const onTouchMove = (e) => {
      if (!panelDragState.current.isDragging) return;
      const touch = e.touches[0];
      let newLeft = touch.clientX - panelDragState.current.offsetX;
      let newTop = touch.clientY - panelDragState.current.offsetY;

      const maxLeft = window.innerWidth - panel.offsetWidth;
      const maxTop = window.innerHeight - panel.offsetHeight;
      newLeft = Math.min(Math.max(0, newLeft), maxLeft);
      newTop = Math.min(Math.max(0, newTop), maxTop);

      panel.style.left = `${newLeft}px`;
      panel.style.top = `${newTop}px`;
      e.preventDefault();
    };
    const onTouchEnd = () => {
      if (!panelDragState.current.isDragging) return;
      panelDragState.current.isDragging = false;
      panel.style.cursor = 'grab';

      savePanelPosition({
        left: panel.offsetLeft,
        top: panel.offsetTop,
        width: panel.offsetWidth,
        height: panel.offsetHeight,
      });
    };

    panel.addEventListener('mousedown', onMouseDown);
    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);

    panel.addEventListener('touchstart', onTouchStart, { passive: false });
    window.addEventListener('touchmove', onTouchMove, { passive: false });
    window.addEventListener('touchend', onTouchEnd);

    return () => {
      panel.removeEventListener('mousedown', onMouseDown);
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);

      panel.removeEventListener('touchstart', onTouchStart);
      window.removeEventListener('touchmove', onTouchMove);
      window.removeEventListener('touchend', onTouchEnd);
    };
  }, [expanded, panelPosition]);

  // 双击节流，单击/双击事件合并处理
  const lastClickTimeRef = useRef(0);
  const DOUBLE_CLICK_DELAY = 400;

  const handleMicClick = () => {
    const now = Date.now();
    if (now - lastClickTimeRef.current < DOUBLE_CLICK_DELAY) {
      // 双击切换展开/折叠
      setExpanded((prev) => !prev);
    } else {
      // 单击控制录音开关
      if (isRecording) stopRecording();
      else startRecording();
    }
    lastClickTimeRef.current = now;
  };

  // 省略声纹录制波纹效果部分，保持不变即可...
  const [ripples, setRipples] = useState([]);
  const rippleCountRef = useRef(0);
  const speakerButtonRef = useRef(null);

  const handleSpeakerRecordClick = (event) => {
    if (isRecording || isSpeakerRecording) return;
    createRipple(event);
    startSpeakerRecording();
  };

  const createRipple = (event) => {
    if (!speakerButtonRef.current) return;
    const rect = speakerButtonRef.current.getBoundingClientRect();
    const size = Math.max(rect.width, rect.height);
    const x = event.clientX - rect.left - size / 2;
    const y = event.clientY - rect.top - size / 2;

    const newRipple = {
      key: rippleCountRef.current,
      size,
      x,
      y,
    };
    rippleCountRef.current += 1;

    setRipples((oldRipples) => [...oldRipples, newRipple]);

    setTimeout(() => {
      setRipples((oldRipples) => oldRipples.filter(r => r.key !== newRipple.key));
    }, 1000);
  };

  return (
    <>
      <style>{`
        /* 麦克风按钮及容器样式 */
        #mic-button-wrapper {
          position: relative;
          width: 100%;
          height: 100%;
          z-index: 10000;
          user-select: none;
          pointer-events: auto;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          gap: 4px;
          overflow: visible;
        }

        #listening-text {
          font-size: 12px;
          color: rgba(128, 128, 128, 0.7);
          user-select: none;
          pointer-events: none;
          font-weight: 500;
          opacity: 0;
          transition: opacity 0.3s ease;
          height: 16px;
          line-height: 16px;
        }
        #listening-text.visible {
          opacity: 1;
        }

        #mic-button {
          background: none;
          border: none;
          padding: 0;
          outline: none;
          display: flex;
          align-items: center;
          justify-content: center;
          filter: none;
          transition: filter 0.3s ease;

          width: 68px;
          height: 68px;
          cursor: grab;
          position: absolute; /* 绝对定位 */
        }
        #mic-button.recording {
          filter: drop-shadow(0 0 5px red);
        }
        #mic-button img {
          width: 48px;
          height: 48px;
          user-select: none;
          pointer-events: none;
          -webkit-user-drag: none;
        }

        /* 语言和声纹识别区域样式 */
        #controls-container {
          max-width: 700px;
          margin: 20px auto 12px auto;
          font-family: Arial, sans-serif;
          color: white;
          display: flex;
          align-items: center;
          gap: 20px;
          user-select: none;
          flex-wrap: wrap;
        }
        #controls-container label {
          font-size: 14px;
          user-select: none;
          display: flex;
          align-items: center;
          gap: 6px;
        }
        #langInput, #wakeWordsInput {
          padding: 6px;
          font-size: 12px;
          width: 140px;
          background-color: #333;
          border: 1px solid #666;
          border-radius: 3px;
          color: white;
          user-select: text;
        }
        #speakerVerificationLabel {
          font-size: 14px;
        }
        #wakeWordToggleLabel {
          font-size: 14px;
          display: flex;
          align-items: center;
          gap: 6px;
        }

        /* 纯 CSS 玻璃态录制声纹按钮 */
        #speaker-record-button {
          position: relative;
          overflow: hidden;
          background: rgba(255, 255, 255, 0.1);
          border-radius: 16px;
          border: 1px solid rgba(255, 255, 255, 0.3);
          color: white;
          padding: 3px 10px;
          font-size: 13px;
          font-weight: 600;
          cursor: pointer;
          user-select: none;
          box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
          backdrop-filter: blur(10px);
          -webkit-backdrop-filter: blur(12px);
          WebkitBackdropFilter: 'blur(10px)',
          transition: all 0.3s ease;
          display: inline-flex;
          align-items: center;
          justify-content: center;
          outline: none;
          border-image: none;
        }
        #speaker-record-button:hover:not(:disabled) {
          background: rgba(255, 255, 255, 0.2);
          box-shadow: 0 8px 40px rgba(255, 255, 255, 0.3);
          transform: scale(1.05);
          filter: brightness(1.1);
        }
        #speaker-record-button:disabled {
          opacity: 0.4;
          cursor: not-allowed;
          box-shadow: none;
          background: rgba(255, 255, 255, 0.05);
          filter: none;
          transform: none;
        }

        /* 额外光泽层 */
        #speaker-record-button::before {
          content: '';
          position: absolute;
          top: -100%;
          left: -100%;
          width: 100%;
          height: 100%;
          background: radial-gradient(circle at 10% 10%, rgba(255, 255, 255, 0.3), transparent 10%);
          pointer-events: none;
          filter: blur(10px);
          opacity: 0.6;
          transition: opacity 0.3s ease;
          border-radius: inherit;
          z-index: 0;
        }

        /* 保留波纹效果的样式 */
        .ripple {
          position: absolute;
          border-radius: 150%;
          background: rgba(91, 155, 255, 0.7);
          animation: rippleEffect 1s ease-out;
          pointer-events: none;
          transform: scale(0);
          opacity: 0.75;
          z-index: 1;
        }

        @keyframes rippleEffect {
          to {
            transform: scale(4);
            opacity: 0;
          }
        }

        /* 面板展开状态样式 */
        #audio-transcriber-panel {
          position: fixed;
          z-index: 10000;
          background-color: rgba(0, 0, 0, 0.85);
          border-radius: 8px;
          box-shadow: 0 0 12px rgba(0, 229, 255, 0.8);
          color: white;
          display: flex;
          flex-direction: column;
          overflow: hidden;
          user-select: none;
        }

        #audio-transcriber-panel .panel-header {
          height: 40px;
          background-color: rgba(0, 229, 255, 0.2);
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 0 12px;
          cursor: grab;
          user-select: none;
        }

        #audio-transcriber-panel .panel-header .mic-button {
          width: 36px;
          height: 36px;
          border-radius: 50%;
          background-color: rgba(0,229,255,0.8);
          border: none;
          display: flex;
          align-items: center;
          justify-content: center;
          outline: none;
          box-shadow: 0 0 10px rgba(0,229,255,0.7);
          cursor: pointer;
          user-select: none;
        }

        #audio-transcriber-panel .panel-header .mic-button.recording {
          background-color: #ff4d4d;
          box-shadow: 0 0 10px red;
        }

        #audio-transcriber-panel .panel-header .mic-button img {
          width: 20px;
          height: 20px;
          user-select: none;
          pointer-events: none;
          -webkit-user-drag: none;
        }
      `}</style>

      {/* 折叠态显示单独麦克风按钮 */}
      {!expanded && (
        <div id="mic-button-wrapper" ref={wrapperRef} style={{ position: 'fixed', left: micPosition.left, top: micPosition.top, zIndex: 10000 }}>
          <div
            id="listening-text"
            className={isRecording ? 'visible' : ''}
            aria-live="polite"
            role="status"
            style={{ marginBottom: 4 }}
          >
            聆听中...
          </div>

          <button
            id="mic-button"
            ref={micButtonRef}
            className={isRecording ? 'recording' : ''}
            onClick={handleMicClick}
            onDoubleClick={(e) => { e.preventDefault(); setExpanded(true); }}
            aria-label={isRecording ? '停止录音，双击展开面板' : '开始录音，双击展开面板'}
            title={isRecording ? '点击停止录音，双击展开面板' : '点击开始录音，双击展开面板'}
            type="button"
            disabled={isSpeakerRecording}
            style={{
              width: 68,
              height: 68,
              cursor: 'grab',
              userSelect: 'none',
              position: 'absolute',
              left: 0,
              top: 0,
              background: 'none',
              border: 'none',
              padding: 0,
              outline: 'none',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              filter: isRecording ? 'drop-shadow(0 0 5px red)' : 'none',
              transition: 'filter 0.3s ease',
            }}
          >
            <img
              src="/core/client/ai-butler/image/mic-icon.png"
              alt="麦克风"
              draggable={false}
              style={{ width: 48, height: 48 }}
            />
          </button>
        </div>
      )}

      {/* 展开态显示完整面板 */}
      {expanded && (
        <div
          id="audio-transcriber-panel"
          ref={panelRef}
          role="region"
          aria-label="语音识别面板"
          style={{
            left: panelPosition.left,
            top: panelPosition.top,
            width: panelPosition.width,
            height: panelPosition.height,
          }}
        >
          <div className="panel-header" title="拖动此区域移动面板，双击麦克风按钮折叠面板">
            <button
              className={`mic-button${isRecording ? ' recording' : ''}`}
              onClick={handleMicClick}
              onDoubleClick={(e) => { e.preventDefault(); setExpanded(false); }}
              aria-label={isRecording ? '停止录音，双击折叠面板' : '开始录音，双击折叠面板'}
              title={isRecording ? '点击停止录音，双击折叠面板' : '点击开始录音，双击折叠面板'}
              type="button"
              disabled={isSpeakerRecording}
            >
              <img
                src="/core/client/ai-butler/image/mic-icon.png"
                alt="麦克风"
                draggable={false}
              />
            </button>
            <div style={{ fontWeight: 'bold', fontSize: 16, userSelect: 'none' }}>
              语音识别面板
            </div>
            <div style={{ fontSize: 12, opacity: 0.7, userSelect: 'none' }}>
              双击麦克风按钮折叠
            </div>
          </div>

          <div
            style={{
              flex: 1,
              overflowY: 'auto',
              padding: 12,
              fontFamily: 'Arial, sans-serif',
              fontSize: 14,
              userSelect: 'text',
            }}
          >
            {/* 识别结果展示 */}
            <div
              style={{
                backgroundColor: 'rgba(255,255,255,0.05)',
                borderRadius: 4,
                minHeight: 120,
                whiteSpace: 'pre-wrap',
                padding: 8,
                marginBottom: 12,
                color: transcription ? 'white' : 'rgba(255,255,255,0.5)',
              }}
              aria-live="polite"
              aria-atomic="true"
              role="region"
              tabIndex={0}
            >
              {transcription || '识别文字展示区...'}
            </div>

            {/* 语言选择 */}
            <label style={{ display: 'block', marginBottom: 6 }}>
              语言：
              <input
                id="langInput"
                type="text"
                value={lang}
                onChange={(e) => setLang(e.target.value)}
                disabled={isRecording || isSpeakerRecording}
                placeholder="auto"
                autoComplete="off"
                style={{
                  marginLeft: 8,
                  padding: 4,
                  borderRadius: 4,
                  border: '1px solid #555',
                  backgroundColor: 'rgba(0,0,0,0.3)',
                  color: 'white',
                  width: 100,
                }}
              />
            </label>

            {/* 声纹识别 */}
            <label style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 6 }}>
              <input
                id="speakerVerificationCheckbox"
                type="checkbox"
                checked={speakerVerification}
                disabled={isRecording || isSpeakerRecording}
                onChange={(e) => setSpeakerVerification(e.target.checked)}
              />
              声纹识别
            </label>

            {/* 语音唤醒 */}
            <label style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 6 }}>
              <input
                id="wakeWordToggleCheckbox"
                type="checkbox"
                checked={wakeWordEnabled}
                disabled={isRecording || isSpeakerRecording}
                onChange={(e) => setWakeWordEnabled(e.target.checked)}
              />
              语音唤醒
            </label>

            {/* 唤醒词输入 */}
            <label style={{ display: 'block', marginBottom: 6 }}>
              唤醒词（拼音，多个用逗号分隔）：
              <input
                id="wakeWordsInput"
                type="text"
                value={wakeWords}
                onChange={(e) => setWakeWords(e.target.value)}
                disabled={isRecording || isSpeakerRecording || !wakeWordEnabled}
                placeholder="如：zhan qi lai, ni hao xiao qian"
                autoComplete="off"
                style={{
                  marginLeft: 8,
                  padding: 4,
                  borderRadius: 4,
                  border: '1px solid #555',
                  backgroundColor: 'rgba(0,0,0,0.3)',
                  color: 'white',
                  width: '100%',
                  boxSizing: 'border-box',
                }}
              />
            </label>

            <button
              id="speaker-record-button"
              ref={speakerButtonRef}
              onClick={handleSpeakerRecordClick}
              disabled={isRecording || isSpeakerRecording}
              type="button"
              aria-live="polite"
              aria-label="录制声纹"
              title="录制约2秒声音用于声纹识别，录制完成自动上传"
              style={{
                position: 'relative',
                overflow: 'hidden',
                background: 'rgba(255, 255, 255, 0.1)',
                borderRadius: 16,
                border: '1px solid rgba(255, 255, 255, 0.3)',
                color: 'white',
                padding: '3px 10px',
                fontSize: 13,
                fontWeight: 600,
                cursor: 'pointer',
                userSelect: 'none',
                boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
                backdropFilter: 'blur(10px)',
                WebkitBackdropFilter: 'blur(12px)',
                transition: 'all 0.3s ease',
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                outline: 'none',
                borderImage: 'none',
              }}
            >
              录制声纹 (2秒)
              {ripples.map(({ key, size, x, y }) => (
                <span
                  key={key}
                  className="ripple"
                  style={{
                    width: size,
                    height: size,
                    top: y,
                    left: x,
                  }}
                />
              ))}
            </button>

            {speakerUploadStatus && (
              <div
                id="speaker-record-status"
                role="alert"
                aria-live="polite"
                aria-atomic="true"
                style={{ marginTop: 8 }}
              >
                {speakerUploadStatus}
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
}
