import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css'; // 记得引入样式

import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl';
import WebRTCPlayer from './components/OldWebRTC';

import FloatingContainer from './components/FloatingContainer';
import SpeechRecognizer from './components/SpeechRecognizer';
import SseListener from './components/SseListener';

const ItemTypes = {
  PANEL: 'panel',
};

function DraggableResizablePanel({
  id,
  left,
  top,
  width = 320,
  height = 200,
  zIndex,
  movePanel,
  bringToFront,
  resizePanel,
  children,
}) {
  const ref = React.useRef(null);

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  // 拖拽时自动置顶
  useEffect(() => {
    if (isDragging) {
      bringToFront(id);
    }
  }, [isDragging, bringToFront, id]);

  const [, drop] = useDrop({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!ref.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        movePanel(item.id, newLeft, newTop);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  });

  drag(drop(ref));

  // 拖拉调整大小事件
  const onResize = (event, { size }) => {
    resizePanel(id, size.width, size.height);
  };

  return (
    <ResizableBox
      width={width}
      height={height}
      minConstraints={[150, 100]}
      maxConstraints={[800, 600]}
      onResize={onResize}
      handle={<span className="custom-handle custom-handle-se" />}
      resizeHandles={['se']}
      style={{
        position: 'absolute',
        left,
        top,
        zIndex,
        boxSizing: 'border-box',
        // 拖拽区域必须有ref，传给useDrag和useDrop
      }}
    >
      <div
        ref={ref}
        style={{
          width: '100%',
          height: '100%',
          padding: 12,
          backgroundColor: 'rgba(255, 255, 255, 0)', // 透明背景
          border: '1px solid #888',
          borderRadius: 4,
          cursor: isDragging ? 'grabbing' : 'grab',
          boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
          userSelect: 'none',
          overflow: 'auto',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {children}
      </div>
    </ResizableBox>
  );
}

function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      return JSON.parse(saved);
    }
  } catch (e) {
    console.warn('读取面板位置失败', e);
  }
  return [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'floating', left: 20, top: 580, width: 320, height: 200, zIndex: 1 },
    { id: 'sse', left: 20, top: 740, width: 320, height: 200, zIndex: 1 },
  ];
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);

  const [panels, setPanels] = useState(loadPanels);
  const maxZIndex = useRef(1);

  const [selectedMenu, setSelectedMenu] = useState('home');

  function handleSendText(text) {
    console.log('识别到的文本:', text);
  }

  function handleSseMessage(data) {
    console.log('收到 SSE 消息:', data);
    setSseMessages((prev) => [...prev, data]);
  }

  function movePanel(id, left, top) {
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, left, top } : p))
    );
  }

  function resizePanel(id, width, height) {
    setPanels((prev) =>
      prev.map((p) =>
        p.id === id
          ? {
              ...p,
              width,
              height,
            }
          : p
      )
    );
  }

  function bringToFront(id) {
    maxZIndex.current += 1;
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, zIndex: maxZIndex.current } : p))
    );
  }

  useEffect(() => {
    localStorage.setItem('panels', JSON.stringify(panels));
  }, [panels]);

  const handleMenuClick = (menuKey) => {
    setSelectedMenu(menuKey);
  };

  const renderContent = () => {
    switch (selectedMenu) {
      case 'home':
        return (
          <>
            <SseListener onMessage={handleSseMessage} />
            {panels.map(({ id, left, top, width, height, zIndex }) => {
              switch (id) {
                case 'audio':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <AudioTranscriber />
                    </DraggableResizablePanel>
                  );
                case 'speech':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <SpeechRecognizer onSendText={handleSendText} />
                    </DraggableResizablePanel>
                  );
                case 'camera':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <CameraControl />
                    </DraggableResizablePanel>
                  );
                case 'webrtc':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <WebRTCPlayer />
                    </DraggableResizablePanel>
                  );
                case 'floating':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <FloatingContainer>
                        <div>
                          <h4>Draggable Floating Box</h4>
                          <p>拖动我试试</p>
                        </div>
                      </FloatingContainer>
                    </DraggableResizablePanel>
                  );
                case 'sse':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <div
                        style={{
                          marginTop: 20,
                          padding: 10,
                          border: '1px solid #ccc',
                          borderRadius: 4,
                          maxHeight: '100%',
                          overflowY: 'auto',
                          backgroundColor: 'rgba(255,255,255,0)', // 透明
                          position: 'relative',
                          zIndex: 0,
                          height: '100%',
                        }}
                      >
                        <h4>SSE 消息</h4>
                        {sseMessages.length === 0 ? (
                          <p>等待接收消息...</p>
                        ) : (
                          <ul>
                            {sseMessages.map((msg, idx) => (
                              <li key={idx}>{msg}</li>
                            ))}
                          </ul>
                        )}
                      </div>
                    </DraggableResizablePanel>
                  );
                default:
                  return null;
              }
            })}
          </>
        );
      case 'butler':
        return (
          <div style={{ padding: 20 }}>
            <h2>管家页面</h2>
            <p>这里是管家页面的内容，请根据需求替换。</p>
          </div>
        );
      case 'ops':
        return (
          <div style={{ padding: 20 }}>
            <h2>运维页面</h2>
            <p>这里是运维页面的内容，请根据需求替换。</p>
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: 20 }}>
            <h2>设置页面</h2>
            <p>这里是设置页面的内容，请根据需求替换。</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
      <h1>React AI Assistant Demo</h1>

      <div
        style={{
          display: 'flex',
          gap: 20,
          marginTop: 20,
          marginBottom: 20,
        }}
        role="menu"
      >
        {[
          { key: 'home', label: '主页' },
          { key: 'butler', label: '管家' },
          { key: 'ops', label: '运维' },
          { key: 'settings', label: '设置' },
        ].map(({ key, label }) => (
          <button
            key={key}
            onClick={() => handleMenuClick(key)}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: '2px solid #007bff',
              backgroundColor: selectedMenu === key ? '#007bff' : '#e7f1ff',
              color: selectedMenu === key ? 'white' : '#007bff',
              fontSize: 16,
              fontWeight: 'bold',
              cursor: 'pointer',
              outline: 'none',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              transition: 'background-color 0.3s, color 0.3s',
            }}
            aria-label={label}
            aria-haspopup="true"
          >
            {label}
          </button>
        ))}
      </div>

      {renderContent()}
    </div>
  );
}
