// ./components/ServerUrlConfig.jsx
import React, { useState, useEffect } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  Paper,
  Divider,
  Snackbar,
  Alert,
  Grid,
  Link
} from '@mui/material';

const ServerUrlConfig = () => {
  // 定义所有需要配置的 URL
  const [urls, setUrls] = useState({
    homeAssistantUrl: 'http://192.168.168.10:8123', // Home Assistant 默认值
    uploadBgFileUrl: 'http://192.168.168.77:6010/api/upload_print_file', // 背景上传 URL 默认值
    // 你可以在这里添加更多需要配置的 URL，例如：
    // apiBaseUrl: '',
    // websocketUrl: '',
  });

  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('success');

  // 组件挂载时从 localStorage 加载配置
  useEffect(() => {
    const savedUrls = localStorage.getItem('serverUrls');
    if (savedUrls) {
      try {
        const parsedUrls = JSON.parse(savedUrls);
        // 合并默认值和已保存值，确保所有字段都有默认值
        setUrls(prevUrls => ({ ...prevUrls, ...parsedUrls }));
      } catch (e) {
        console.error("Failed to parse serverUrls from localStorage", e);
        showSnackbar('读取配置失败', 'error');
      }
    }
    // 如果 localStorage 中没有配置，则使用上方 useState 中定义的默认值
  }, []);

  // 保存配置到 localStorage
  const saveConfig = () => {
    try {
      localStorage.setItem('serverUrls', JSON.stringify(urls));
      showSnackbar('服务地址配置已保存', 'success');

      // 通知其他监听 'storage' 事件的组件配置已更新
      // App.jsx 中的 HomeAssistantPanel 和 Settings.jsx 中的 uploadBackgroundFile 已经实现了监听
      window.dispatchEvent(new StorageEvent('storage', {
        key: 'serverUrls',
        newValue: JSON.stringify(urls)
      }));

    } catch (e) {
      console.error("Failed to save serverUrls to localStorage", e);
      showSnackbar('保存配置失败: 存储错误', 'error');
    }
  };

  const showSnackbar = (message, severity) => {
    setSnackbarMessage(message);
    setSnackbarSeverity(severity);
    setOpenSnackbar(true);
  };

  const handleUrlChange = (key, value) => {
    setUrls(prev => ({
      ...prev,
      [key]: value
    }));
  };

  return (
    <Paper sx={{ p: 2, mb: 2, backgroundColor: 'rgba(255, 255, 255, 0.05)' }}>
      <Typography variant="h6" gutterBottom sx={{ color: 'white' }}>
        服务地址设置
      </Typography>
      <Divider sx={{ mb: 2, backgroundColor: 'rgba(255, 255, 255, 0.1)' }} />

      <Grid container spacing={2}>
        {/* Home Assistant URL 配置 */}
        <Grid item xs={12}>
          <TextField
            label="Home Assistant 地址"
            fullWidth
            variant="outlined"
            value={urls.homeAssistantUrl}
            onChange={(e) => handleUrlChange('homeAssistantUrl', e.target.value)}
            placeholder="http://192.168.168.10:8123"
            InputLabelProps={{ style: { color: 'white' } }}
            InputProps={{
              style: { color: 'white', backgroundColor: 'rgba(255, 255, 255, 0.08)' },
            }}
            helperText={
              <Link
                href="https://www.home-assistant.io/"
                target="_blank"
                rel="noopener"
                sx={{ color: '#64b5f6', fontSize: '0.75rem' }}
              >
                什么是 Home Assistant?
              </Link>
            }
            FormHelperTextProps={{ style: { marginLeft: 0, marginTop: 4 } }}
          />
        </Grid>

        {/* 背景文件上传 URL 配置 */}
        <Grid item xs={12}>
          <TextField
            label="背景文件上传地址"
            fullWidth
            variant="outlined"
            value={urls.uploadBgFileUrl}
            onChange={(e) => handleUrlChange('uploadBgFileUrl', e.target.value)}
            placeholder="http://192.168.168.77:6010/api/upload_print_file"
            InputLabelProps={{ style: { color: 'white' } }}
            InputProps={{
              style: { color: 'white', backgroundColor: 'rgba(255, 255, 255, 0.08)' },
            }}
            helperText="用于上传自定义壁纸的服务器端点"
            FormHelperTextProps={{ style: { color: 'rgba(255, 255, 255, 0.7)', marginLeft: 0, marginTop: 4 } }}
          />
        </Grid>

        {/* 可以在这里添加更多 TextField 来配置其他 URL */}

        {/* 保存按钮 */}
        <Grid item xs={12}>
          <Button
            variant="contained"
            onClick={saveConfig}
            sx={{
              backgroundColor: '#1976d2', // 和之前隐藏菜单按钮颜色一致
              color: 'white',
              '&:hover': {
                backgroundColor: '#1565c0',
              },
              mt: 1
            }}
          >
            保存配置
          </Button>
        </Grid>
      </Grid>

      {/* 保存结果提示 Snackbar */}
      <Snackbar
        open={openSnackbar}
        autoHideDuration={3000}
        onClose={() => setOpenSnackbar(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          onClose={() => setOpenSnackbar(false)}
          severity={snackbarSeverity}
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Paper>
  );
};

export default ServerUrlConfig;
