// ButlersContext.js
import React, { createContext, useState, useContext } from 'react';

// 初始管家数据
const initialButlers = [
  {
    id: 'cs',
    name: '管家禅师',
    type: 'video',
    src: '/core/client/ai-butler/video/豆包禅师.mp4',
  },
  {
    id: 'xl',
    name: '管家小粒',
    type: 'video',
    src: '/core/client/ai-butler/video/小粒.mp4',
  },
  {
    id: 'xj',
    name: '管家小贾',
    type: 'video',
    src: '/core/client/ai-butler/video/小贾.mp4',
  },
  {
    id: 'xqt',
    name: '管家星期天',
    type: 'video',
    src: '/core/client/ai-butler/video/星期天.mp4',
  },
];

// 创建Context
const ButlerContext = createContext({
  activeButlerId: initialButlers[0].id,
  setActiveButlerId: () => {},
  butlers: initialButlers,
  addButler: () => {}, // 空函数，防止未包裹Provider报错
});

// Provider组件
export function ButlerProvider({ children }) {
  // 用useState管理动态butlers
  const [butlers, setButlers] = useState(initialButlers);
  const [activeButlerId, setActiveButlerId] = useState(initialButlers[0].id);

  // 动态添加管家方法
  function addButler(newButler) {
    setButlers(prevButlers => {
      const existsIndex = prevButlers.findIndex(b => b.id === newButler.id);
      if (existsIndex !== -1) {
        const updatedButlers = [...prevButlers];
        updatedButlers[existsIndex] = newButler;
        return updatedButlers;
      }
      return [...prevButlers, newButler];
    });
  }
  

  return (
    <ButlerContext.Provider value={{ activeButlerId, setActiveButlerId, butlers, addButler }}>
      {children}
    </ButlerContext.Provider>
  );
}

// 自定义hook
export function useButler() {
  return useContext(ButlerContext);
}
