// src/components/OldWebRTC.jsx
import React, { useEffect, useRef, useState } from 'react';

export default function OldWebRTC({ renderVideo = true, keepConnection = false }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);
  const pcRef = useRef(null);
  const textareaRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const animationFrameIdRef = useRef(null);
  const alphaVideoRef = useRef(null);

  const containerRef = useRef(null);

  const [useStun, setUseStun] = useState(false);
  const [started, setStarted] = useState(false);
  const [zoomWidth, setZoomWidth] = useState(600);
  const [greenThreshold, setGreenThreshold] = useState(50);
  const [videoReady, setVideoReady] = useState(false);
  const [alphaReady, setAlphaReady] = useState(false);
  const [processingMode, setProcessingMode] = useState('auto');
  const [errorMessage, setErrorMessage] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [resourceCheckCount, setResourceCheckCount] = useState(0);
  const [maxResourceCheckCount] = useState(30); // 最多检查30次，约3秒

  // 立即更新的标志
  const isStartedRef = useRef(false);
  const isVideoReadyRef = useRef(false);
  const videoTracksRef = useRef([]); // 存储视频轨道

  const initialWidth = 600;
  const initialHeight = 450;
  const SIGNALING_URL = '/offer';
  const zoomStep = 50;

  const [chatPos, setChatPos] = useState(() => {
    try {
      const saved = localStorage.getItem('chatbox-position');
      if (saved) {
        const pos = JSON.parse(saved);
        if (typeof pos.left === 'number' && typeof pos.top === 'number') {
          return pos;
        }
      }
    } catch {}
    return { left: 20, top: 20 };
  });

  const draggingRef = useRef(false);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  function onDragStart(e) {
    if (e.target.tagName.toLowerCase() === 'textarea') {
      draggingRef.current = false;
      return;
    }
    
    draggingRef.current = true;
    const targetRect = e.currentTarget.getBoundingClientRect();
    if (e.type === 'touchstart' && e.touches?.[0]) {
      dragOffsetRef.current = {
        x: e.touches[0].clientX - targetRect.left,
        y: e.touches[0].clientY - targetRect.top,
      };
    } else {
      dragOffsetRef.current = {
        x: e.clientX - targetRect.left,
        y: e.clientY - targetRect.top,
      };
    }
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragMove(e) {
    if (!draggingRef.current) return;
  
    let clientX, clientY;
    if (e.type === 'touchmove' && e.touches?.[0]) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else if (e.type === 'mousemove') {
      clientX = e.clientX;
      clientY = e.clientY;
    } else {
      return;
    }
  
    if (!containerRef.current || !e.currentTarget) return;
  
    const containerRect = containerRef.current.getBoundingClientRect();
    const chatRect = e.currentTarget.getBoundingClientRect();
    const chatWidth = chatRect.width;
    const chatHeight = chatRect.height;
  
    let newLeft = clientX - containerRect.left - dragOffsetRef.current.x;
    let newTop = clientY - containerRect.top - dragOffsetRef.current.y;
  
    newLeft = Math.min(Math.max(0, newLeft), containerRect.width - chatWidth);
    newTop = Math.min(Math.max(0, newTop), containerRect.height - chatHeight);
  
    setChatPos({ left: newLeft, top: newTop });
  
    e.preventDefault();
    e.stopPropagation();
  }
  
  function onDragEnd(e) {
    if (draggingRef.current) {
      draggingRef.current = false;
      localStorage.setItem('chatbox-position', JSON.stringify(chatPos));
      e.preventDefault();
      e.stopPropagation();
    }
  }

  function isGreen(r, g, b) {
    return g > r + greenThreshold && g > b + greenThreshold;
  }

  // 计算透明度百分比
  function calculateTransparencyPercentage(data) {
    let transparentPixels = 0;
    
    for (let i = 3; i < data.length; i += 4) {
      if (data[i] === 0) {
        transparentPixels++;
      }
    }
    
    return (transparentPixels / (data.length / 4) * 100).toFixed(2);
  }

  function processVideoFrame() {
    const video = videoRef.current;
    const alphaVideo = alphaVideoRef.current;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    
    // 使用立即更新的标志
    if (!video || !canvas || !ctx || !isStartedRef.current || !isVideoReadyRef.current) {
      // 记录当前状态
      const checkCount = resourceCheckCount + 1;
      setResourceCheckCount(checkCount);
      
      console.log(`processVideoFrame: 等待资源准备就绪... (尝试 ${checkCount}/${maxResourceCheckCount})`);
      console.log(`started: ${started}, videoReady: ${videoReady}, alphaReady: ${alphaReady}`);
      console.log(`isStartedRef: ${isStartedRef.current}, isVideoReadyRef: ${isVideoReadyRef.current}`);
      
      if (video) {
        console.log(`视频状态: ${video.readyState} (0=HAVE_NOTHING, 1=HAVE_METADATA, 2=HAVE_CURRENT_DATA, 3=HAVE_FUTURE_DATA, 4=HAVE_ENOUGH_DATA)`);
        console.log(`视频宽度: ${video.videoWidth}, 视频高度: ${video.videoHeight}`);
        
        // 检查视频轨道状态
        if (video.srcObject && video.srcObject.getVideoTracks) {
          const videoTracks = video.srcObject.getVideoTracks();
          console.log(`视频轨道数量: ${videoTracks.length}`);
          
          videoTracks.forEach((track, index) => {
            console.log(`轨道 ${index + 1}: ID=${track.id}, 状态=${track.readyState}`);
          });
        }
      }
      
      if (canvas) {
        console.log(`Canvas状态: 宽度=${canvas.width}, 高度=${canvas.height}`);
      } else {
        console.log('Canvas未初始化');
      }
      
      if (ctx) {
        console.log('Canvas上下文已初始化');
      } else {
        console.log('Canvas上下文未初始化');
      }
      
      if (checkCount >= maxResourceCheckCount) {
        setErrorMessage('视频资源加载超时，请检查连接或刷新页面');
        console.error('视频资源加载超时，停止尝试');
        return;
      }
      
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    if (video.paused || video.ended) {
      console.log('processVideoFrame: 视频暂停或结束');
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    try {
      // 确保Canvas尺寸正确
      if (canvas.width !== video.videoWidth || canvas.height !== video.videoHeight) {
        console.log(`processVideoFrame: 设置Canvas尺寸为 ${video.videoWidth}x${video.videoHeight}`);
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
      }

      // 清除画布，确保透明背景
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // 根据处理模式决定如何渲染
      if (processingMode === 'separate' || (processingMode === 'auto' && alphaReady)) {
        console.log('processVideoFrame: 使用分离Alpha通道模式渲染');
        
        // 检查Alpha视频是否准备好
        if (!alphaVideo || !alphaVideo.readyState >= 2) {
          console.warn('processVideoFrame: Alpha通道未就绪，使用RGBA模式替代');
          renderRGBAFrame(video, ctx, canvas.width, canvas.height);
          animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
          return;
        }
        
        const rgbCanvas = document.createElement('canvas');
        rgbCanvas.width = canvas.width;
        rgbCanvas.height = canvas.height;
        const rgbCtx = rgbCanvas.getContext('2d');
        
        // 绘制RGB图像
        rgbCtx.drawImage(video, 0, 0, canvas.width, canvas.height);
        const rgbData = rgbCtx.getImageData(0, 0, canvas.width, canvas.height).data;
        
        // 绘制Alpha通道图像
        const alphaCanvas = document.createElement('canvas');
        alphaCanvas.width = canvas.width;
        alphaCanvas.height = canvas.height;
        const alphaCtx = alphaCanvas.getContext('2d');
        alphaCtx.drawImage(alphaVideo, 0, 0, canvas.width, canvas.height);
        const alphaData = alphaCtx.getImageData(0, 0, canvas.width, canvas.height).data;
        
        // 验证数据有效性
        if (rgbData.length !== alphaData.length) {
          console.error(`processVideoFrame: RGB数据长度(${rgbData.length})与Alpha数据长度(${alphaData.length})不匹配`);
          renderRGBAFrame(video, ctx, canvas.width, canvas.height);
          animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
          return;
        }
        
        // 创建合并后的图像数据
        const mergedImageData = ctx.createImageData(canvas.width, canvas.height);
        const mergedData = mergedImageData.data;
        
        // 合并RGB和Alpha通道
        for (let i = 0; i < rgbData.length; i += 4) {
          mergedData[i] = rgbData[i];       // R
          mergedData[i + 1] = rgbData[i + 1];   // G
          mergedData[i + 2] = rgbData[i + 2];   // B
          
          // 使用Alpha通道的亮度值作为透明度
          mergedData[i + 3] = alphaData[i];     // A
        }
        
        // 分析Alpha通道数据，用于调试
        const transparencyPercentage = calculateTransparencyPercentage(mergedData);
        console.log(`Alpha通道处理完成，透明度: ${transparencyPercentage}%`);
        setDebugInfo(`Alpha通道处理完成，透明度: ${transparencyPercentage}%`);
        
        // 将合并后的图像绘制到主Canvas
        ctx.putImageData(mergedImageData, 0, 0);
      } else {
        console.log('processVideoFrame: 使用RGBA模式渲染');
        renderRGBAFrame(video, ctx, canvas.width, canvas.height);
      }
    } catch (e) {
      console.error('processVideoFrame: 视频帧处理错误:', e);
      setErrorMessage(`视频渲染错误: ${e.message}`);
    }

    animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
  }

  function renderRGBAFrame(video, ctx, width, height) {
    if (!video || !ctx) {
      console.error('renderRGBAFrame: 缺少video或ctx');
      return;
    }
    
    try {
      // 确保视频有可用帧
      if (video.readyState < 2) {
        console.log('renderRGBAFrame: 视频尚未准备好绘制，readyState:', video.readyState);
        animationFrameIdRef.current = requestAnimationFrame(() => renderRGBAFrame(video, ctx, width, height));
        return;
      }
      
      // 确保Canvas上下文支持透明度
      if (ctx.globalCompositeOperation !== 'source-over') {
        ctx.globalCompositeOperation = 'source-over';
        console.log('设置Canvas合成操作为 source-over');
      }
      
      // 直接绘制视频到Canvas
      ctx.drawImage(video, 0, 0, width, height);
      
      // 获取图像数据
      const imageData = ctx.getImageData(0, 0, width, height);
      const data = imageData.data;
      
      // 检查是否有透明度通道
      let hasTransparency = false;
      for (let i = 3; i < data.length; i += 4) {
        if (data[i] < 255) {
          hasTransparency = true;
          break;
        }
      }
      
      // 如果没有检测到透明度，尝试手动处理绿色背景
      if (!hasTransparency && processingMode === 'rgba') {
        console.log('未检测到Alpha透明度，尝试手动处理绿色背景');
        
        // 处理绿色背景
        const transparencyPercentage = processGreenScreen(imageData);
        console.log(`绿色背景处理完成，透明度: ${transparencyPercentage}%`);
        
        // 将处理后的图像数据写回Canvas
        ctx.putImageData(imageData, 0, 0);
        
        // 更新调试信息
        setDebugInfo(`绿色背景处理完成，透明度: ${transparencyPercentage}%`);
      } else {
        // 分析现有Alpha通道
        const transparencyPercentage = calculateTransparencyPercentage(data);
        console.log(`检测到Alpha通道，透明度: ${transparencyPercentage}%`);
        setDebugInfo(`检测到Alpha通道，透明度: ${transparencyPercentage}%`);
      }
    } catch (error) {
      console.error('renderRGBAFrame 错误:', error);
      setErrorMessage(`视频渲染错误: ${error.message}`);
    }
  }

  // 绿色背景抠图功能
  function processGreenScreen(imageData) {
    const data = imageData.data;
    let transparentPixels = 0;
    
    for (let i = 0; i < data.length; i += 4) {
      const r = data[i];
      const g = data[i + 1];
      const b = data[i + 2];
      
      // 如果是绿色，设置透明度为0
      if (isGreen(r, g, b)) {
        data[i + 3] = 0;
        transparentPixels++;
      }
    }
    
    // 返回被抠除的像素比例
    return (transparentPixels / (data.length / 4) * 100).toFixed(2);
  }

  function analyzeAlphaData(data) {
    let totalPixels = data.length / 4;
    let transparentPixels = 0;
    let semiTransparentPixels = 0;
    
    for (let i = 3; i < data.length; i += 4) {
      if (data[i] === 0) {
        transparentPixels++;
      } else if (data[i] < 255) {
        semiTransparentPixels++;
      }
    }
    
    const transparentPercent = (transparentPixels / totalPixels * 100).toFixed(2);
    const semiTransparentPercent = (semiTransparentPixels / totalPixels * 100).toFixed(2);
    
    const debugMsg = `Alpha通道分析: 共${totalPixels}个像素，${transparentPixels}个完全透明 (${transparentPercent}%)，${semiTransparentPixels}个半透明 (${semiTransparentPercent}%)`;
    console.log(debugMsg);
    setDebugInfo(debugMsg);
  }

  async function negotiate() {
    console.log('negotiate: 开始建立WebRTC连接');
    const pc = pcRef.current;
    if (!pc) return;

    pc.addTransceiver('video', { direction: 'recvonly' });
    pc.addTransceiver('audio', { direction: 'recvonly' });

    try {
      const offer = await pc.createOffer();
      console.log('negotiate: 创建Offer成功');
      await pc.setLocalDescription(offer);
      console.log('negotiate: 设置本地描述成功');

      await new Promise((resolve) => {
        if (pc.iceGatheringState === 'complete') {
          console.log('negotiate: ICE收集已完成');
          resolve();
        } else {
          const checkState = () => {
            if (pc.iceGatheringState === 'complete') {
              pc.removeEventListener('icegatheringstatechange', checkState);
              console.log('negotiate: ICE收集完成');
              resolve();
            }
          };
          pc.addEventListener('icegatheringstatechange', checkState);
          console.log('negotiate: 正在收集ICE候选...');
        }
      });

      console.log('negotiate: 发送Offer到服务器:', SIGNALING_URL);
      const response = await fetch(SIGNALING_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sdp: pc.localDescription.sdp,
          type: pc.localDescription.type,
        }),
      });
      
      if (!response.ok) {
        throw new Error(`服务器响应错误: ${response.status}`);
      }
      
      const answer = await response.json();
      console.log('negotiate: 收到服务器Answer');
      await pc.setRemoteDescription(answer);
      console.log('negotiate: 设置远程描述成功，WebRTC连接建立完成');
    } catch (e) {
      console.error('negotiate: 协商失败:', e);
      alert('协商失败: ' + e.message);
      setErrorMessage(`WebRTC连接失败: ${e.message}`);
    }
  }

  function start() {
    console.log('start: 启动WebRTC连接');
    if (isStartedRef.current) return;

    const config = { sdpSemantics: 'unified-plan' };
    const pc = new RTCPeerConnection(config);
    pcRef.current = pc;

    // 立即更新标志
    isStartedRef.current = true;
    
    // 监听连接状态变化
    pc.addEventListener('connectionstatechange', () => {
      console.log(`WebRTC连接状态变化: ${pc.connectionState}`);
      if (pc.connectionState === 'failed') {
        setErrorMessage('WebRTC连接失败');
      }
    });

    pc.addEventListener('track', (evt) => {
      console.log('收到新的媒体轨道:', evt.track.kind, evt.streams[0].id);
      
      // 存储所有视频轨道
      if (evt.track.kind === 'video') {
        videoTracksRef.current.push(evt.track);
        console.log(`已存储视频轨道，总数: ${videoTracksRef.current.length}`);
      }
      
      if (evt.track.kind === 'video') {
        // 检查是否是Alpha通道视频
        if (evt.streams[0].id.endsWith('_alpha')) {
          console.log('收到Alpha通道视频流');
          if (alphaVideoRef.current) {
            alphaVideoRef.current.srcObject = evt.streams[0];
            
            // Alpha通道视频元数据加载后设置就绪状态
            alphaVideoRef.current.addEventListener('loadedmetadata', () => {
              console.log('Alpha通道视频元数据加载完成');
              setAlphaReady(true);
              console.log('切换到分离通道模式');
              setProcessingMode('separate');
            }, { once: true });
            
            alphaVideoRef.current.addEventListener('canplay', () => {
              console.log('Alpha通道视频可以播放');
            }, { once: true });
            
            alphaVideoRef.current.addEventListener('error', (e) => {
              console.error('Alpha通道视频错误:', e.target.error);
              setErrorMessage(`Alpha通道错误: ${e.target.error.message}`);
            });
          }
        } else {
          // 普通RGB视频
          console.log('收到RGB视频流');
          if (videoRef.current) {
            videoRef.current.srcObject = evt.streams[0];
            
            // 视频元数据加载后初始化Canvas
            videoRef.current.addEventListener('loadedmetadata', () => {
              console.log('RGB视频元数据加载完成，尺寸:', videoRef.current.videoWidth, videoRef.current.videoHeight);
              
              // 只有当视频有有效尺寸时才设置为就绪状态
              if (videoRef.current.videoWidth > 0 && videoRef.current.videoHeight > 0) {
                isVideoReadyRef.current = true;
                setVideoReady(true);
                
                // 延迟初始化Canvas，确保所有状态都已更新
                setTimeout(() => {
                  const canvas = canvasRef.current;
                  if (canvas) {
                    // 确保Canvas上下文支持透明度
                    const ctx = canvas.getContext('2d', { 
                      willReadFrequently: true,
                      alpha: true // 明确请求支持透明度
                    });
                    
                    if (!ctx) {
                      console.error('无法获取Canvas 2D上下文');
                      setErrorMessage('无法初始化视频渲染上下文');
                      return;
                    }
                    
                    ctxRef.current = ctx;
                    
                    // 设置合成操作，确保透明度正确处理
                    ctx.globalCompositeOperation = 'source-over';
                    
                    // 设置Canvas尺寸
                    canvas.width = videoRef.current.videoWidth;
                    canvas.height = videoRef.current.videoHeight;
                    
                    console.log('Canvas初始化完成，尺寸:', canvas.width, canvas.height);
                    
                    // 只有当视频可以播放时才开始处理帧
                    if (videoRef.current.readyState >= 2) {
                      console.log('视频已准备好，开始处理视频帧');
                      setResourceCheckCount(0); // 重置资源检查计数
                      processVideoFrame();
                    } else {
                      console.log('视频尚未可以播放，等待canplay事件');
                      
                      // 监听canplay事件
                      const onCanPlay = () => {
                        console.log('视频canplay事件触发，开始处理视频帧');
                        videoRef.current.removeEventListener('canplay', onCanPlay);
                        setResourceCheckCount(0);
                        processVideoFrame();
                      };
                      
                      videoRef.current.addEventListener('canplay', onCanPlay);
                    }
                  }
                }, 0);
              } else {
                console.warn('视频尺寸无效，宽度:', videoRef.current.videoWidth, '高度:', videoRef.current.videoHeight);
              }
            }, { once: true });
            
            videoRef.current.addEventListener('canplay', () => {
              console.log('RGB视频可以播放，readyState:', videoRef.current.readyState);
              
              // 确保started状态为true
              if (!started) {
                console.log('设置started为true');
                setStarted(true);
              }
              
              // 如果视频元数据已加载但尚未开始处理帧
              if (isVideoReadyRef.current && !animationFrameIdRef.current) {
                console.log('视频已准备好，开始处理视频帧（来自canplay事件）');
                setResourceCheckCount(0);
                processVideoFrame();
              }
            }, { once: true });
            
            videoRef.current.addEventListener('error', (e) => {
              console.error('RGB视频错误:', e.target.error);
              setErrorMessage(`RGB视频错误: ${e.target.error.message}`);
            });
          }
        }
      } else if (evt.track.kind === 'audio') {
        console.log('收到音频流');
        if (audioRef.current) {
          audioRef.current.srcObject = evt.streams[0];
        }
      }
    });

    // 使用Promise确保所有状态都已更新
    Promise.resolve().then(() => {
      setStarted(true);
      setErrorMessage('');
      negotiate();
      setZoomWidth(initialWidth);
    });
  }

  function stop() {
    console.log('stop: 停止WebRTC连接');
    if (!isStartedRef.current) return;

    // 停止视频处理
    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
      animationFrameIdRef.current = null;
    }

    // 重置立即更新的标志
    isStartedRef.current = false;
    isVideoReadyRef.current = false;
    
    setStarted(false);
    setVideoReady(false);
    setAlphaReady(false);
    setProcessingMode('auto');
    setErrorMessage('');
    setDebugInfo('');
    setResourceCheckCount(0);

    // 停止所有媒体轨道
    if (videoRef.current?.srcObject) {
      console.log('停止RGB视频轨道');
      videoRef.current.srcObject.getTracks().forEach(track => {
        console.log(`停止轨道: ${track.kind}, ID: ${track.id}`);
        track.stop();
      });
      videoRef.current.srcObject = null;
    }
    if (alphaVideoRef.current?.srcObject) {
      console.log('停止Alpha通道视频轨道');
      alphaVideoRef.current.srcObject.getTracks().forEach(track => {
        console.log(`停止轨道: ${track.kind}, ID: ${track.id}`);
        track.stop();
      });
      alphaVideoRef.current.srcObject = null;
    }
    if (audioRef.current?.srcObject) {
      console.log('停止音频轨道');
      audioRef.current.srcObject.getTracks().forEach(track => {
        console.log(`停止轨道: ${track.kind}, ID: ${track.id}`);
        track.stop();
      });
      audioRef.current.srcObject = null;
    }

    // 关闭RTCPeerConnection
    if (!keepConnection) {
      setTimeout(() => {
        if (pcRef.current) {
          console.log('关闭RTCPeerConnection');
          pcRef.current.close();
          pcRef.current = null;
        }
      }, 500);
    }
  }

  function zoomIn() {
    setZoomWidth(w => w + zoomStep);
  }
  function zoomOut() {
    setZoomWidth(w => (w - zoomStep > 0 ? w - zoomStep : w));
  }

  const [message, setMessage] = useState('');
  const [imageFile, setImageFile] = useState(null);

  const sendTextMessage = async () => {
    if (!message.trim()) return;
    try {
      console.log('发送文本消息:', message);
      const response = await fetch('https://192.168.168.77:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: message }),
      });
      if (!response.ok) throw new Error('状态码: ' + response.status);
      console.log('文本发送成功');
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  const sendMultiModal = async () => {
    if (!imageFile) return;
    try {
      console.log('发送多模态消息，包含图片');
      const reader = new FileReader();
      reader.onloadend = async () => {
        const response = await fetch('https://192.168.1.70:6010/multimodal', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: message.trim(), image: reader.result }),
        });
        if (!response.ok) throw new Error('状态码: ' + response.status);
        console.log('多模态发送成功');
        setImageFile(null);
        setMessage('');
      };
      reader.readAsDataURL(imageFile);
    } catch (error) {
      console.error('多模态发送失败：', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (imageFile) {
      await sendMultiModal();
    } else {
      await sendTextMessage();
      setMessage('');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  useEffect(() => {
    console.log('组件挂载或卸载，清理动画帧');
    return () => {
      if (animationFrameIdRef.current) {
        console.log('取消动画帧请求');
        cancelAnimationFrame(animationFrameIdRef.current);
      }
    };
  }, []);

  // 使用useEffect监听状态变化
  useEffect(() => {
    if (isStartedRef.current && isVideoReadyRef.current && !animationFrameIdRef.current) {
      const video = videoRef.current;
      if (video && video.readyState >= 2) {
        console.log('状态就绪，开始处理视频帧（useEffect触发）');
        setResourceCheckCount(0);
        processVideoFrame();
      } else {
        console.log('视频尚未可以播放，等待（useEffect触发）');
      }
    }
  }, [isStartedRef.current, isVideoReadyRef.current]);

  const frostedGlassBtnStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    color: 'white',
    fontWeight: 'bold',
    padding: '8px 16px',
    cursor: 'pointer',
    borderRadius: 4,
    outline: 'none',
    transition: 'all 0.3s',
    fontSize: 14,
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
  };

  const disabledBtnStyle = {
    ...frostedGlassBtnStyle,
    opacity: 0.4,
    cursor: 'not-allowed',
    boxShadow: 'none',
  };

  const getBtnHoverStyle = (disabled) => {
    if (disabled) return {};
    return {
      backgroundColor: 'rgba(255, 255, 255, 0.2)',
      boxShadow: '0 6px 20px rgba(255, 255, 255, 0.3)',
      transform: 'scale(1.05)',
    };
  };

  const [startBtnHover, setStartBtnHover] = useState(false);
  const [stopBtnHover, setStopBtnHover] = useState(false);
  const [zoomInHover, setZoomInHover] = useState(false);
  const [zoomOutHover, setZoomOutHover] = useState(false);
  const [interruptBtnHover, setInterruptBtnHover] = useState(false);

  const handleInterruptClick = async () => {
    try {
      console.log('发送打断请求');
      const resp = await fetch('http://192.168.168.77:6010/api/interrupt_speaking', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!resp.ok) throw new Error('状态码: ' + resp.status);
      console.log('打断请求发送成功');
    } catch (e) {
      console.error('打断接口异常:', e);
    }
  };

  return (
    <div ref={containerRef} style={{ maxWidth: 1280, position: 'relative', minHeight: '100vh' }}>
      <div className="option" style={{ marginBottom: 8 }}>
        <input
          id="use-stun"
          type="checkbox"
          checked={useStun}
          onChange={(e) => setUseStun(e.target.checked)}
          style={{ accentColor: 'white' }}
          disabled={started}
          hidden
        />
      </div>

      <div style={{ marginBottom: 8 }}>
        <button
          onClick={zoomIn}
          style={{
            ...frostedGlassBtnStyle,
            marginRight: 8,
            ...(zoomInHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomInHover(true)}
          onMouseLeave={() => setZoomInHover(false)}
          type="button"
        >
          放大
        </button>
        <button
          onClick={zoomOut}
          style={{
            ...frostedGlassBtnStyle,
            ...(zoomOutHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomOutHover(true)}
          onMouseLeave={() => setZoomOutHover(false)}
          type="button"
        >
          缩小
        </button>
      </div>

      <button
        onClick={start}
        disabled={isStartedRef.current}
        style={{
          ...(isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(startBtnHover && !isStartedRef.current ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStartBtnHover(true)}
        onMouseLeave={() => setStartBtnHover(false)}
      >
        启动
      </button>
      <button
        onClick={stop}
        disabled={!isStartedRef.current}
        style={{
          ...(!isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(stopBtnHover && isStartedRef.current ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStopBtnHover(true)}
        onMouseLeave={() => setStopBtnHover(false)}
      >
        停止
      </button>

      {/* 显示当前状态和错误信息 */}
      {errorMessage && (
        <div style={{ color: 'red', margin: '10px 0', padding: '10px', background: 'rgba(0,0,0,0.5)', borderRadius: 4 }}>
          <strong>错误:</strong> {errorMessage}
        </div>
      )}
      
      <div style={{ color: 'white', margin: '10px 0' }}>
        <strong>状态:</strong> {started ? '已连接' : '未连接'} | 
        <strong>模式:</strong> {processingMode} | 
        <strong>视频:</strong> {videoReady ? '就绪' : '加载中'} | 
        <strong>Alpha:</strong> {alphaReady ? '就绪' : '未检测到'}
      </div>
      
      {/* 显示调试信息 */}
      {debugInfo && (
        <div style={{ color: 'yellow', margin: '10px 0', padding: '10px', background: 'rgba(0,0,0,0.5)', borderRadius: 4, whiteSpace: 'pre-wrap' }}>
          <strong>调试信息:</strong> {debugInfo}
        </div>
      )}

      {renderVideo && (
        <div id="media" style={{ marginTop: 20 }}>
          <h2 style={{ color: 'white' }}>管家</h2>

          <audio ref={audioRef} autoPlay />
          
          {/* 原视频标签（临时显示用于调试） */}
          <video
            ref={videoRef}
            style={{
              display: 'block',
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 10,
              zIndex: 100,
              border: '2px solid red'
            }}
            autoPlay
            playsInline
          />
          
          {/* Alpha通道视频（隐藏） */}
          <video
            ref={alphaVideoRef}
            style={{
              display: 'none',
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 130,
              zIndex: 100,
              border: '2px solid green'
            }}
            autoPlay
            playsInline
          />
          
          {/* Canvas（主显示区域） */}
          <canvas
            ref={canvasRef}
            style={{
              width: zoomWidth,
              height: (zoomWidth * initialHeight) / initialWidth,
              display: 'block',
              position: 'relative',
              zIndex: 10,
              border: '2px solid white',
              backgroundColor: 'transparent' // 明确设置背景为透明
            }}
          />
        </div>
      )}

      <div
        style={{
          position: 'absolute',
          left: chatPos.left,
          top: chatPos.top,
          width: 320,
          borderRadius: 6,
          padding: 12,
          color: 'white',
          userSelect: 'none',
          cursor: draggingRef.current ? 'grabbing' : 'grab',
          zIndex: 9999,
          backdropFilter: 'blur(10px)',
          backgroundColor: 'rgba(0,0,0,0.5)',
        }}
        onMouseDown={onDragStart}
        onMouseMove={onDragMove}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={onDragStart}
        onTouchMove={onDragMove}
        onTouchEnd={onDragEnd}
      >
        <form
          id="echo-form"
          onSubmit={handleSubmit}
          style={{ display: 'flex', flexDirection: 'column', gap: 8 }}
        >
          <textarea
            ref={textareaRef}
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            style={{
              width: '100%',
              resize: 'vertical',
              padding: 8,
              borderRadius: 8,
              border: '1px solid rgba(255,255,255,0.3)',
              backgroundColor: 'rgba(255,255,255,0.1)',
              color: 'white',
              outline: 'none',
            }}
            placeholder="请输入文本..."
            onClick={(e) => e.stopPropagation()}
          />
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
            <button
              type="button"
              onClick={handleInterruptClick}
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 16px',
                fontSize: 14,
              }}
              onMouseEnter={() => setInterruptBtnHover(true)}
              onMouseLeave={() => setInterruptBtnHover(false)}
            >
              打断说话
            </button>
            <label
              htmlFor="image-upload"
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 12px',
                cursor: 'pointer',
              }}
            >
              上传图片
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleImageChange}
            />
            <button
              type="submit"
              style={{ ...frostedGlassBtnStyle, padding: '6px 16px' }}
            >
              发送
            </button>
          </div>
          {imageFile && (
            <div style={{ margin: '10px 0', padding: 8, border: '1px solid rgba(255,255,255,0.3)', borderRadius: 4, backgroundColor: 'rgba(255,255,255,0.1)' }}>
              <p style={{ margin: '0 0 8px 0' }}>已选择图片: {imageFile.name}</p>
              <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={() => setImageFile(null)}
                  style={{
                    backgroundColor: 'rgba(255, 0, 0, 0.3)',
                    border: '1px solid rgba(255, 0, 0, 0.5)',
                    color: 'white',
                    padding: '4px 8px',
                    cursor: 'pointer',
                    borderRadius: 4,
                    outline: 'none',
                    fontSize: 12,
                  }}
                >
                  移除
                </button>
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}
