import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl';
import WebRTCPlayer from './components/OldWebRTC';

import FloatingContainer from './components/FloatingContainer';
import SpeechRecognizer from './components/SpeechRecognizer';
import SseListener from './components/SseListener';

import ButlerShowcase from './components/ButlerShowcase'; // 新增，导入管家模块

const ItemTypes = {
  PANEL: 'panel',
};

function DraggableResizablePanel({
  id,
  left,
  top,
  width = 320,
  height = 200,
  zIndex,
  movePanel,
  bringToFront,
  resizePanel,
  children,
}) {
  const dragRef = React.useRef(null);
  const dropRef = React.useRef(null);

  // 这里依赖数组改为空，防止频繁绑定解绑导致的事件失效
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top, width, height },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), []);

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!dropRef.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        movePanel(item.id, newLeft, newTop, width, height);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  }), [left, top, width, height]);

  drag(dragRef);
  drop(dropRef);

  const onResize = (event, { size }) => {
    resizePanel(id, size.width, size.height);
  };

  return (
    <div
      ref={dropRef}
      style={{
        position: 'absolute',
        left,
        top,
        zIndex,
        userSelect: 'none',
      }}
    >
      <ResizableBox
        width={width}
        height={height}
        minConstraints={[150, 100]}
        maxConstraints={[800, 600]}
        onResize={onResize}
        resizeHandles={['se']}
        // 这里去掉 handle 自定义，直接使用默认拖拽点，防止样式/事件冲突
        // handle={<span className="custom-handle custom-handle-se" />}
      >
        <div
          style={{
            width: '100%',
            height: '100%',
            border: '1px solid #888',
            borderRadius: 4,
            backgroundColor: 'rgba(255, 255, 255, 0)',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            userSelect: 'none',
          }}
        >
          {/* 拖动杆 */}
          <div
            ref={dragRef}
            onMouseDown={() => bringToFront(id)}
            style={{
              height: 30,
              backgroundColor: 'rgba(0, 123, 255, 0.8)',
              color: 'white',
              cursor: 'grab',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              borderTopLeftRadius: 4,
              borderTopRightRadius: 4,
              fontWeight: 'bold',
            }}
          >
            拖动我移动
          </div>
          <div
            style={{
              flex: 1,
              padding: 12,
              overflow: 'auto',
              userSelect: 'auto',
            }}
          >
            {children}
          </div>
        </div>
      </ResizableBox>
    </div>
  );
}


function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      return JSON.parse(saved);
    }
  } catch (e) {
    console.warn('读取面板位置失败', e);
  }
  return [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'floating', left: 20, top: 580, width: 320, height: 200, zIndex: 1 },
    { id: 'sse', left: 20, top: 740, width: 320, height: 200, zIndex: 1 },
  ];
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);

  const [panels, setPanels] = useState(loadPanels);
  const maxZIndex = useRef(1);

  const [selectedMenu, setSelectedMenu] = useState('home');

  function handleSendText(text) {
    console.log('识别到的文本:', text);
  }

  function handleSseMessage(data) {
    console.log('收到 SSE 消息:', data);
    setSseMessages((prev) => [...prev, data]);
  }

  // 新增边界限制，防止面板拖出屏幕
  function movePanel(id, left, top, width, height) {
    const maxLeft = window.innerWidth - width;
    const maxTop = window.innerHeight - height;

    const boundedLeft = Math.min(Math.max(0, left), maxLeft);
    const boundedTop = Math.min(Math.max(0, top), maxTop);

    setPanels((prev) =>
      prev.map((p) =>
        p.id === id ? { ...p, left: boundedLeft, top: boundedTop } : p
      )
    );
  }

  function resizePanel(id, width, height) {
    setPanels((prev) =>
      prev.map((p) =>
        p.id === id
          ? {
              ...p,
              width,
              height,
            }
          : p
      )
    );
  }

  function bringToFront(id) {
    maxZIndex.current += 1;
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, zIndex: maxZIndex.current } : p))
    );
  }

  useEffect(() => {
    localStorage.setItem('panels', JSON.stringify(panels));
  }, [panels]);

  const handleMenuClick = (menuKey) => {
    setSelectedMenu(menuKey);
  };

  // ------------ 新增默认面板变量，方便重置使用
  const defaultPanels = [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'floating', left: 20, top: 580, width: 320, height: 200, zIndex: 1 },
    { id: 'sse', left: 20, top: 740, width: 320, height: 200, zIndex: 1 },
  ];

  // 新增重置所有面板函数
  const resetAllPanels = () => {
    localStorage.removeItem('panels');
    setPanels(defaultPanels);
  };

  // 新增重置单个面板函数
  const resetSinglePanel = (id) => {
    setPanels((prev) =>
      prev.map((p) => {
        if (p.id === id) {
          const defaultPanel = defaultPanels.find((dp) => dp.id === id);
          return defaultPanel || p;
        }
        return p;
      })
    );
  };

  const renderContent = () => {
    switch (selectedMenu) {
      case 'home':
        return (
          <>
            <SseListener onMessage={handleSseMessage} />
            {panels.map(({ id, left, top, width, height, zIndex }) => {
              switch (id) {
                case 'audio':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <AudioTranscriber />
                    </DraggableResizablePanel>
                  );
                case 'speech':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <SpeechRecognizer onSendText={handleSendText} />
                    </DraggableResizablePanel>
                  );
                case 'camera':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <CameraControl />
                    </DraggableResizablePanel>
                  );
                case 'webrtc':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <WebRTCPlayer />
                    </DraggableResizablePanel>
                  );
                case 'floating':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <FloatingContainer>
                        <div>
                          <h4>Draggable Floating Box</h4>
                          <p>拖动我试试</p>
                        </div>
                      </FloatingContainer>
                    </DraggableResizablePanel>
                  );
                case 'sse':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <div
                        style={{
                          marginTop: 20,
                          padding: 10,
                          border: '1px solid #ccc',
                          borderRadius: 4,
                          maxHeight: '100%',
                          overflowY: 'auto',
                          backgroundColor: 'rgba(255,255,255,0)',
                          position: 'relative',
                          zIndex: 0,
                          height: '100%',
                        }}
                      >
                        <h4>SSE 消息</h4>
                        {sseMessages.length === 0 ? (
                          <p>等待接收消息...</p>
                        ) : (
                          <ul>
                            {sseMessages.map((msg, idx) => (
                              <li key={idx}>{msg}</li>
                            ))}
                          </ul>
                        )}
                      </div>
                    </DraggableResizablePanel>
                  );
                default:
                  return null;
              }
            })}
          </>
        );
      case 'butler':
        return (
          <div style={{ padding: 20 }}>
            <h2>管家页面</h2>
            <ButlerShowcase /> {/* 新增，管家页面内容替换 */}
          </div>
        );
      case 'ops':
        return (
          <div style={{ padding: 20 }}>
            <h2>运维页面</h2>
            <p>这里是运维页面的内容，请根据需求替换。</p>
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: 20 }}>
            <h2>设置页面</h2>

            {/* 新增重置所有面板按钮 */}
            <button
              onClick={resetAllPanels}
              style={{
                marginBottom: 20,
                padding: '8px 16px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              重置所有面板到默认
            </button>

            <div>
              <h3>重置单个面板位置</h3>
              {panels.map(({ id, left, top, width, height }) => {
                const defaultPanel = defaultPanels.find((dp) => dp.id === id);
                return (
                  <div
                    key={id}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: 10,
                      border: '1px solid #ccc',
                      borderRadius: 4,
                      padding: 10,
                      maxWidth: 400,
                      backgroundColor: 'rgba(0,123,255,0.05)',
                    }}
                  >
                    <div>
                      <strong>{id}</strong>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        当前：{left},{top}，大小：{width}x{height}
                      </div>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        默认：{defaultPanel?.left},{defaultPanel?.top}，大小：
                        {defaultPanel?.width}x{defaultPanel?.height}
                      </div>
                    </div>
                    <button
                      onClick={() => resetSinglePanel(id)}
                      style={{
                        padding: '4px 10px',
                        borderRadius: 4,
                        border: 'none',
                        backgroundColor: '#28a745',
                        color: 'white',
                        cursor: 'pointer',
                      }}
                    >
                      重置
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
      <h1>React AI Assistant Demo</h1>

      <div
        style={{
          display: 'flex',
          gap: 20,
          marginTop: 20,
          marginBottom: 20,
        }}
        role="menu"
      >
        {[
          { key: 'home', label: '主页' },
          { key: 'butler', label: '管家' },
          { key: 'ops', label: '运维' },
          { key: 'settings', label: '设置' },
        ].map(({ key, label }) => (
          <button
            key={key}
            onClick={() => handleMenuClick(key)}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: '2px solid #00e5ff',
              backgroundColor: selectedMenu === key ? '#00e5ff' : '#e7f1ff',
              color: selectedMenu === key ? 'white' : '#00e5ff',
              fontSize: 16,
              fontWeight: 'bold',
              cursor: 'pointer',
              outline: 'none',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              transition: 'background-color 0.3s, color 0.3s',
            }}
            aria-label={label}
            aria-haspopup="true"
          >
            {label}
          </button>
        ))}
      </div>

      {renderContent()}
    </div>
  );
}
