import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';

import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl';
import WebRTCPlayer from './components/OldWebRTC';

import FloatingContainer from './components/FloatingContainer';
import SpeechRecognizer from './components/SpeechRecognizer';
import SseListener from './components/SseListener';

const ItemTypes = {
  PANEL: 'panel',
};

function DraggablePanel({ id, left, top, zIndex, movePanel, bringToFront, children }) {
  const ref = React.useRef(null);

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  // 监听拖拽状态变化，拖拽开始时调用 bringToFront
  useEffect(() => {
    if (isDragging) {
      bringToFront(id);
    }
  }, [isDragging, bringToFront, id]);

  const [, drop] = useDrop({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!ref.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        movePanel(item.id, newLeft, newTop);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  });

  drag(drop(ref));

  return (
    <div
      ref={ref}
      style={{
        position: 'absolute',
        left,
        top,
        zIndex,
        width: 320,
        padding: 12,
        backgroundColor: isDragging ? '#cce5ff' : '#f9f9f9',
        border: '1px solid #888',
        borderRadius: 4,
        cursor: 'move',
        boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
        userSelect: 'none',
        boxSizing: 'border-box',
      }}
    >
      {children}
    </div>
  );
}

// 从localStorage加载面板，失败使用默认值
function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      return JSON.parse(saved);
    }
  } catch (e) {
    console.warn('读取面板位置失败', e);
  }
  return [
    { id: 'audio', left: 20, top: 20, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, zIndex: 1 },
    { id: 'camera', left: 20, top: 320, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, zIndex: 1 },
    { id: 'floating', left: 20, top: 580, zIndex: 1 },
    { id: 'sse', left: 20, top: 740, zIndex: 1 }, // 新增 SSE 面板
  ];
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);

  const [panels, setPanels] = useState(loadPanels);
  const maxZIndex = useRef(1);

  function handleSendText(text) {
    console.log('识别到的文本:', text);
  }

  function handleSseMessage(data) {
    console.log('收到 SSE 消息:', data);
    setSseMessages((prev) => [...prev, data]);
  }

  function movePanel(id, left, top) {
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, left, top } : p))
    );
  }

  function bringToFront(id) {
    maxZIndex.current += 1;
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, zIndex: maxZIndex.current } : p))
    );
  }

  // panels变更时，存localStorage
  useEffect(() => {
    localStorage.setItem('panels', JSON.stringify(panels));
  }, [panels]);

  return (
    <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
      <h1>AI-Sphere-Butler Demo</h1>

      <SseListener onMessage={handleSseMessage} />

      {panels.map(({ id, left, top, zIndex }) => {
        switch (id) {
          case 'audio':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <AudioTranscriber />
              </DraggablePanel>
            );
          case 'speech':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <SpeechRecognizer onSendText={handleSendText} />
              </DraggablePanel>
            );
          case 'camera':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <CameraControl />
              </DraggablePanel>
            );
          case 'webrtc':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <WebRTCPlayer />
              </DraggablePanel>
            );
          case 'floating':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <FloatingContainer>
                  <div>
                    <h4>Draggable Floating Box</h4>
                    <p>拖动我试试</p>
                  </div>
                </FloatingContainer>
              </DraggablePanel>
            );
          case 'sse':
            return (
              <DraggablePanel
                key={id}
                id={id}
                left={left}
                top={top}
                zIndex={zIndex}
                movePanel={movePanel}
                bringToFront={bringToFront}
              >
                <div
                  style={{
                    marginTop: 20,
                    padding: 10,
                    border: '1px solid #ccc',
                    borderRadius: 4,
                    maxHeight: 150,
                    overflowY: 'auto',
                    backgroundColor: '#fafafa',
                    position: 'relative',
                    zIndex: 0,
                  }}
                >
                  <h4>SSE 消息</h4>
                  {sseMessages.length === 0 ? (
                    <p>等待接收消息...</p>
                  ) : (
                    <ul>
                      {sseMessages.map((msg, idx) => (
                        <li key={idx}>{msg}</li>
                      ))}
                    </ul>
                  )}
                </div>
              </DraggablePanel>
            );
          default:
            return null;
        }
      })}
    </div>
  );
}
