          ...(toggleControlsHover ? getBtnHoverStyle(false) : {}),
        }}
        onMouseEnter={() => setToggleControlsHover(true)}
        onMouseLeave={() => setToggleControlsHover(false)}
        // 修复：添加触摸事件处理，避免被动监听器问题
        onTouchStart={(e) => {
          if (e.cancelable) {
