              onMouseLeave={() => setZoomInHover(false)}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                zoomIn();
              }}
              onTouchEnd={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
              }}
            >
              放大
            </button>
            <button
              onClick={zoomOut}
              style={{
                ...frostedGlassBtnStyle,
                ...(zoomOutHover ? getBtnHoverStyle(false) : {}),
              }}
              onMouseEnter={() => setZoomOutHover(true)}
              onMouseLeave={() => setZoomOutHover(false)}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                zoomOut();
              }}
              onTouchEnd={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
              }}
            >
              缩小
            </button>
            <button
