                height: '6px',
                borderRadius: '3px',
                backgroundColor: '#00e5ff',
              }}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
              }}
              onTouchMove={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
              }}
            />
            <span>{greenThreshold.toFixed(1)}</span>
            <label style={{ marginLeft: 10 }}>整体透明度:</label>
            <input
              type="range"
              min="0.0"
