              onChange={(e) => setGlobalAlpha(parseFloat(e.target.value))}
              style={{ 
                width: 150,
                accentColor: '#00e5ff',
                WebkitAppearance: 'none',
                height: '6px',
                borderRadius: '3px',
                backgroundColor: '#00e5ff',
              }}
              // 修复：添加触摸事件处理，避免被动监听器问题
