                tabIndex={0}
                onClick={() => setUsePresetColorOpen((v) => !v)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    setUsePresetColorOpen((v) => !v);
                  }
                }}
                style={{
                  padding: '8px 12px',
                  borderRadius: 4,
                  border: '1px solid rgba(255,255,255,0.3)',
                  backgroundColor: 'transparent',
                  color: 'white',
                  cursor: 'pointer',
                  userSelect: 'none',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  fontWeight: 'bold',
                  fontSize: 14,
                }}
                aria-haspopup="listbox"
                aria-expanded={usePresetColorOpen}
                // 修复：添加触摸事件处理，避免被动监听器问题
                onTouchStart={(e) => {
                  if (e.cancelable) {
                    e.preventDefault();
                  }
                  setUsePresetColorOpen((v) => !v);
                }}
                onTouchEnd={(e) => {
                  if (e.cancelable) {
                    e.preventDefault();
                  }
                }}
              >
                {presetColors.find(c => c.value === chromaKeyColor)?.label || '请选择'}
                <svg
                  style={{ width: 16, height: 16, transform: usePresetColorOpen ? 'rotate(180deg)' : undefined, transition: 'transform 0.3s' }}
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="white"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <polyline points="6 9 12 15 18 9" />
                </svg>
              </div>
              {usePresetColorOpen && (
                <ul
                  role="listbox"
                  tabIndex={-1}
                  aria-activedescendant={chromaKeyColor}
                  style={{
                    marginTop: 4,
                    border: '1px solid rgba(255,255,255,0.3)',
                    borderRadius: 4,
                    backgroundColor: 'rgba(255, 255, 255, 0.15)',
                    maxHeight: 150,
                    overflowY: 'auto',
                    listStyle: 'none',
                    padding: 0,
                    position: 'absolute',
                    width: '100%',
                    zIndex: 1000,
                    userSelect: 'none',
                  }}
                >
                  {presetColors.map(({ value, label }) => {
                    const isSelected = value === chromaKeyColor;
                    return (
                      <li
                        id={value}
                        key={value}
                        role="option"
                        aria-selected={isSelected}
                        tabIndex={0}
                        onClick={() => {
                          setChromaKeyColor(value);
                          setUsePresetColorOpen(false);
                        }}
                        onKeyDown={(e) => {
                          if (e.key === 'Enter' || e.key === ' ') {
                            e.preventDefault();
                            setChromaKeyColor(value);
                            setUsePresetColorOpen(false);
                          }
                        }}
                        style={{
                          padding: '8px 12px',
                          cursor: 'pointer',
                          backgroundColor: isSelected ? '#00e5ff' : 'transparent',
                          color: isSelected ? '#fff' : 'white',
                          fontWeight: isSelected ? '700' : '500',
                          transition: 'background-color 0.2s, color 0.2s',
                        }}
                        onMouseEnter={(e) => {
                          if (!isSelected) {
                            e.currentTarget.style.backgroundColor = 'rgba(0, 229, 255, 0.15)';
                            e.currentTarget.style.color = '#00e5ff';
                          }
                        }}
                        onMouseLeave={(e) => {
                          if (!isSelected) {
                            e.currentTarget.style.backgroundColor = 'transparent';
                            e.currentTarget.style.color = 'white';
                          }
                        }}
                        // 修复：添加触摸事件处理，避免被动监听器问题
                        onTouchStart={(e) => {
                          if (e.cancelable) {
                            e.preventDefault();
                          }
                          setChromaKeyColor(value);
                          setUsePresetColorOpen(false);
                        }}
                        onTouchEnd={(e) => {
                          if (e.cancelable) {
                            e.preventDefault();
                          }
                        }}
                      >
                        {label}
                      </li>
                    );
                  })}
                </ul>
              )}
            </div>
          )}
          {/* 自定义颜色选择器 */}
          {useCustomColor && (
            <div style={{ color: 'white', margin: '8px 0', display: 'flex', gap: 10, alignItems: 'center' }}>
              <label>自定义颜色:</label>
              <input
                type="color"
                value={customChromaKeyColor}
                onChange={(e) => setCustomChromaKeyColor(e.target.value)}
                style={{ width: 36, height: 36, border: 'none', cursor: 'pointer' }}
                // 修复：添加触摸事件处理，避免被动监听器问题
                onTouchStart={(e) => {
                  if (e.cancelable) {
                    e.preventDefault();
                  }
                }}
              />
              <span style={{ width: 60, height: 24, backgroundColor: customChromaKeyColor, borderRadius: 4 }} />
              <span>{customChromaKeyColor}</span>
            </div>
          )}
          <button
            onClick={start}
            disabled={isStartedRef.current}
            style={{
              ...(isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
              ...(startBtnHover && !isStartedRef.current ? getBtnHoverStyle(false) : {}),
            }}
            onMouseEnter={() => setStartBtnHover(true)}
            onMouseLeave={() => setStartBtnHover(false)}
            // 修复：添加触摸事件处理，避免被动监听器问题
            onTouchStart={(e) => {
              if (e.cancelable) {
                e.preventDefault();
              }
              if (!isStartedRef.current) {
                start();
              }
            }}
            onTouchEnd={(e) => {
              if (e.cancelable) {
                e.preventDefault();
              }
            }}
          >
            启动
          </button>
          <button
            onClick={stop}
            disabled={!isStartedRef.current}
            style={{
              ...(!isStartedRef.current ? disabledBtnStyle : frostedGlassBtnStyle),
              ...(stopBtnHover && isStartedRef.current ? getBtnHoverStyle(false) : {}),
            }}
            onMouseEnter={() => setStopBtnHover(true)}
            onMouseLeave={() => setStopBtnHover(false)}
            // 修复：添加触摸事件处理，避免被动监听器问题
            onTouchStart={(e) => {
              if (e.cancelable) {
                e.preventDefault();
              }
              if (isStartedRef.current) {
                stop();
              }
            }}
            onTouchEnd={(e) => {
              if (e.cancelable) {
                e.preventDefault();
              }
            }}
          >
            停止
          </button>
        </>
      )}
      {errorMessage && (
        <div
          style={{
            color: 'red',
            margin: '10px 0',
            padding: '10px',
            background: 'rgba(0,0,0,0.5)',
            borderRadius: 4,
          }}
        >
          <strong>错误:</strong> {errorMessage}
        </div>
      )}
      <div style={{ color: 'white', margin: '10px 0' }}>
        <strong>状态:</strong> {started ? '已连接' : '未连接'} | <strong>模式:</strong> {processingMode} |{' '}
        <strong>视频:</strong> {videoReady ? '就绪' : '加载中'} | <strong>Alpha:</strong>{' '}
        {alphaReady ? '就绪' : '未检测到'} | <strong>WebCodecs:</strong> 不使用 |{' '}
        <strong>连接尝试:</strong> {connectionAttempts}/{maxConnectionAttempts}
      </div>
      {debugInfo && (
        <div
          style={{
            color: 'yellow',
            margin: '10px 0',
            padding: '10px',
            background: 'transparent',
            borderRadius: 4,
            whiteSpace: 'pre-wrap',
          }}
        >
          <strong>调试信息:</strong> {debugInfo}
        </div>
      )}
      {renderVideo && (
        <div id="media" style={{ marginTop: 20, position: 'relative' }}>
          <h2 style={{ color: 'white' }}>管家</h2>
          <audio ref={audioRef} autoPlay />
          {/* 原视频窗口 - 极小化且透明 */}
          <video
            ref={videoRef}
            style={{
              position: 'fixed',
              width: '2px',
              height: '2px',
              opacity: 0.01,
              pointerEvents: 'none',
              left: '10px',
              top: '10px',
            }}
            autoPlay
            playsInline
            muted
          />
          {/* 抠图画布 */}
          <canvas
            ref={canvasRef}
            style={{
              width: zoomWidth,
              height: 'auto',
              display: 'block',
              position: 'relative',
              zIndex: 10,
              backgroundColor: 'transparent',
              maxWidth: '100%',
              boxSizing: 'border-box',
            }}
          />
          {/* 自定义画中画 */}
          {enablePip && !isPiPMode && (
            <video
              ref={pipVideoRef}
              style={{
                position: 'fixed',
                bottom: 10,
                right: 10,
                width: 160,
                height: 'auto',
                backgroundColor: 'transparent',
                zIndex: 10000,
                border: 'none',
                boxShadow: '0 0 15px rgba(255, 255, 255, 0.5)',
                borderRadius: 4,
              }}
              muted
              playsInline
              autoPlay
            />
          )}
        </div>
      )}
      {/* 修复：改进聊天框的触摸事件处理 */}
      <div
        style={{
          position: 'absolute',
          left: chatPos.left,
          top: chatPos.top,
          width: 320,
          borderRadius: 6,
          padding: 12,
          color: 'white',
          userSelect: 'none',
          cursor: draggingRef.current ? 'grabbing' : 'grab',
          zIndex: 9999,
          backdropFilter: 'blur(10px)',
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          border: '1px solid rgba(255,255,255,0.3)',
          boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
        }}
        onMouseDown={onDragStart}
        onMouseMove={onDragMove}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={onDragStart}
        onTouchMove={onDragMove}
        onTouchEnd={onDragEnd}
      >
        <form
          id="echo-form"
          onSubmit={handleSubmit}
          style={{ display: 'flex', flexDirection: 'column', gap: 8 }}
        >
          <textarea
            ref={textareaRef}
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            style={{
              width: '100%',
              resize: 'vertical',
              padding: 8,
              borderRadius: 8,
              border: '1px solid rgba(255,255,255,0.3)',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              color: 'white',
              outline: 'none',
              fontWeight: 'bold',
              fontSize: 14,
            }}
            placeholder="请输入文本..."
            onClick={(e) => e.stopPropagation()}
            // 修复：添加触摸事件处理，避免被动监听器问题
            onTouchStart={(e) => {
              e.stopPropagation();
              // 不阻止默认行为，允许文本选择
            }}
            onTouchMove={(e) => {
              e.stopPropagation();
            }}
            onTouchEnd={(e) => {
              e.stopPropagation();
            }}
          />
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8, alignItems: 'center' }}>
            <button
              type="button"
              onClick={handleInterruptClick}
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 16px',
                fontSize: 14,
                color: 'white',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255,255,255,0.3)',
                boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
              }}
              onMouseEnter={() => setInterruptBtnHover(true)}
              onMouseLeave={() => setInterruptBtnHover(false)}
              onClickCapture={(e) => e.stopPropagation()}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
                handleInterruptClick();
              }}
              onTouchEnd={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
              }}
            >
              打断说话
            </button>
            <label
              htmlFor="file-upload"
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 12px',
                cursor: 'pointer',
                color: 'white',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255,255,255,0.3)',
                boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
              }}
              onClickCapture={(e) => e.stopPropagation()}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
                // 触发文件输入的点击
                const fileInput = document.getElementById('file-upload');
                if (fileInput) {
                  fileInput.click();
                }
              }}
              onTouchEnd={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
              }}
            >
              上传文件
            </label>
            <input
              id="file-upload"
              type="file"
              style={{ display: 'none' }}
              onChange={handleFileChange}
            />
            <button
              type="submit"
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 16px',
                color: 'white',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255,255,255,0.3)',
                boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
              }}
              onClickCapture={(e) => e.stopPropagation()}
              // 修复：添加触摸事件处理，避免被动监听器问题
              onTouchStart={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
                handleSubmit(e);
              }}
              onTouchEnd={(e) => {
                if (e.cancelable) {
                  e.preventDefault();
                }
                e.stopPropagation();
              }}
            >
              发送
            </button>
          </div>
          {uploadStatus && (
            <div style={{ 
              marginTop: '8px', 
              color: uploadStatus.includes('失败') ? 'red' : 'white',
              fontSize: 13
            }}>
              {uploadStatus}
            </div>
          )}
          {file && (
            <div
              style={{
                margin: '10px 0',
                padding: 8,
                border: '1px solid rgba(255,255,255,0.3)',
                borderRadius: 4,
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                color: 'white',
                wordBreak: 'break-all',
              }}
            >
              <p style={{ margin: '0 0 8px 0', fontWeight: '600' }}>
                已选择文件: {file.name}
              </p>
              <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={() => setFile(null)}
                  style={{
                    backgroundColor: 'rgba(255, 0, 0, 0.3)',
                    border: '1px solid rgba(255, 0, 0, 0.5)',
                    color: 'white',
                    padding: '4px 8px',
                    cursor: 'pointer',
                    borderRadius: 4,
                    outline: 'none',
                    fontSize: 12,
                  }}
                  onClickCapture={(e) => e.stopPropagation()}
                  // 修复：添加触摸事件处理，避免被动监听器问题
                  onTouchStart={(e) => {
                    if (e.cancelable) {
                      e.preventDefault();
                    }
                    e.stopPropagation();
                    setFile(null);
                  }}
                  onTouchEnd={(e) => {
                    if (e.cancelable) {
                      e.preventDefault();
                    }
                    e.stopPropagation();
                  }}
                >
                  移除
                </button>
              </div>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}
