// src/components/OldWebRTC.jsx
import React, { useEffect, useRef, useState } from 'react';

export default function OldWebRTC({ renderVideo = true, keepConnection = false }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);
  const pcRef = useRef(null);
  const canvasRef = useRef(null);
  const pipRef = useRef(false);
  const frameCount = useRef(0);
  const lastFrameTime = useRef(0);
  const fpsCounter = useRef(0);
  const fpsDisplay = useRef(0);

  const containerRef = useRef(null);

  const [useStun, setUseStun] = useState(false);
  const [started, setStarted] = useState(false);
  const [zoomWidth, setZoomWidth] = useState(600);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [debugLogs, setDebugLogs] = useState([]);
  const [canvasError, setCanvasError] = useState(null);
  const [isCanvasRendering, setIsCanvasRendering] = useState(false);
  const [canvasInitialized, setCanvasInitialized] = useState(false);

  // 视频初始宽高
  const initialWidth = 600;
  const initialHeight = 450;

  // 信令服务器地址
  const SIGNALING_URL = '/offer';

  // 视频缩放比例
  const zoomStep = 50;

  // 聊天框位置状态
  const [chatPos, setChatPos] = useState(() => {
    try {
      const saved = localStorage.getItem('chatbox-position');
      if (saved) {
        const pos = JSON.parse(saved);
        return pos;
      }
    } catch {}
    return { left: 20, top: 20 };
  });

  // 绿幕抠图颜色阈值
  const [greenThreshold, setGreenThreshold] = useState({
    r: 80,
    g: 150,
    b: 80
  });

  // 拖拽相关状态
  const draggingRef = useRef(false);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  // 拖拽事件处理
  function onDragStart(e) {
    if (e.target.tagName.toLowerCase() === 'textarea') {
      draggingRef.current = false;
      return;
    }
    
    draggingRef.current = true;
    const targetRect = e.currentTarget.getBoundingClientRect();
    if (e.type === 'touchstart' && e.touches && e.touches[0]) {
      dragOffsetRef.current = {
        x: e.touches[0].clientX - targetRect.left,
        y: e.touches[0].clientY - targetRect.top,
      };
    } else {
      dragOffsetRef.current = {
        x: e.clientX - targetRect.left,
        y: e.clientY - targetRect.top,
      };
    }
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragMove(e) {
    if (!draggingRef.current) return;
  
    let clientX, clientY;
    if (e.type === 'touchmove' && e.touches && e.touches[0]) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else if (e.type === 'mousemove') {
      clientX = e.clientX;
      clientY = e.clientY;
    } else {
      return;
    }
  
    if (!containerRef.current || !e.currentTarget) return;
  
    const containerRect = containerRef.current.getBoundingClientRect();
    const chatRect = e.currentTarget.getBoundingClientRect();
    const chatWidth = chatRect.width;
    const chatHeight = chatRect.height;
  
    let newLeft = clientX - containerRect.left - dragOffsetRef.current.x;
    let newTop = clientY - containerRect.top - dragOffsetRef.current.y;
  
    newLeft = Math.min(Math.max(0, newLeft), containerRect.width - chatWidth);
    newTop = Math.min(Math.max(0, newTop), containerRect.height - chatHeight);
  
    setChatPos({ left: newLeft, top: newTop });
  
    e.preventDefault();
    e.stopPropagation();
  }
  
  function onDragEnd(e) {
    if (draggingRef.current) {
      draggingRef.current = false;
      localStorage.setItem('chatbox-position', JSON.stringify(chatPos));
      e.preventDefault();
      e.stopPropagation();
    }
  }

  // 调试日志记录
  const logDebug = (message, isImportant = true) => {
    if (isImportant || Math.random() > 0.95) { // 重要消息或5%的普通消息
      setDebugLogs(prev => {
        const newLogs = [...prev, `[${new Date().toLocaleTimeString()}] ${message}`];
        return newLogs.slice(-10);
      });
      console.log('[WebRTC Debug]', message);
    }
  };

  // WebRTC协商
  async function negotiate() {
    const pc = pcRef.current;
    if (!pc) return;

    pc.addTransceiver('video', { direction: 'recvonly' });
    pc.addTransceiver('audio', { direction: 'recvonly' });

    try {
      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);

      await new Promise((resolve) => {
        if (pc.iceGatheringState === 'complete') {
          resolve();
        } else {
          const checkState = () => {
            if (pc.iceGatheringState === 'complete') {
              pc.removeEventListener('icegatheringstatechange', checkState);
              resolve();
            }
          };
          pc.addEventListener('icegatheringstatechange', checkState);
        }
      });

      const response = await fetch(SIGNALING_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sdp: pc.localDescription.sdp,
          type: pc.localDescription.type,
        }),
      });
      const answer = await response.json();
      await pc.setRemoteDescription(answer);
      logDebug('WebRTC协商完成');
    } catch (e) {
      logDebug('协商失败: ' + e.message);
      alert('协商失败: ' + e.message);
    }
  }

  function start() {
    if (started) return;

    const config = { sdpSemantics: 'unified-plan' };
    const pc = new RTCPeerConnection(config);
    pcRef.current = pc;

    pc.addEventListener('track', (evt) => {
      logDebug(`收到${evt.track.kind}轨道`);
      if (evt.track.kind === 'video') {
        if (videoRef.current) {
          videoRef.current.srcObject = evt.streams[0];
          logDebug('视频轨道已绑定到video元素');
        }
      } else if (evt.track.kind === 'audio') {
        if (audioRef.current) {
          audioRef.current.srcObject = evt.streams[0];
          logDebug('音频轨道已绑定到audio元素');
        }
      }
    });

    pc.addEventListener('iceconnectionstatechange', () => {
      logDebug('ICE连接状态: ' + pc.iceConnectionState);
      if (pc.iceConnectionState === 'failed') {
        alert('ICE连接失败，请重试');
      }
    });

    setStarted(true);
    negotiate();
    setZoomWidth(initialWidth);
  }

  function stop() {
    if (!started) return;

    setStarted(false);
    if (document.pictureInPictureElement) {
      document.exitPictureInPicture();
      pipRef.current = false;
    }

    if (!keepConnection) {
      setTimeout(() => {
        if (pcRef.current) {
          pcRef.current.close();
          pcRef.current = null;
          logDebug('关闭WebRTC连接');
        }
      }, 500);

      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
        videoRef.current.srcObject = null;
      }
      if (audioRef.current && audioRef.current.srcObject) {
        audioRef.current.srcObject.getTracks().forEach((t) => t.stop());
        audioRef.current.srcObject = null;
      }
    }
    setVideoLoaded(false);
    setCanvasInitialized(false);
  }

  // 缩放功能
  function zoomIn() {
    setZoomWidth((w) => w + zoomStep);
  }
  function zoomOut() {
    setZoomWidth((w) => (w - zoomStep > 0 ? w - zoomStep : w));
  }

  // 消息发送功能
  const sendTextMessage = async () => {
    if (!message.trim()) return;
    try {
      const response = await fetch('https://192.168.168.77:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: message }),
      });
      if (!response.ok) throw new Error('状态码: ' + response.status);
      logDebug('文本发送成功');
    } catch (error) {
      logDebug('文本发送失败：' + error.message);
    }
  };

  const sendMultiModal = async () => {
    if (!imageFile) return;
    try {
      const reader = new FileReader();
      reader.onloadend = async () => {
        const response = await fetch('https://192.168.1.70:6010/multimodal', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: message.trim(), image: reader.result }),
        });
        if (!response.ok) throw new Error('状态码: ' + response.status);
        logDebug('多模态发送成功');
        setImageFile(null);
        setMessage('');
      };
      reader.readAsDataURL(imageFile);
    } catch (error) {
      logDebug('多模态发送失败：' + error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (imageFile) {
      await sendMultiModal();
    } else {
      await sendTextMessage();
      setMessage('');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  // 画中画功能
  const togglePictureInPicture = async () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    try {
      if (document.pictureInPictureElement) {
        await document.exitPictureInPicture();
        pipRef.current = false;
        logDebug('退出画中画');
      } else if (document.pictureInPictureEnabled) {
        await canvas.requestPictureInPicture();
        pipRef.current = true;
        logDebug('进入画中画');
      }
    } catch (error) {
      logDebug('画中画错误: ' + error.message);
      alert('画中画功能无法使用: ' + error.message);
    }
  };

  // 打断功能
  const handleInterruptClick = async () => {
    try {
      const resp = await fetch('http://192.168.168.77:6010/api/interrupt_speaking', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!resp.ok) throw new Error('状态码: ' + resp.status);
      logDebug('打断请求发送成功');
    } catch (e) {
      logDebug('打断接口异常: ' + e.message);
    }
  };

  // 视频加载与Canvas初始化
  useEffect(() => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    const handleVideoLoaded = () => {
      setVideoLoaded(true);
      
      // 检查Canvas是否正确获取上下文
      const ctx = canvas.getContext('2d', { willReadFrequently: true });
      if (!ctx) {
        setCanvasError('无法获取Canvas 2D上下文');
        logDebug('错误: 无法获取Canvas 2D上下文');
        return;
      }
      
      // 设置Canvas尺寸
      canvas.width = video.videoWidth || 1080;
      canvas.height = video.videoHeight || 1914;
      logDebug(`视频加载完成 (${canvas.width}x${canvas.height})`);
      
      // 初始化Canvas渲染
      if (!canvasInitialized) {
        setCanvasInitialized(true);
        logDebug('Canvas初始化成功');
      }
      
      // 计算FPS
      const calculateFPS = () => {
        const now = performance.now();
        if (lastFrameTime.current !== 0) {
          const delta = now - lastFrameTime.current;
          fpsCounter.current = Math.round(1000 / delta);
          // 平滑FPS显示
          fpsDisplay.current = Math.round((fpsDisplay.current * 4 + fpsCounter.current) / 5);
        }
        lastFrameTime.current = now;
      };
      
      // 帧处理函数
      const processVideoFrame = () => {
        if (!started || !videoLoaded) return;
        
        try {
          calculateFPS();
          
          // 绘制视频帧
          if (ctx) {
            ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
            
            // 显示调试信息
            ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
            ctx.fillRect(10, 10, 150, 50);
            ctx.fillStyle = 'white';
            ctx.font = '14px Arial';
            ctx.fillText(`Frame: ${frameCount.current}`, 20, 30);
            ctx.fillText(`FPS: ${fpsDisplay.current}`, 20, 50);
            
            frameCount.current++;
            setIsCanvasRendering(true);
          }
          
          // 继续下一帧
          requestAnimationFrame(processVideoFrame);
        } catch (error) {
          setCanvasError(error.message);
          logDebug('帧处理错误: ' + error.message);
          // 出错时仍继续尝试绘制下一帧
          requestAnimationFrame(processVideoFrame);
        }
      };
      
      // 开始处理帧
      if (started) {
        requestAnimationFrame(processVideoFrame);
      }
    };

    video.addEventListener('loadedmetadata', handleVideoLoaded);
    video.addEventListener('play', () => {
      logDebug('视频开始播放');
      if (started && videoLoaded) {
        requestAnimationFrame(() => {
          const ctx = canvas.getContext('2d');
          if (ctx) {
            ctx.fillStyle = 'rgba(255, 0, 0, 0.5)';
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            logDebug('尝试绘制红色测试矩形');
          }
        });
      }
    });

    // 监听视频错误
    video.addEventListener('error', (e) => {
      logDebug(`视频错误: ${video.error?.message || '未知错误'}`);
      setCanvasError(`视频错误: ${video.error?.message || '未知错误'}`);
    });

    return () => {
      video.removeEventListener('loadedmetadata', handleVideoLoaded);
      video.removeEventListener('play', () => {});
      video.removeEventListener('error', () => {});
    };
  }, [started, videoLoaded, canvasInitialized]);

  // 状态变化时重启帧处理
  useEffect(() => {
    if (started && videoLoaded && canvasRef.current) {
      logDebug('开始处理视频帧');
      frameCount.current = 0;
      lastFrameTime.current = 0;
      fpsCounter.current = 0;
      fpsDisplay.current = 0;
      
      // 尝试绘制初始帧
      requestAnimationFrame(() => {
        const canvas = canvasRef.current;
        if (canvas) {
          const ctx = canvas.getContext('2d');
          if (ctx) {
            ctx.fillStyle = 'rgba(0, 255, 0, 0.3)';
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            logDebug('绘制绿色测试背景');
          }
        }
      });
    }
  }, [started, videoLoaded]);

  // 通用样式
  const frostedGlassBtnStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    color: 'white',
    fontWeight: 'bold',
    padding: '8px 16px',
    cursor: 'pointer',
    borderRadius: 4,
    outline: 'none',
    transition: 'all 0.3s',
    fontSize: 14,
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
  };

  const disabledBtnStyle = {
    ...frostedGlassBtnStyle,
    opacity: 0.4,
    cursor: 'not-allowed',
    boxShadow: 'none',
  };

  const getBtnHoverStyle = (disabled) => !disabled && {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    boxShadow: '0 6px 20px rgba(255, 255, 255, 0.3)',
    transform: 'scale(1.05)',
  };

  // 按钮hover状态
  const [startBtnHover, setStartBtnHover] = useState(false);
  const [stopBtnHover, setStopBtnHover] = useState(false);
  const [zoomInHover, setZoomInHover] = useState(false);
  const [zoomOutHover, setZoomOutHover] = useState(false);
  const [interruptBtnHover, setInterruptBtnHover] = useState(false);
  const [pipBtnHover, setPipBtnHover] = useState(false);

  // 消息和图片状态
  const [message, setMessage] = useState('');
  const [imageFile, setImageFile] = useState(null);

  return (
    <div ref={containerRef} style={{ maxWidth: 1280, position: 'relative', minHeight: '100vh' }}>
      {/* 调试日志显示 */}
      <div style={{ 
        position: 'fixed', bottom: 10, right: 10, 
        backgroundColor: 'rgba(0,0,0,0.7)', color: 'white', 
        padding: 8, borderRadius: 4, maxWidth: 300, 
        maxHeight: 150, overflowY: 'auto', fontSize: 12,
        zIndex: 99999
      }}>
        <div style={{ fontWeight: 'bold', marginBottom: 4 }}>调试日志</div>
        {debugLogs.map((log, i) => (
          <div key={i} style={{ margin: 2 }}>{log}</div>
        ))}
        {canvasError && (
          <div style={{ color: 'red', marginTop: 4, fontWeight: 'bold' }}>
            Canvas错误: {canvasError}
          </div>
        )}
      </div>

      <div className="option" style={{ marginBottom: 8 }}>
        <input
          id="use-stun"
          type="checkbox"
          checked={useStun}
          onChange={(e) => setUseStun(e.target.checked)}
          style={{ accentColor: 'white', hidden: true }}
          disabled={started}
        />
      </div>

      <div style={{ marginBottom: 8 }}>
        <button
          onClick={zoomIn}
          style={{ ...frostedGlassBtnStyle, marginRight: 8, ...(zoomInHover ? getBtnHoverStyle(false) : {}) }}
          onMouseEnter={() => setZoomInHover(true)}
          onMouseLeave={() => setZoomInHover(false)}
        >
          放大
        </button>
        <button
          onClick={zoomOut}
          style={{ ...frostedGlassBtnStyle, marginRight: 8, ...(zoomOutHover ? getBtnHoverStyle(false) : {}) }}
          onMouseEnter={() => setZoomOutHover(true)}
          onMouseLeave={() => setZoomOutHover(false)}
        >
          缩小
        </button>
        <button
          onClick={togglePictureInPicture}
          disabled={!started}
          style={{ 
            ...(started ? frostedGlassBtnStyle : disabledBtnStyle), 
            marginRight: 8, 
            ...(pipBtnHover && started ? getBtnHoverStyle(false) : {}) 
          }}
          onMouseEnter={() => setPipBtnHover(true)}
          onMouseLeave={() => setPipBtnHover(false)}
        >
          {pipRef.current ? '退出画中画' : '画中画'}
        </button>
      </div>

      <button
        onClick={start}
        disabled={started}
        style={{
          ...(started ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(startBtnHover && !started ? getBtnHoverStyle(false) : {}),
          marginRight: 8,
        }}
        onMouseEnter={() => setStartBtnHover(true)}
        onMouseLeave={() => setStartBtnHover(false)}
      >
        启动
      </button>
      <button
        onClick={stop}
        disabled={!started}
        style={{
          ...(!started ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(stopBtnHover && started ? getBtnHoverStyle(false) : {}),
        }}
        onMouseEnter={() => setStopBtnHover(true)}
        onMouseLeave={() => setStopBtnHover(false)}
      >
        停止
      </button>

      {/* 视频区域 */}
      {renderVideo && (
        <div id="media" style={{ marginTop: 20 }}>
          <h2 style={{ color: 'white' }}>管家</h2>

          <audio ref={audioRef} autoPlay />
          
          {/* 原始视频（临时显示，帮助排查问题） */}
          <video
            ref={videoRef}
            style={{ 
              display: 'block', // 改为'block'强制显示原始视频
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 10,
              zIndex: 100,
              border: '2px solid red'
            }}
            autoPlay
            playsInline
          />
          
          {/* 处理后的视频（Canvas） */}
          <canvas
            ref={canvasRef}
            style={{ 
              width: zoomWidth, 
              height: (zoomWidth * initialHeight) / initialWidth,
              position: 'relative',
              zIndex: 10,
              border: '2px solid white',
              backgroundColor: 'rgba(0,0,0,0.1)'
            }}
          />
          
          {/* 无视频时的提示 */}
          {started && videoLoaded && !isCanvasRendering && (
            <div style={{ 
              position: 'absolute', 
              top: '50%', 
              left: '50%', 
              transform: 'translate(-50%, -50%)',
              color: 'white',
              fontSize: 16,
              backgroundColor: 'rgba(0,0,0,0.7)',
              padding: 20,
              borderRadius: 10,
              textAlign: 'center',
              maxWidth: 300
            }}>
              {canvasError || '视频加载中...'}
              {!canvasError && <div style={{ fontSize: 14, marginTop: 10 }}>
                请检查：<br/>
                1. 浏览器是否支持Canvas 2D<br/>
                2. 视频流是否正常（右上角小窗口）<br/>
                3. 浏览器控制台是否有错误
              </div>}
            </div>
          )}
          
          {/* Canvas初始化状态提示 */}
          {started && !canvasInitialized && (
            <div style={{ 
              position: 'absolute', 
              top: '50%', 
              left: '50%', 
              transform: 'translate(-50%, -50%)',
              color: 'yellow',
              fontSize: 16,
              backgroundColor: 'rgba(0,0,0,0.7)',
              padding: 10,
              borderRadius: 5
            }}>
              Canvas初始化中...
            </div>
          )}
        </div>
      )}

      {/* 聊天框 */}
      <div
        style={{
          position: 'absolute',
          left: chatPos.left,
          top: chatPos.top,
          width: 320,
          borderRadius: 6,
          padding: 12,
          color: 'white',
          userSelect: 'none',
          cursor: draggingRef.current ? 'grabbing' : 'grab',
          zIndex: 9999,
        }}
        onMouseDown={onDragStart}
        onMouseMove={onDragMove}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={onDragStart}
        onTouchMove={onDragMove}
        onTouchEnd={onDragEnd}
      >
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            style={{
              width: '100%',
              resize: 'vertical',
              padding: 8,
              borderRadius: 8,
              border: '1px solid rgba(255,255,255,0.3)',
              backgroundColor: 'rgba(255,255,255,0.1)',
              backdropFilter: 'blur(10px)',
              color: 'white',
              outline: 'none',
            }}
            placeholder="请输入文本..."
            onClick={(e) => e.stopPropagation()}
          />
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
            <button
              type="button"
              onClick={handleInterruptClick}
              style={{ ...frostedGlassBtnStyle, padding: '6px 16px', fontSize: 14 }}
              onMouseEnter={() => setInterruptBtnHover(true)}
              onMouseLeave={() => setInterruptBtnHover(false)}
            >
              打断说话
            </button>
            <label
              htmlFor="image-upload"
              style={{ ...frostedGlassBtnStyle, padding: '6px 12px', cursor: 'pointer' }}
            >
              上传图片
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleImageChange}
            />
            <button
              type="submit"
              style={{ ...frostedGlassBtnStyle, padding: '6px 16px' }}
            >
              发送
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
