import React, { useState, useRef, useEffect } from 'react';

// 示例菜单项
const menuItems = [
  { key: 'home', label: '主页' },
  { key: 'butler', label: '管家' },
  { key: 'car', label: '车' },
  { key: 'homeassistant', label: 'HA' },
  { key: 'ops', label: '运维' },
  { key: 'settings', label: '设置' },
];

export default function Settings() {
  const [selectedMenu, setSelectedMenu] = useState('home');
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // 点击页面其他地方关闭菜单
  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // 样式定义
  const panelContainerStyle = {
    padding: 12,
    borderRadius: 6,
    backgroundColor: 'rgba(255, 255, 255, 0.15)', // 白色半透明磨砂背景
    backdropFilter: 'blur(12px)',
    WebkitBackdropFilter: 'blur(12px)',
    color: '#222',
    marginBottom: 20,
    userSelect: 'none',
    width: 320,
  };

  const labelStyle = {
    display: 'block',
    marginBottom: 6,
    color: 'rgba(34,34,34,0.7)',
    fontWeight: '600',
  };

  const dropdownWrapperStyle = {
    position: 'relative',
    userSelect: 'none',
  };

  const dropdownHeaderStyle = {
    padding: '10px 14px',
    borderRadius: 6,
    backgroundColor: 'rgba(255, 255, 255, 0.25)',
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    border: '1px solid rgba(255,255,255,0.4)',
    color: '#222',
    fontWeight: '600',
    cursor: 'pointer',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  };

  const dropdownListStyle = {
    position: 'absolute',
    top: 'calc(100% + 6px)',
    left: 0,
    right: 0,
    maxHeight: 180,
    overflowY: 'auto',
    borderRadius: 8,
    backgroundColor: 'rgba(255, 255, 255, 0.15)',
    backdropFilter: 'blur(12px)',
    WebkitBackdropFilter: 'blur(12px)',
    boxShadow: '0 8px 24px rgba(0,0,0,0.12)',
    zIndex: 1000,
  };

  const dropdownItemStyle = (isSelected) => ({
    padding: '10px 14px',
    cursor: 'pointer',
    backgroundColor: isSelected ? '#00e5ff' : 'transparent',
    color: isSelected ? '#fff' : '#222',
    fontWeight: isSelected ? '700' : '500',
    transition: 'background-color 0.2s, color 0.2s',
  });

  const dropdownItemHoverStyle = {
    backgroundColor: 'rgba(0, 229, 255, 0.15)',
    color: '#00e5ff',
  };

  // 输入框及选择框样式
  const inputSelectStyle = {
    width: '100%',
    padding: 8,
    borderRadius: 4,
    border: 'none',
    fontSize: 16,
    backgroundColor: 'rgba(255, 255, 255, 0.3)', // 白色半透明
    color: '#222',
    outline: 'none',
    boxSizing: 'border-box',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: 'rgba(255, 255, 255, 0.7)', // 白色半透明
    color: '#222', // 深色文字
    fontWeight: 'bold',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
    userSelect: 'none',
    marginTop: 10,
  };

  const resetSingleBtnStyle = {
    padding: '4px 10px',
    borderRadius: 4,
    border: 'none',
    backgroundColor: 'rgba(0, 123, 255, 0.6)', // 浅蓝色半透明
    color: 'white',
    cursor: 'pointer',
    userSelect: 'none',
  };

  // 示例处理函数
  function handleMenuSelect(key) {
    setSelectedMenu(key);
    setMenuOpen(false);
  }

  // 预览示例，显示当前选择菜单
  function renderPreview() {
    const current = menuItems.find(i => i.key === selectedMenu);
    return <div style={{
      padding: 20,
      borderRadius: 8,
      backgroundColor: 'rgba(255,255,255,0.15)',
      backdropFilter: 'blur(10px)',
      WebkitBackdropFilter: 'blur(10px)',
      color: '#222',
      textAlign: 'center',
      fontWeight: '700',
      fontSize: 18,
    }}>{current ? current.label : '无'}</div>;
  }

  return (
    <div id="wallpaperSettingsPanel" style={panelContainerStyle}>
      {/* 自定义菜单选择 */}
      <label htmlFor="customMenuSelect" style={labelStyle}>选择菜单项</label>
      <div style={dropdownWrapperStyle} ref={menuRef}>
        <div
          id="customMenuSelect"
          role="button"
          tabIndex={0}
          onClick={() => setMenuOpen(!menuOpen)}
          onKeyDown={e => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              setMenuOpen(!menuOpen);
            }
          }}
          style={dropdownHeaderStyle}
          aria-haspopup="listbox"
          aria-expanded={menuOpen}
          aria-labelledby="customMenuSelect"
        >
          {menuItems.find(item => item.key === selectedMenu)?.label || '请选择'}
          <svg
            style={{ width: 18, height: 18, transform: menuOpen ? 'rotate(180deg)' : undefined, transition: 'transform 0.3s' }}
            viewBox="0 0 24 24"
            fill="none"
            stroke="#222"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <polyline points="6 9 12 15 18 9" />
          </svg>
        </div>
        {menuOpen && (
          <ul
            role="listbox"
            tabIndex={-1}
            aria-activedescendant={selectedMenu}
            style={dropdownListStyle}
          >
            {menuItems.map(({ key, label }) => {
              const isSelected = key === selectedMenu;
              return (
                <li
                  id={key}
                  key={key}
                  role="option"
                  aria-selected={isSelected}
                  onClick={() => handleMenuSelect(key)}
                  onKeyDown={e => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      handleMenuSelect(key);
                    }
                  }}
                  style={dropdownItemStyle(isSelected)}
                  onMouseEnter={e => {
                    if (!isSelected) {
                      e.currentTarget.style.backgroundColor = dropdownItemHoverStyle.backgroundColor;
                      e.currentTarget.style.color = dropdownItemHoverStyle.color;
                    }
                  }}
                  onMouseLeave={e => {
                    if (!isSelected) {
                      e.currentTarget.style.backgroundColor = 'transparent';
                      e.currentTarget.style.color = '#222';
                    }
                  }}
                  tabIndex={0}
                >
                  {label}
                </li>
              );
            })}
          </ul>
        )}
      </div>

      {/* 示例输入框 */}
      <div style={{ marginTop: 20 }}>
        <label htmlFor="exampleInput" style={labelStyle}>示例输入框</label>
        <input
          id="exampleInput"
          type="text"
          placeholder="请输入内容"
          style={inputSelectStyle}
        />
      </div>

      {/* 示例按钮 */}
      <button type="button" onClick={() => alert('保存设置')} style={buttonStyle}>
        保存设置
      </button>

      {/* 示例重置按钮 */}
      <button
        type="button"
        onClick={() => alert('重置单项')}
        style={{ ...resetSingleBtnStyle, marginTop: 10 }}
      >
        重置单项
      </button>

      {/* 预览区 */}
      <div style={{ marginTop: 30 }}>
        <h4 style={{ color: '#222', marginBottom: 12 }}>预览</h4>
        {renderPreview()}
      </div>
    </div>
  );
}
