import React, { useState, useRef, useEffect } from 'react';

// 示例管家数据，全部是视频，id唯一
const butlers = [
  {
    id: 'butler1',
    name: '管家禅师',
    type: 'video',
    src: '/video/豆包禅师.mp4', // 请确保视频文件放在 public/video/目录内
  },
  {
    id: 'butler2',
    name: '管家小粒',
    type: 'video',
    src: '/video/小粒.mp4',
  },
  {
    id: 'butler3',
    name: '管家小贾',
    type: 'video',
    src: '/video/小贾.mp4',
  },
  {
    id: 'butler4',
    name: '管家星期天',
    type: 'video',
    src: '/video/星期天.mp4',
  },
];

export default function ButlerShowcase() {
  // 当前激活管家ID
  const [activeButlerId, setActiveButlerId] = useState(butlers[0].id);
  // 鼠标悬停管家ID
  const [hoveredId, setHoveredId] = useState(null);
  // 是否显示确认对话框
  const [showConfirm, setShowConfirm] = useState(false);
  // 待确认切换管家ID
  const confirmButlerId = useRef(null);
  // 定时器引用
  const hoverTimer = useRef(null);

  // 鼠标移入，启动3秒定时器弹出确认
  function handleMouseEnter(id) {
    setHoveredId(id);
    if (hoverTimer.current) {
      clearTimeout(hoverTimer.current);
      hoverTimer.current = null;
    }
    hoverTimer.current = setTimeout(() => {
      confirmButlerId.current = id;
      setShowConfirm(true);
    }, 3000);
  }

  // 鼠标离开，取消定时器和隐藏确认框
  function handleMouseLeave() {
    setHoveredId(null);
    if (hoverTimer.current) {
      clearTimeout(hoverTimer.current);
      hoverTimer.current = null;
    }
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 确认切换管家
  function confirmSwitch() {
    if (confirmButlerId.current && confirmButlerId.current !== activeButlerId) {
      setActiveButlerId(confirmButlerId.current);
    }
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 取消切换
  function cancelSwitch() {
    setShowConfirm(false);
    confirmButlerId.current = null;
    setHoveredId(null);
  }

  // 清理定时器，防止内存泄漏
  useEffect(() => {
    return () => {
      if (hoverTimer.current) {
        clearTimeout(hoverTimer.current);
        hoverTimer.current = null;
      }
    };
  }, []);

  return (
    <>
      <div
        className="steward-gallery"
        style={{
          fontFamily: 'Arial, sans-serif',
          backgroundColor: '#1a1a1a',
          padding: 40,
          minHeight: '100vh',
          userSelect: 'none',
        }}
      >
        <div
          className="display-case"
          style={{
            position: 'relative',
            maxWidth: 900,
            margin: '0 auto',
            borderRadius: 8,
            overflow: 'hidden',
          }}
        >
          {/* 展示柜装饰 */}
          <div
            className="case-top"
            style={{
              height: 20,
              background:
                'linear-gradient(90deg, #1e3c72, #2a5298)',
              boxShadow: '0 0 10px rgba(0, 150, 255, 0.7)',
            }}
          />
          <div
            className="case-bottom"
            style={{
              height: 20,
              background:
                'linear-gradient(90deg, #1e3c72, #2a5298)',
              boxShadow: '0 0 10px rgba(0, 150, 255, 0.7)',
            }}
          />

          <div
            className="stewards-container"
            style={{
              display: 'flex',
              justifyContent: 'space-around',
              backgroundColor: 'rgba(0,0,0,0.7)',
              padding: '30px 0',
            }}
          >
            {butlers.map(({ id, name, type, src }) => {
              const isActive = id === activeButlerId;
              const isHovered = id === hoveredId;

              // 主题颜色和背景色设计 (你可以根据id或名称自定义)
              let themeBg = '#333'; // 默认dark
              if (id === 'butler1') themeBg = '#333'; // dark
              else if (id === 'butler2' || id === 'butler3' || id === 'butler4')
                themeBg = '#d32f2f'; // red

              return (
                <div
                  key={id}
                  className={`steward ${isActive ? 'active' : ''} ${
                    isHovered ? 'hovered' : ''
                  }`}
                  onMouseEnter={() => handleMouseEnter(id)}
                  onMouseLeave={handleMouseLeave}
                  style={{
                    position: 'relative',
                    width: 120,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease',
                    transform: isHovered ? 'translateY(-10px)' : 'translateY(0)',
                    userSelect: 'none',
                  }}
                >
                  {/* 管家形象背景（模拟展示柜灯光色调） */}
                  <div
                    style={{
                      content: '""',
                      display: 'block',
                      width: 80,
                      height: 120,
                      backgroundColor: themeBg,
                      borderRadius: 12,
                      marginBottom: 10,
                      filter: 'drop-shadow(0 0 6px rgba(0,255,255,0.7))',
                      position: 'relative',
                      overflow: 'hidden',
                    }}
                  >
                    {type === 'image' ? (
                      <img
                        src={src}
                        alt={name}
                        draggable={false}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                        }}
                      />
                    ) : (
                      <video
                        src={src}
                        autoPlay
                        loop
                        muted
                        playsInline
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                          pointerEvents: 'none',
                        }}
                      />
                    )}
                  </div>

                  {/* 底座 */}
                  <div
                    className="base"
                    style={{
                      width: 60,
                      height: 10,
                      backgroundColor: '#424242',
                      borderRadius: 2,
                      marginBottom: 15,
                    }}
                  />

                  {/* 指示灯 */}
                  <div
                    className="indicator-light"
                    style={{
                      width: 12,
                      height: 12,
                      borderRadius: '50%',
                      backgroundColor: isActive
                        ? '#00e5ff'
                        : isHovered
                        ? '#4fc3f7'
                        : '#555',
                      boxShadow: isActive
                        ? '0 0 15px #00e5ff'
                        : isHovered
                        ? '0 0 8px #4fc3f7'
                        : 'none',
                      marginBottom: 8,
                      transition: 'background-color 0.3s, box-shadow 0.3s',
                    }}
                  />

                  {/* 名字 */}
                  <div
                    style={{
                      color: isActive ? '#0ff' : '#ccc',
                      fontWeight: 'bold',
                      textAlign: 'center',
                      userSelect: 'none',
                      textShadow: isActive ? '0 0 6px #0ff' : 'none',
                      whiteSpace: 'nowrap',
                    }}
                  >
                    {name}
                  </div>

                  {/* 确认对话框 */}
                  {showConfirm && confirmButlerId.current === id && (
                    <div
                      className="dialog"
                      style={{
                        position: 'absolute',
                        bottom: '110%',
                        left: '50%',
                        transform: 'translateX(-50%)',
                        background: 'rgba(30,30,30,0.85)',
                        color: 'white',
                        padding: 15,
                        borderRadius: 8,
                        width: 160,
                        textAlign: 'center',
                        backdropFilter: 'blur(5px)',
                        boxShadow: '0 0 20px rgba(0,150,255,0.5)',
                        zIndex: 10,
                        animation: 'fadeIn 0.3s',
                        userSelect: 'none',
                      }}
                      onMouseEnter={() => {
                        if (hoverTimer.current) {
                          clearTimeout(hoverTimer.current);
                          hoverTimer.current = null;
                        }
                      }}
                      onMouseLeave={handleMouseLeave}
                    >
                      <p style={{ margin: '0 0 12px' }}>
                        是否切换到管家 <strong>{name}</strong>？
                      </p>
                      <div
                        style={{
                          display: 'flex',
                          justifyContent: 'center',
                          gap: 10,
                        }}
                      >
                        <button
                          onClick={confirmSwitch}
                          style={{
                            background: '#0288d1',
                            color: 'white',
                            border: 'none',
                            padding: '5px 12px',
                            borderRadius: 4,
                            cursor: 'pointer',
                            transition: 'background 0.3s',
                            userSelect: 'none',
                          }}
                          onMouseEnter={(e) => e.stopPropagation()}
                        >
                          确定
                        </button>
                        <button
                          onClick={cancelSwitch}
                          style={{
                            background: '#0288d1',
                            color: 'white',
                            border: 'none',
                            padding: '5px 12px',
                            borderRadius: 4,
                            cursor: 'pointer',
                            transition: 'background 0.3s',
                            userSelect: 'none',
                          }}
                          onMouseEnter={(e) => e.stopPropagation()}
                        >
                          取消
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* 动画样式 */}
      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateX(-50%) translateY(10px);}
          to { opacity: 1; transform: translateX(-50%) translateY(0);}
        }
      `}</style>
    </>
  );
}
