import React, { useState, useRef, useEffect } from 'react';

// 示例管家数据，全部是视频，id唯一
const butlers = [
  {
    id: 'butler1',
    name: '管家禅师',
    type: 'video',
    src: '/video/豆包禅师.mp4', // 请确保视频文件放在 public/video/目录内
  },
  {
    id: 'butler2',
    name: '管家小粒',
    type: 'video',
    src: '/video/小粒.mp4',
  },
  {
    id: 'butler3',
    name: '管家小贾',
    type: 'video',
    src: '/video/小贾.mp4',
  },
  {
    id: 'butler4',
    name: '管家星期天',
    type: 'video',
    src: '/video/星期天.mp4',
  },
];

export default function ButlerShowcase() {
  // 当前激活管家ID
  const [activeButlerId, setActiveButlerId] = useState(butlers[0].id);
  // 鼠标悬停管家ID
  const [hoveredId, setHoveredId] = useState(null);
  // 是否显示确认对话框
  const [showConfirm, setShowConfirm] = useState(false);
  // 待确认切换管家ID
  const confirmButlerId = useRef(null);
  // 定时器引用
  const hoverTimer = useRef(null);
  // 对话框是否在管家上方显示时，鼠标是否在对话框内
  const dialogHovering = useRef(false);

  // 鼠标移入管家项，启动3秒定时器弹出确认
  function handleMouseEnter(id) {
    setHoveredId(id);
    if (hoverTimer.current) {
      clearTimeout(hoverTimer.current);
      hoverTimer.current = null;
    }
    hoverTimer.current = setTimeout(() => {
      confirmButlerId.current = id;
      setShowConfirm(true);
    }, 3000);
  }

  // 鼠标离开管家项时，延迟判断是否关闭确认框
  function handleMouseLeave() {
    setHoveredId(null);
    // 这里延迟一小段时间，防止鼠标移入对话框立刻关闭
    setTimeout(() => {
      if (!dialogHovering.current) {
        if (hoverTimer.current) {
          clearTimeout(hoverTimer.current);
          hoverTimer.current = null;
        }
        setShowConfirm(false);
        confirmButlerId.current = null;
      }
    }, 100); // 100ms延迟，可以根据体验调整
  }

  // 鼠标进入对话框时设置标识，防止关闭
  function handleDialogMouseEnter() {
    dialogHovering.current = true;
    if (hoverTimer.current) {
      clearTimeout(hoverTimer.current);
      hoverTimer.current = null;
    }
  }
  // 鼠标离开对话框时，关闭确认框并重置变量
  function handleDialogMouseLeave() {
    dialogHovering.current = false;
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 确认切换管家
  function confirmSwitch() {
    if (confirmButlerId.current && confirmButlerId.current !== activeButlerId) {
      setActiveButlerId(confirmButlerId.current);
    }
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 取消切换
  function cancelSwitch() {
    setShowConfirm(false);
    confirmButlerId.current = null;
    setHoveredId(null);
  }

  // 清理定时器，防止内存泄漏
  useEffect(() => {
    return () => {
      if (hoverTimer.current) {
        clearTimeout(hoverTimer.current);
        hoverTimer.current = null;
      }
    };
  }, []);

  return (
    <>
      <div
        className="steward-gallery"
        style={{
          fontFamily: 'Arial, sans-serif',
          backgroundColor: 'transparent', // 透明背景，去除深灰色背景
          padding: 40,
          minHeight: '100vh',
          userSelect: 'none',
        }}
      >
        <div
          className="display-case"
          style={{
            position: 'relative',
            maxWidth: 1000,
            margin: '0 auto',
            borderRadius: 8,
            overflow: 'visible', // 允许溢出显示阴影和对话框
            backgroundColor: 'transparent', // 去除背景色
            boxShadow: 'none', // 去除阴影
          }}
        >
          {/* 展示柜装饰，顶部底部去除，改为透明背景 */}
          <div
            className="case-top"
            style={{
              height: 20,
              backgroundColor: 'transparent',
              boxShadow: 'none',
            }}
          />
          <div
            className="case-bottom"
            style={{
              height: 20,
              backgroundColor: 'transparent',
              boxShadow: 'none',
            }}
          />

          <div
            className="stewards-container"
            style={{
              display: 'flex',
              justifyContent: 'center', // 居中排列
              backgroundColor: 'transparent', // 透明背景
              padding: '30px 0',
              gap: 0, // 管家紧挨着，无间隔
            }}
          >
            {butlers.map(({ id, name, type, src }, index) => {
              const isActive = id === activeButlerId;
              const isHovered = id === hoveredId;
              const isConfirming = showConfirm && confirmButlerId.current === id;

              // 主题颜色和背景色设计 (你可以根据id或名称自定义)
              let themeBg = '#333'; // 默认dark
              if (id === 'butler1') themeBg = '#333'; // dark
              else if (id === 'butler2' || id === 'butler3' || id === 'butler4')
                themeBg = '#d32f2f'; // red

              return (
                <div
                  key={id}
                  className={`steward ${isActive ? 'active' : ''} ${
                    isHovered ? 'hovered' : ''
                  }`}
                  onMouseEnter={() => handleMouseEnter(id)}
                  onMouseLeave={handleMouseLeave}
                  style={{
                    position: 'relative',
                    width: 240,
                    height: 480,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease',
                    transform: isHovered ? 'translateY(-10px)' : 'translateY(0)',
                    userSelect: 'none',
                    marginRight: index !== butlers.length - 1 ? 0 : 0,
                    zIndex: isConfirming ? 100 : 1, // 对话框显示时提升z-index，避免被遮挡
                  }}
                >
                  {/* 管家形象背景（模拟展示柜灯光色调） */}
                  <div
                    style={{
                      width: 160,
                      height: 320,
                      backgroundColor: themeBg,
                      borderRadius: 12,
                      marginBottom: 20,
                      filter: 'drop-shadow(0 0 12px rgba(0,255,255,0.7))',
                      position: 'relative',
                      overflow: 'hidden',
                      zIndex: 1,
                    }}
                  >
                    {type === 'image' ? (
                      <img
                        src={src}
                        alt={name}
                        draggable={false}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                        }}
                      />
                    ) : (
                      <video
                        src={src}
                        autoPlay
                        loop
                        muted
                        playsInline
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          borderRadius: 12,
                          userSelect: 'none',
                          pointerEvents: 'none',
                        }}
                      />
                    )}
                  </div>

                  {/* 确认对话框，位置调整到管家形象上方 */}
                  {isConfirming && (
                    <div
                      className="dialog"
                      style={{
                        position: 'absolute',
                        bottom: 480 + 16, // 高度加点空隙，确保在管家顶部上方
                        left: '50%',
                        transform: 'translateX(-50%)',
                        background: 'rgba(30,30,30,0.95)', // 颜色稍微加深，防止透明导致难见
                        color: 'white',
                        padding: 15,
                        borderRadius: 8,
                        width: 200,
                        textAlign: 'center',
                        backdropFilter: 'blur(5px)',
                        boxShadow: '0 0 25px rgba(0,150,255,0.8)', // 加强发光效果
                        zIndex: 101,
                        animation: 'fadeIn 0.3s',
                        userSelect: 'none',
                        pointerEvents: 'auto',
                      }}
                      onMouseEnter={handleDialogMouseEnter}
                      onMouseLeave={handleDialogMouseLeave}
                    >
                      <p style={{ margin: '0 0 12px', fontSize: 16 }}>
                        是否切换到管家 <strong>{name}</strong>？
                      </p>
                      <div
                        style={{
                          display: 'flex',
                          justifyContent: 'center',
                          gap: 12,
                        }}
                      >
                        <button
                          onClick={confirmSwitch}
                          style={{
                            background: '#0288d1',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            borderRadius: 6,
                            cursor: 'pointer',
                            transition: 'background 0.3s',
                            userSelect: 'none',
                          }}
                          onMouseEnter={(e) => e.stopPropagation()}
                        >
                          确认
                        </button>
                        <button
                          onClick={cancelSwitch}
                          style={{
                            background: '#0288d1',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            borderRadius: 6,
                            cursor: 'pointer',
                            transition: 'background 0.3s',
                            userSelect: 'none',
                          }}
                          onMouseEnter={(e) => e.stopPropagation()}
                        >
                          取消
                        </button>
                      </div>
                    </div>
                  )}

                  {/* 底座 */}
                  <div
                    className="base"
                    style={{
                      width: 120,
                      height: 20,
                      backgroundColor: '#424242',
                      borderRadius: 4,
                      marginTop: 12,
                      zIndex: 1,
                    }}
                  />

                  {/* 指示灯 */}
                  <div
                    className="indicator-light"
                    style={{
                      width: 20,
                      height: 20,
                      borderRadius: '50%',
                      backgroundColor: isActive
                        ? '#00e5ff'
                        : isHovered
                        ? '#4fc3f7'
                        : '#555',
                      boxShadow: isActive
                        ? '0 0 20px #00e5ff'
                        : isHovered
                        ? '0 0 12px #4fc3f7'
                        : 'none',
                      marginTop: 12,
                      transition: 'background-color 0.3s, box-shadow 0.3s',
                      zIndex: 1,
                    }}
                  />

                  {/* 名字 */}
                  <div
                    style={{
                      color: isActive ? '#0ff' : '#ccc',
                      fontWeight: 'bold',
                      textAlign: 'center',
                      userSelect: 'none',
                      textShadow: isActive ? '0 0 8px #0ff' : 'none',
                      whiteSpace: 'nowrap',
                      fontSize: 18,
                      marginTop: 12,
                      zIndex: 1,
                    }}
                  >
                    {name}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* 动画样式 */}
      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateX(-50%) translateY(10px);}
          to { opacity: 1; transform: translateX(-50%) translateY(0);}
        }
      `}</style>
    </>
  );
}
