// App.jsx（完整修复版）
import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import ButlerShowcase from './components/ButlerShowcase';
import { useButler, ButlerProvider } from './components/ButlersContext';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

import AudioTranscriber from './components/AudioTranscriber'; // 麦克风组件
import CameraControl from './components/CameraControl';
import WebRTCPlayer from './components/OldWebRTC';

import FloatingContainer from './components/FloatingContainer';
import SseListener from './components/SseListener';

import Clock from './components/Clock';

import { RecorderProvider } from './contexts/RecorderProvider';
import TTSPanel from './components/TTSPanel';
import WallpaperManager from './components/WallpaperManager';

const ItemTypes = {
  PANEL: 'panel',
};

const panelTitles = {
  audio: { cn: '语音识别', en: 'ASR' },
  t2audio: { cn: '语音合成', en: 'TTS' },
  camera: { cn: '摄像头', en: 'CAMERA' },
  webrtc: { cn: '当前管家', en: 'AI Butler' },
  microphone: { cn: '麦克风', en: 'MIC' }, // 麦克风面板标题（关键修复）
};

// 修复：加载面板时强制添加麦克风面板
function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      const panels = JSON.parse(saved);
      // 检查是否有麦克风面板，没有则添加（关键修复）
      if (!panels.some(p => p.id === 'microphone')) {
        panels.push({ 
          id: 'microphone', 
          left: 20,  // 默认位置
          top: 650, 
          width: 320, 
          height: 180, 
          zIndex: 2 
        });
      }
      console.log('[Init] 从localStorage加载panel配置(含麦克风):', panels);
      return panels;
    }
  } catch (e) {
    console.warn('读取面板位置失败', e);
  }
  // 默认面板配置（含麦克风，关键修复）
  return [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 't2audio', left: 360, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 400, width: 320, height: 240, zIndex: 1 },
    { id: 'microphone', left: 20, top: 650, width: 320, height: 180, zIndex: 2 }, // 麦克风默认配置
  ];
}

function HomeAssistantPanel() {
  const HA_URL = 'http://192.168.168.10:8123';
  const [loading, setLoading] = useState(true);
  const [showOverlay, setShowOverlay] = useState(true);
  const iframeRef = useRef(null);
  
  const glassStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.1)',
    backdropFilter: 'blur(8px)',
    WebkitBackdropFilter: 'blur(10px)',
    pointerEvents: 'none',
    zIndex: 1,
  };

  const containerStyle = {
    width: '100%',
    height: 'calc(100vh - 180px)',
    position: 'relative',
    overflow: 'hidden',
    borderRadius: 4,
    backgroundColor: 'rgba(0, 0, 0, 0.05)',
  };
  
  const iframeStyle = {
    width: '100%',
    height: '100%',
    border: 'none',
    opacity: 0.85,
    backgroundColor: 'transparent',
    transition: 'opacity 0.5s ease',
  };

  const handleIframeLoad = () => {
    setLoading(false);
    setTimeout(() => setShowOverlay(false), 1000);
  };

  return (
    <div style={containerStyle}>
      {loading && (
        <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 10 }}>
          <div className="spinner" style={{ width: 50, height: 50, border: '3px solid rgba(0,229,255,0.3)', borderRadius: '50%', borderTopColor: '#00e5ff', animation: 'spin 1s linear infinite' }} />
          <p style={{ marginLeft: 15, color: 'white' }}>加载 Home Assistant...</p>
        </div>
      )}
      {showOverlay && <div style={glassStyle} />}
      <iframe
        ref={iframeRef}
        src={HA_URL}
        title="Home Assistant"
        style={iframeStyle}
        onLoad={handleIframeLoad}
        sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
      />
    </div>
  );
}

function DraggableResizablePanel({
  id,
  left,
  top,
  width = 320,
  height = 200,
  zIndex,
  movePanel,
  bringToFront,
  resizePanel,
  children,
}) {
  const dragDropRef = React.useRef(null);

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top, width, height },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), [id, left, top, width, height]);

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!dragDropRef.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        movePanel(item.id, newLeft, newTop, width, height);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  }), [left, top, width, height]);

  drag(drop(dragDropRef));

  const onResize = (event, { size }) => {
    resizePanel(id, size.width, size.height);
  };

  const title = panelTitles[id] || { cn: id.toUpperCase(), en: id.toUpperCase() };

  return (
    <div
      className="panel"
      ref={dragDropRef}
      onMouseDown={() => bringToFront(id)}
      style={{
        position: 'absolute',
        left,
        top,
        zIndex,
        userSelect: 'none',
        width,
        height,
        opacity: isDragging ? 0.7 : 1,
        boxSizing: 'border-box',
      }}
    >
      <ResizableBox
        width={width}
        height={height}
        minConstraints={[150, 100]}
        maxConstraints={[800, 600]}
        onResize={onResize}
        resizeHandles={['se']}
        handle={
          <span
            className="custom-handle custom-handle-se"
            style={{
              position: 'absolute',
              width: 20,
              height: 20,
              right: 0,
              bottom: 0,
              cursor: 'se-resize',
              backgroundColor: 'rgba(0,0,0,0.2)',
              borderRadius: '0 0 4px 0',
              zIndex: 10,
            }}
          />
        }
      >
        <div
          style={{
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(255, 255, 255, 0)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            userSelect: 'none',
          }}
        >
          <div
            style={{
              height: 30,
              backgroundColor: 'transparent',
              color: 'white',
              cursor: 'grab',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              borderTopLeftRadius: 4,
              borderTopRightRadius: 4,
              fontWeight: 'bold',
              flexShrink: 0,
              lineHeight: 1,
              paddingTop: 2,
              paddingBottom: 2,
            }}
          >
            <div style={{ fontSize: (id === 'webrtc' || id === 'audio' || id === 't2audio' || id === 'microphone') ? 21 : 12, fontWeight: 'bold' }}>
              {title.cn}
            </div>
            <div style={{ fontSize: 13, fontWeight: 'normal', opacity: 0.7 }}>
              {title.en}
            </div>
          </div>
          <div style={{ flex: 1, padding: 12, overflow: 'auto', userSelect: 'auto', position: 'relative' }}>
            {children}
          </div>
        </div>
      </ResizableBox>
    </div>
  );
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);
  const [panels, setPanels] = useState(loadPanels()); // 加载含麦克风的面板配置
  const maxZIndex = useRef(1);
  const [selectedMenu, setSelectedMenu] = useState('home');
  const [showGlassTransition, setShowGlassTransition] = useState(false);
  const pendingMenuRef = useRef(null);
  const transitionTimeoutRef = useRef(null);
  const [webrtcConnected, setWebrtcConnected] = useState(false);

  // 日志调试（关键：检查面板是否包含麦克风）
  useEffect(() => {
    console.log('[Debug] 当前所有面板ID:', panels.map(p => p.id));
    console.log('[State] Panels updated:', panels);
  }, [panels]);

  function handleSseMessage(data) {
    console.log('收到 SSE 消息:', data);
    setSseMessages(prev => [...prev, data]);
  }

  function movePanel(id, left, top, width, height) {
    const maxLeft = window.innerWidth - width;
    const maxTop = window.innerHeight - height;
    const boundedLeft = Math.min(Math.max(0, left), maxLeft);
    const boundedTop = Math.min(Math.max(0, top), maxTop);

    setPanels(prev =>
      prev.map(p => p.id === id ? { ...p, left: boundedLeft, top: boundedTop } : p)
    );
  }

  function resizePanel(id, width, height) {
    setPanels(prev =>
      prev.map(p => p.id === id ? { ...p, width, height } : p)
    );
  }

  function bringToFront(id) {
    maxZIndex.current += 1;
    setPanels(prev =>
      prev.map(p => p.id === id ? { ...p, zIndex: maxZIndex.current } : p)
    );
  }

  function DebugActiveButler() {
    const { activeButlerId } = useButler();
    return (
      <div style={{ color: 'white', marginBottom: 12, fontWeight: 'bold' }}>
        当前激活管家ID: {activeButlerId}
      </div>
    );
  }

  // 保存面板配置（含麦克风）
  useEffect(() => {
    localStorage.setItem('panels', JSON.stringify(panels));
    console.log('[Save] 面板配置已保存（含麦克风）:', panels.map(p => p.id));
  }, [panels]);

  const handleMenuClick = (menuKey) => {
    if (showGlassTransition) return;
    pendingMenuRef.current = menuKey;
    setShowGlassTransition(true);
    if (transitionTimeoutRef.current) clearTimeout(transitionTimeoutRef.current);
    transitionTimeoutRef.current = setTimeout(() => {
      setSelectedMenu(pendingMenuRef.current);
      pendingMenuRef.current = null;
      setShowGlassTransition(false);
    }, 400);
  };

  const handleWebrtcConnect = () => setWebrtcConnected(true);
  const handleWebrtcDisconnect = () => setWebrtcConnected(false);

  const defaultPanels = [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 't2audio', left: 360, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 400, width: 320, height: 240, zIndex: 1 },
    { id: 'microphone', left: 20, top: 650, width: 320, height: 180, zIndex: 2 }, // 麦克风默认配置
  ];

  const resetAllPanels = () => {
    localStorage.removeItem('panels');
    setPanels(defaultPanels);
  };

  const resetSinglePanel = (id) => {
    setPanels(prev =>
      prev.map(p => {
        if (p.id === id) {
          const defaultPanel = defaultPanels.find(dp => dp.id === id);
          return defaultPanel || p;
        }
        return p;
      })
    );
  };

  const renderContent = () => {
    switch (selectedMenu) {
      case 'home':
        return (
          <div style={{ padding: 20 }}>
            <h2>主页</h2>
            <DebugActiveButler />
          </div>
        );
      case 'butler':
        return (
          <div style={{ padding: 20 }}>
            <h2>管家页面</h2>
            <DebugActiveButler />
            <ButlerShowcase />
          </div>
        );
      case 'car':
        return (
          <div style={{ padding: 20 }}>
            <h2>车机控制</h2>
            <p>这里是车机控制页面</p>
          </div>
        );
      case 'homeassistant':
        return (
          <div style={{ padding: 20, height: 'calc(100vh - 120px)' }}>
            <h2>Home Assistant 控制面板</h2>
            <HomeAssistantPanel />
          </div>
        );
      case 'ops':
        return (
          <div style={{ padding: 20 }}>
            <h2>运维页面</h2>
            <p>这里是运维页面的内容</p>
            <div style={{ marginTop: 20, padding: 10, border: '1px solid rgba(136,136,136,0.3)', borderRadius: 4, height: 300, overflowY: 'auto', backgroundColor: 'rgba(255,255,255,0.05)' }}>
              <h4>日志模块 (SSE 消息)</h4>
              {sseMessages.length === 0 ? (
                <p>等待接收消息...</p>
              ) : (
                <ul style={{ paddingLeft: 20, margin: 0 }}>
                  {sseMessages.map((msg, idx) => (
                    <li key={idx} style={{ wordBreak: 'break-word' }}>{msg}</li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: 20 }}>
            <h2>设置页面</h2>
            <button
              onClick={resetAllPanels}
              style={{ marginBottom: 20, padding: '8px 16px', backgroundColor: '#00e5ff', color: 'white', border: 'none', borderRadius: 4, cursor: 'pointer' }}
            >
              重置所有面板到默认
            </button>
            <div>
              <h3>重置单个面板位置</h3>
              {panels.map(({ id, left, top, width, height }) => (
                <div key={id} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 10, border: '1px solid rgba(136,136,136,0.3)', borderRadius: 4, padding: 10, maxWidth: 400 }}>
                  <div>
                    <strong>{id}</strong>
                    <div style={{ fontSize: 12, color: '#555' }}>
                      当前：{left},{top}，大小：{width}x{height}
                    </div>
                  </div>
                  <button
                    onClick={() => resetSinglePanel(id)}
                    style={{ padding: '4px 10px', borderRadius: 4, border: 'none', backgroundColor: '#28a745', color: 'white', cursor: 'pointer' }}
                  >
                    重置
                  </button>
                </div>
              ))}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  const frostedGlassBtnStyle = {
    width: 60,
    height: 60,
    borderRadius: '50%',
    border: '2px solid #00e5ff',
    backgroundColor: 'rgba(0,229,255,0.15)',
    color: '#00e5ff',
    fontSize: 16,
    fontWeight: 'bold',
    cursor: showGlassTransition ? 'wait' : 'pointer',
    outline: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    userSelect: 'none',
    transition: 'background-color 0.3s, color 0.3s, box-shadow 0.3s',
    backdropFilter: 'blur(10px)',
    boxShadow: '0 4px 30px rgba(0,229,255,0.4)',
  };

  const frostedGlassBtnSelectedStyle = {
    ...frostedGlassBtnStyle,
    backgroundColor: 'rgba(0,229,255,0.35)',
    color: 'white',
    boxShadow: '0 6px 40px rgba(0,229,255,0.7)',
  };

  return (
    <ButlerProvider>
      <RecorderProvider>
        <WallpaperManager selectedMenu={selectedMenu} showGlassTransition={showGlassTransition} />
        <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
          <Clock />
          <h1 style={{ color: 'white' }}>AI-Sphere-Butler 全能管家</h1>
          <SseListener onMessage={handleSseMessage} />

          <div style={{ display: 'flex', gap: 20, marginTop: 20, marginBottom: 20 }}>
            {[
              { key: 'home', label: '主页' },
              { key: 'butler', label: '管家' },
              { key: 'car', label: '车' },
              { key: 'homeassistant', label: 'HA' },
              { key: 'ops', label: '运维' },
              { key: 'settings', label: '设置' },
            ].map(({ key, label }) => (
              <button
                key={key}
                onClick={() => handleMenuClick(key)}
                style={selectedMenu === key ? frostedGlassBtnSelectedStyle : frostedGlassBtnStyle}
                aria-label={label}
              >
                {label}
              </button>
            ))}
          </div>

          {showGlassTransition && (
            <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(0,0,0,0.5)', backdropFilter: 'blur(15px)', zIndex: 50, transition: 'opacity 0.4s ease' }} />
          )}

          {renderContent()}

          {/* 保持WebRTC连接 */}
          {webrtcConnected && selectedMenu !== 'home' && (
            <WebRTCPlayer renderVideo={false} keepConnection={true} onStart={handleWebrtcConnect} onStop={handleWebrtcDisconnect} />
          )}

          {/* 渲染全局面板（含麦克风） */}
          {panels.map(({ id, left, top, width, height, zIndex }) => {
            console.log('[Render] 渲染全局面板:', id, `@ (${left},${top}), 大小: ${width}x${height}, zIndex: ${zIndex}`);
            
            // 关键：添加麦克风面板渲染逻辑
            switch (id) {
              case 'audio':
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <AudioTranscriber />
                  </DraggableResizablePanel>
                );
              case 't2audio':
                return (
                  <DraggableResizablePanel key={id} id={id} left={left} top={top} width={width} height={height} zIndex={zIndex} movePanel={movePanel} bringToFront={bringToFront} resizePanel={resizePanel}>
                    <TTSPanel />
                  </DraggableResizablePanel>
                );
              case 'webrtc':
                return (
                  <DraggableResizablePanel key={id} id={id} left={left} top={top} width={width} height={height} zIndex={zIndex} movePanel={movePanel} bringToFront={bringToFront} resizePanel={resizePanel}>
                    <WebRTCPlayer renderVideo={true} keepConnection={true} onStart={handleWebrtcConnect} onStop={handleWebrtcDisconnect} />
                  </DraggableResizablePanel>
                );
              case 'camera':
                return (
                  <DraggableResizablePanel key={id} id={id} left={left} top={top} width={width} height={height} zIndex={zIndex} movePanel={movePanel} bringToFront={bringToFront} resizePanel={resizePanel}>
                    <CameraControl />
                  </DraggableResizablePanel>
                );
              case 'microphone': // 关键：渲染麦克风面板
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <AudioTranscriber /> {/* 关联麦克风组件 */}
                  </DraggableResizablePanel>
                );
              default:
                return null;
            }
          })}
        </div>
      </RecorderProvider>
    </ButlerProvider>
  );
}
