import React from 'react';
import styles from './FloatingContainer.module.css';
import { useDraggable } from '../hooks/useDraggable';

export default function FloatingContainer({ children }) {
  const dragRef = useDraggable();

  return (
    <div
      ref={dragRef}
      style={{
        position: 'absolute',
        top: 100,
        left: 100,
        padding: 12,
        backgroundColor: '#eee',
        border: '1px solid #ccc',
        borderRadius: 8,
        cursor: 'move',
        userSelect: 'none',
        zIndex: 1000,
      }}
    >
      {children}
    </div>
  );
}
