// src/components/OldWebRTC.jsx
import React, { useEffect, useRef, useState } from 'react';

export default function OldWebRTC({ renderVideo = true, keepConnection = false }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);
  const pcRef = useRef(null);
  const textareaRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const animationFrameIdRef = useRef(null);
  const alphaVideoRef = useRef(null);

  const containerRef = useRef(null);

  const [useStun, setUseStun] = useState(false);
  const [started, setStarted] = useState(false);
  const [zoomWidth, setZoomWidth] = useState(600);
  const [greenThreshold, setGreenThreshold] = useState(50);
  const [videoReady, setVideoReady] = useState(false);
  const [alphaReady, setAlphaReady] = useState(false);
  const [processingMode, setProcessingMode] = useState('auto');
  const [errorMessage, setErrorMessage] = useState('');

  const initialWidth = 600;
  const initialHeight = 450;
  const SIGNALING_URL = '/offer';
  const zoomStep = 50;

  const [chatPos, setChatPos] = useState(() => {
    try {
      const saved = localStorage.getItem('chatbox-position');
      if (saved) {
        const pos = JSON.parse(saved);
        if (typeof pos.left === 'number' && typeof pos.top === 'number') {
          return pos;
        }
      }
    } catch {}
    return { left: 20, top: 20 };
  });

  const draggingRef = useRef(false);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  function onDragStart(e) {
    if (e.target.tagName.toLowerCase() === 'textarea') {
      draggingRef.current = false;
      return;
    }
    
    draggingRef.current = true;
    const targetRect = e.currentTarget.getBoundingClientRect();
    if (e.type === 'touchstart' && e.touches?.[0]) {
      dragOffsetRef.current = {
        x: e.touches[0].clientX - targetRect.left,
        y: e.touches[0].clientY - targetRect.top,
      };
    } else {
      dragOffsetRef.current = {
        x: e.clientX - targetRect.left,
        y: e.clientY - targetRect.top,
      };
    }
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragMove(e) {
    if (!draggingRef.current) return;
  
    let clientX, clientY;
    if (e.type === 'touchmove' && e.touches?.[0]) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else if (e.type === 'mousemove') {
      clientX = e.clientX;
      clientY = e.clientY;
    } else {
      return;
    }
  
    if (!containerRef.current || !e.currentTarget) return;
  
    const containerRect = containerRef.current.getBoundingClientRect();
    const chatRect = e.currentTarget.getBoundingClientRect();
    const chatWidth = chatRect.width;
    const chatHeight = chatRect.height;
  
    let newLeft = clientX - containerRect.left - dragOffsetRef.current.x;
    let newTop = clientY - containerRect.top - dragOffsetRef.current.y;
  
    newLeft = Math.min(Math.max(0, newLeft), containerRect.width - chatWidth);
    newTop = Math.min(Math.max(0, newTop), containerRect.height - chatHeight);
  
    setChatPos({ left: newLeft, top: newTop });
  
    e.preventDefault();
    e.stopPropagation();
  }
  
  function onDragEnd(e) {
    if (draggingRef.current) {
      draggingRef.current = false;
      localStorage.setItem('chatbox-position', JSON.stringify(chatPos));
      e.preventDefault();
      e.stopPropagation();
    }
  }

  function isGreen(r, g, b) {
    return g > r + greenThreshold && g > b + greenThreshold;
  }

  function processVideoFrame() {
    const video = videoRef.current;
    const alphaVideo = alphaVideoRef.current;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    
    if (!video || !canvas || !ctx || !started) {
      console.log('processVideoFrame: 等待资源准备就绪...');
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    if (video.paused || video.ended) {
      console.log('processVideoFrame: 视频暂停或结束');
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
      return;
    }

    try {
      // 确保Canvas尺寸正确
      if (canvas.width !== video.videoWidth || canvas.height !== video.videoHeight) {
        console.log(`processVideoFrame: 设置Canvas尺寸为 ${video.videoWidth}x${video.videoHeight}`);
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
      }

      // 清除画布，确保透明背景
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // 根据处理模式决定如何渲染
      if (processingMode === 'separate' || (processingMode === 'auto' && alphaReady)) {
        console.log('processVideoFrame: 使用分离Alpha通道模式渲染');
        
        // 检查Alpha视频是否准备好
        if (!alphaVideo || !alphaVideo.readyState >= 2) {
          console.warn('processVideoFrame: Alpha通道未就绪，使用RGBA模式替代');
          ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
          animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
          return;
        }
        
        const rgbCanvas = document.createElement('canvas');
        rgbCanvas.width = canvas.width;
        rgbCanvas.height = canvas.height;
        const rgbCtx = rgbCanvas.getContext('2d');
        
        // 绘制RGB图像
        rgbCtx.drawImage(video, 0, 0, canvas.width, canvas.height);
        const rgbData = rgbCtx.getImageData(0, 0, canvas.width, canvas.height).data;
        
        // 绘制Alpha通道图像
        const alphaCanvas = document.createElement('canvas');
        alphaCanvas.width = canvas.width;
        alphaCanvas.height = canvas.height;
        const alphaCtx = alphaCanvas.getContext('2d');
        alphaCtx.drawImage(alphaVideo, 0, 0, canvas.width, canvas.height);
        const alphaData = alphaCtx.getImageData(0, 0, canvas.width, canvas.height).data;
        
        // 验证数据有效性
        if (rgbData.length !== alphaData.length) {
          console.error(`processVideoFrame: RGB数据长度(${rgbData.length})与Alpha数据长度(${alphaData.length})不匹配`);
          ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
          animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
          return;
        }
        
        // 创建合并后的图像数据
        const mergedImageData = ctx.createImageData(canvas.width, canvas.height);
        const mergedData = mergedImageData.data;
        
        // 合并RGB和Alpha通道
        for (let i = 0; i < rgbData.length; i += 4) {
          mergedData[i] = rgbData[i];       // R
          mergedData[i + 1] = rgbData[i + 1];   // G
          mergedData[i + 2] = rgbData[i + 2];   // B
          
          // 使用Alpha通道的亮度值作为透明度
          mergedData[i + 3] = alphaData[i];     // A
        }
        
        // 将合并后的图像绘制到主Canvas
        ctx.putImageData(mergedImageData, 0, 0);
      } else {
        console.log('processVideoFrame: 使用RGBA模式渲染');
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
        
        // 检查是否可能是RGBA格式但没有正确显示
        if (processingMode === 'rgba') {
          // 尝试提取第一帧的部分像素，检查Alpha通道
          const testImageData = ctx.getImageData(0, 0, 10, 10);
          const testData = testImageData.data;
          let hasTransparency = false;
          
          for (let i = 3; i < testData.length; i += 4) {
            if (testData[i] < 255) {
              hasTransparency = true;
              break;
            }
          }
          
          console.log(`processVideoFrame: 测试Alpha通道 - ${hasTransparency ? '存在透明像素' : '全不透明'}`);
        }
      }
    } catch (e) {
      console.error('processVideoFrame: 视频帧处理错误:', e);
      setErrorMessage(`视频渲染错误: ${e.message}`);
    }

    animationFrameIdRef.current = requestAnimationFrame(processVideoFrame);
  }

  async function negotiate() {
    console.log('negotiate: 开始建立WebRTC连接');
    const pc = pcRef.current;
    if (!pc) return;

    pc.addTransceiver('video', { direction: 'recvonly' });
    pc.addTransceiver('audio', { direction: 'recvonly' });

    try {
      const offer = await pc.createOffer();
      console.log('negotiate: 创建Offer成功');
      await pc.setLocalDescription(offer);
      console.log('negotiate: 设置本地描述成功');

      await new Promise((resolve) => {
        if (pc.iceGatheringState === 'complete') {
          console.log('negotiate: ICE收集已完成');
          resolve();
        } else {
          const checkState = () => {
            if (pc.iceGatheringState === 'complete') {
              pc.removeEventListener('icegatheringstatechange', checkState);
              console.log('negotiate: ICE收集完成');
              resolve();
            }
          };
          pc.addEventListener('icegatheringstatechange', checkState);
          console.log('negotiate: 正在收集ICE候选...');
        }
      });

      console.log('negotiate: 发送Offer到服务器:', SIGNALING_URL);
      const response = await fetch(SIGNALING_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sdp: pc.localDescription.sdp,
          type: pc.localDescription.type,
        }),
      });
      
      if (!response.ok) {
        throw new Error(`服务器响应错误: ${response.status}`);
      }
      
      const answer = await response.json();
      console.log('negotiate: 收到服务器Answer');
      await pc.setRemoteDescription(answer);
      console.log('negotiate: 设置远程描述成功，WebRTC连接建立完成');
    } catch (e) {
      console.error('negotiate: 协商失败:', e);
      alert('协商失败: ' + e.message);
      setErrorMessage(`WebRTC连接失败: ${e.message}`);
    }
  }

  function start() {
    console.log('start: 启动WebRTC连接');
    if (started) return;

    const config = { sdpSemantics: 'unified-plan' };
    const pc = new RTCPeerConnection(config);
    pcRef.current = pc;

    // 监听连接状态变化
    pc.addEventListener('connectionstatechange', () => {
      console.log(`WebRTC连接状态变化: ${pc.connectionState}`);
      if (pc.connectionState === 'failed') {
        setErrorMessage('WebRTC连接失败');
      }
    });

    pc.addEventListener('track', (evt) => {
      console.log('收到新的媒体轨道:', evt.track.kind, evt.streams[0].id);
      
      if (evt.track.kind === 'video') {
        // 检查是否是Alpha通道视频
        if (evt.streams[0].id.endsWith('_alpha')) {
          console.log('收到Alpha通道视频流');
          if (alphaVideoRef.current) {
            alphaVideoRef.current.srcObject = evt.streams[0];
            
            // Alpha通道视频元数据加载后设置就绪状态
            alphaVideoRef.current.addEventListener('loadedmetadata', () => {
              console.log('Alpha通道视频元数据加载完成');
              setAlphaReady(true);
              console.log('切换到分离通道模式');
              setProcessingMode('separate');
            }, { once: true });
            
            alphaVideoRef.current.addEventListener('canplay', () => {
              console.log('Alpha通道视频可以播放');
            }, { once: true });
            
            alphaVideoRef.current.addEventListener('error', (e) => {
              console.error('Alpha通道视频错误:', e.target.error);
              setErrorMessage(`Alpha通道错误: ${e.target.error.message}`);
            });
          }
        } else {
          // 普通RGB视频
          console.log('收到RGB视频流');
          if (videoRef.current) {
            videoRef.current.srcObject = evt.streams[0];
            
            // 视频元数据加载后初始化Canvas
            videoRef.current.addEventListener('loadedmetadata', () => {
              console.log('RGB视频元数据加载完成，尺寸:', videoRef.current.videoWidth, videoRef.current.videoHeight);
              setVideoReady(true);
              const canvas = canvasRef.current;
              if (canvas) {
                const ctx = canvas.getContext('2d', { willReadFrequently: true });
                ctxRef.current = ctx;
                console.log('开始处理视频帧');
                processVideoFrame();
              }
              
              // 如果3秒后还没有收到Alpha通道，切换到RGBA模式
              setTimeout(() => {
                if (!alphaReady && started) {
                  console.log('3秒后未检测到Alpha通道，切换到RGBA模式');
                  setProcessingMode('rgba');
                }
              }, 3000);
            }, { once: true });
            
            videoRef.current.addEventListener('canplay', () => {
              console.log('RGB视频可以播放');
            }, { once: true });
            
            videoRef.current.addEventListener('error', (e) => {
              console.error('RGB视频错误:', e.target.error);
              setErrorMessage(`RGB视频错误: ${e.target.error.message}`);
            });
          }
        }
      } else if (evt.track.kind === 'audio') {
        console.log('收到音频流');
        if (audioRef.current) {
          audioRef.current.srcObject = evt.streams[0];
        }
      }
    });

    setStarted(true);
    setErrorMessage('');
    negotiate();
    setZoomWidth(initialWidth);
  }

  function stop() {
    console.log('stop: 停止WebRTC连接');
    if (!started) return;

    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
      animationFrameIdRef.current = null;
    }

    setStarted(false);
    setVideoReady(false);
    setAlphaReady(false);
    setProcessingMode('auto');
    setErrorMessage('');

    if (!keepConnection) {
      setTimeout(() => {
        if (pcRef.current) {
          console.log('关闭RTCPeerConnection');
          pcRef.current.close();
          pcRef.current = null;
        }
      }, 500);

      if (videoRef.current?.srcObject) {
        console.log('停止RGB视频轨道');
        videoRef.current.srcObject.getTracks().forEach(t => t.stop());
        videoRef.current.srcObject = null;
      }
      if (alphaVideoRef.current?.srcObject) {
        console.log('停止Alpha通道视频轨道');
        alphaVideoRef.current.srcObject.getTracks().forEach(t => t.stop());
        alphaVideoRef.current.srcObject = null;
      }
      if (audioRef.current?.srcObject) {
        console.log('停止音频轨道');
        audioRef.current.srcObject.getTracks().forEach(t => t.stop());
        audioRef.current.srcObject = null;
      }
    }
  }

  function zoomIn() {
    setZoomWidth(w => w + zoomStep);
  }
  function zoomOut() {
    setZoomWidth(w => (w - zoomStep > 0 ? w - zoomStep : w));
  }

  const [message, setMessage] = useState('');
  const [imageFile, setImageFile] = useState(null);

  const sendTextMessage = async () => {
    if (!message.trim()) return;
    try {
      console.log('发送文本消息:', message);
      const response = await fetch('https://192.168.168.77:6010/qwener', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: message }),
      });
      if (!response.ok) throw new Error('状态码: ' + response.status);
      console.log('文本发送成功');
    } catch (error) {
      console.error('文本发送失败：', error);
    }
  };

  const sendMultiModal = async () => {
    if (!imageFile) return;
    try {
      console.log('发送多模态消息，包含图片');
      const reader = new FileReader();
      reader.onloadend = async () => {
        const response = await fetch('https://192.168.1.70:6010/multimodal', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: message.trim(), image: reader.result }),
        });
        if (!response.ok) throw new Error('状态码: ' + response.status);
        console.log('多模态发送成功');
        setImageFile(null);
        setMessage('');
      };
      reader.readAsDataURL(imageFile);
    } catch (error) {
      console.error('多模态发送失败：', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (imageFile) {
      await sendMultiModal();
    } else {
      await sendTextMessage();
      setMessage('');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file || null);
  };

  useEffect(() => {
    console.log('组件挂载或卸载，清理动画帧');
    return () => {
      if (animationFrameIdRef.current) {
        console.log('取消动画帧请求');
        cancelAnimationFrame(animationFrameIdRef.current);
      }
    };
  }, []);

  const frostedGlassBtnStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    color: 'white',
    fontWeight: 'bold',
    padding: '8px 16px',
    cursor: 'pointer',
    borderRadius: 4,
    outline: 'none',
    transition: 'all 0.3s',
    fontSize: 14,
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 10px rgba(255, 255, 255, 0.15)',
  };

  const disabledBtnStyle = {
    ...frostedGlassBtnStyle,
    opacity: 0.4,
    cursor: 'not-allowed',
    boxShadow: 'none',
  };

  const getBtnHoverStyle = (disabled) => {
    if (disabled) return {};
    return {
      backgroundColor: 'rgba(255, 255, 255, 0.2)',
      boxShadow: '0 6px 20px rgba(255, 255, 255, 0.3)',
      transform: 'scale(1.05)',
    };
  };

  const [startBtnHover, setStartBtnHover] = useState(false);
  const [stopBtnHover, setStopBtnHover] = useState(false);
  const [zoomInHover, setZoomInHover] = useState(false);
  const [zoomOutHover, setZoomOutHover] = useState(false);
  const [interruptBtnHover, setInterruptBtnHover] = useState(false);

  const handleInterruptClick = async () => {
    try {
      console.log('发送打断请求');
      const resp = await fetch('http://192.168.168.77:6010/api/interrupt_speaking', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!resp.ok) throw new Error('状态码: ' + resp.status);
      console.log('打断请求发送成功');
    } catch (e) {
      console.error('打断接口异常:', e);
    }
  };

  return (
    <div ref={containerRef} style={{ maxWidth: 1280, position: 'relative', minHeight: '100vh' }}>
      <div className="option" style={{ marginBottom: 8 }}>
        <input
          id="use-stun"
          type="checkbox"
          checked={useStun}
          onChange={(e) => setUseStun(e.target.checked)}
          style={{ accentColor: 'white' }}
          disabled={started}
          hidden
        />
      </div>

      <div style={{ marginBottom: 8 }}>
        <button
          onClick={zoomIn}
          style={{
            ...frostedGlassBtnStyle,
            marginRight: 8,
            ...(zoomInHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomInHover(true)}
          onMouseLeave={() => setZoomInHover(false)}
          type="button"
        >
          放大
        </button>
        <button
          onClick={zoomOut}
          style={{
            ...frostedGlassBtnStyle,
            ...(zoomOutHover ? getBtnHoverStyle(false) : {})
          }}
          onMouseEnter={() => setZoomOutHover(true)}
          onMouseLeave={() => setZoomOutHover(false)}
          type="button"
        >
          缩小
        </button>
      </div>

      <button
        onClick={start}
        disabled={started}
        style={{
          ...(started ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(startBtnHover && !started ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStartBtnHover(true)}
        onMouseLeave={() => setStartBtnHover(false)}
      >
        启动
      </button>
      <button
        onClick={stop}
        disabled={!started}
        style={{
          ...(!started ? disabledBtnStyle : frostedGlassBtnStyle),
          ...(stopBtnHover && started ? getBtnHoverStyle(false) : {})
        }}
        onMouseEnter={() => setStopBtnHover(true)}
        onMouseLeave={() => setStopBtnHover(false)}
      >
        停止
      </button>

      {/* 新增：显示当前状态和错误信息 */}
      {errorMessage && (
        <div style={{ color: 'red', margin: '10px 0', padding: '10px', background: 'rgba(0,0,0,0.5)', borderRadius: 4 }}>
          <strong>错误:</strong> {errorMessage}
        </div>
      )}
      
      <div style={{ color: 'white', margin: '10px 0' }}>
        <strong>状态:</strong> {started ? '已连接' : '未连接'} | 
        <strong>模式:</strong> {processingMode} | 
        <strong>视频:</strong> {videoReady ? '就绪' : '加载中'} | 
        <strong>Alpha:</strong> {alphaReady ? '就绪' : '未检测到'}
      </div>

      {renderVideo && (
        <div id="media" style={{ marginTop: 20 }}>
          <h2 style={{ color: 'white' }}>管家</h2>

          <audio ref={audioRef} autoPlay />
          
          {/* 原视频标签（临时显示用于调试） */}
          <video
            ref={videoRef}
            style={{
              display: 'block',
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 10,
              zIndex: 100,
              border: '2px solid red'
            }}
            autoPlay
            playsInline
          />
          
          {/* Alpha通道视频（隐藏） */}
          <video
            ref={alphaVideoRef}
            style={{
              display: 'none',
              width: 100,
              height: 'auto',
              position: 'absolute',
              top: 10,
              right: 130,
              zIndex: 100,
              border: '2px solid green'
            }}
            autoPlay
            playsInline
          />
          
          {/* Canvas（主显示区域） */}
          <canvas
            ref={canvasRef}
            style={{
              width: zoomWidth,
              height: (zoomWidth * initialHeight) / initialWidth,
              display: 'block',
              position: 'relative',
              zIndex: 10,
              border: '2px solid white',
              backgroundColor: 'transparent'
            }}
          />
        </div>
      )}

      <div
        style={{
          position: 'absolute',
          left: chatPos.left,
          top: chatPos.top,
          width: 320,
          borderRadius: 6,
          padding: 12,
          color: 'white',
          userSelect: 'none',
          cursor: draggingRef.current ? 'grabbing' : 'grab',
          zIndex: 9999,
          backdropFilter: 'blur(10px)',
          backgroundColor: 'rgba(0,0,0,0.5)',
        }}
        onMouseDown={onDragStart}
        onMouseMove={onDragMove}
        onMouseUp={onDragEnd}
        onMouseLeave={onDragEnd}
        onTouchStart={onDragStart}
        onTouchMove={onDragMove}
        onTouchEnd={onDragEnd}
      >
        <form
          id="echo-form"
          onSubmit={handleSubmit}
          style={{ display: 'flex', flexDirection: 'column', gap: 8 }}
        >
          <textarea
            ref={textareaRef}
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows={3}
            style={{
              width: '100%',
              resize: 'vertical',
              padding: 8,
              borderRadius: 8,
              border: '1px solid rgba(255,255,255,0.3)',
              backgroundColor: 'rgba(255,255,255,0.1)',
              color: 'white',
              outline: 'none',
            }}
            placeholder="请输入文本..."
            onClick={(e) => e.stopPropagation()}
          />
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
            <button
              type="button"
              onClick={handleInterruptClick}
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 16px',
                fontSize: 14,
              }}
              onMouseEnter={() => setInterruptBtnHover(true)}
              onMouseLeave={() => setInterruptBtnHover(false)}
            >
              打断说话
            </button>
            <label
              htmlFor="image-upload"
              style={{
                ...frostedGlassBtnStyle,
                padding: '6px 12px',
                cursor: 'pointer',
              }}
            >
              上传图片
            </label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleImageChange}
            />
            <button
              type="submit"
              style={{ ...frostedGlassBtnStyle, padding: '6px 16px' }}
            >
              发送
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
