import React, { useEffect, useState, useRef } from 'react';
import cameraSwitchIcon from '../assets/icons/camera-switch.png';
import styles from './CameraControl.module.css';

export default function CameraControl() {
  const videoRef = useRef(null);
  const floatingContainerRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);
  const [isCameraVisible, setIsCameraVisible] = useState(true);

  // 拖动状态和偏移
  const isDraggingRef = useRef(false);
  const offsetXRef = useRef(0);
  const offsetYRef = useRef(0);

  // 当前流和摄像头 deviceId（使用 useRef 避免闭包问题）
  const currentStreamRef = useRef(null);
  const currentCameraDeviceIdRef = useRef(null);

  // 1. 获取摄像头设备列表
  async function getCameraDevices() {
    try {
      const allDevices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = allDevices.filter((d) => d.kind === 'videoinput');
      return videoDevices;
    } catch (error) {
      console.error('无法获取摄像头设备列表:', error);
      return [];
    }
  }

  // 2. 初始化摄像头，启动指定 deviceId 的摄像头
  async function initializeCamera(deviceId) {
    stopCamera();

    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: deviceId ? { exact: deviceId } : undefined },
        audio: false,
      });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play().catch(() => {
          // play 失败的捕获，防止报错
        });
      }
      currentStreamRef.current = stream;
      currentCameraDeviceIdRef.current = deviceId;
      setStream(stream);
      setSelectedDeviceId(deviceId);
      return stream;
    } catch (error) {
      console.error('摄像头初始化失败:', error);
      alert('摄像头初始化失败: ' + error.message);
      throw error;
    }
  }

  // 3. 停止摄像头
  function stopCamera() {
    if (currentStreamRef.current) {
      currentStreamRef.current.getTracks().forEach((track) => track.stop());
      currentStreamRef.current = null;
      setStream(null);
    }
  }

  // 4. 切换摄像头（单击）
  async function switchCamera() {
    const videoDevices = await getCameraDevices();
    if (videoDevices.length < 2) {
      console.log('设备仅支持一个摄像头，无法切换');
      return;
    }
    // 找到下一个摄像头 deviceId
    let nextDeviceId = null;
    for (const device of videoDevices) {
      if (device.deviceId !== currentCameraDeviceIdRef.current) {
        nextDeviceId = device.deviceId;
        break;
      }
    }
    if (!nextDeviceId) {
      // 如果没找到其他，切回第一个
      nextDeviceId = videoDevices[0].deviceId;
    }
    try {
      await initializeCamera(nextDeviceId);
      console.log('摄像头已切换:', nextDeviceId);
    } catch (e) {
      console.error('切换摄像头失败:', e);
    }
  }

  // 5. 显示/隐藏摄像头（双击）
  function toggleCameraVisibility() {
    setIsCameraVisible((v) => !v);
  }

  // 6. 处理点击事件，单击切换摄像头
  //    双击显示隐藏摄像头，利用 button 的 onClick 和 onDoubleClick 简化逻辑
  function handleButtonClick() {
    switchCamera();
  }

  function handleButtonDoubleClick() {
    toggleCameraVisibility();
  }

  // 7. 页面初始化，获取设备列表，初始化第一个摄像头
  useEffect(() => {
    async function initializePage() {
      const videoDevices = await getCameraDevices();
      setDevices(videoDevices);
      if (videoDevices.length > 0) {
        try {
          await initializeCamera(videoDevices[0].deviceId);
        } catch {
          // 初始化失败已经有提示
        }
      } else {
        console.log('未找到摄像头设备');
      }
    }
    initializePage();

    // Cleanup on unmount
    return () => {
      stopCamera();
      if (floatingContainerRef.current) {
        floatingContainerRef.current.removeEventListener('mousedown', onMouseDown);
        floatingContainerRef.current.removeEventListener('touchstart', onTouchStart);
      }
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);
      document.removeEventListener('touchmove', onTouchMove);
      document.removeEventListener('touchend', onTouchEnd);
    };
  }, []);

  // 8. 悬浮拖动功能相关函数
  const onMouseDown = (e) => {
    if (!floatingContainerRef.current) return;
    isDraggingRef.current = true;
    offsetXRef.current = e.clientX - floatingContainerRef.current.offsetLeft;
    offsetYRef.current = e.clientY - floatingContainerRef.current.offsetTop;
    floatingContainerRef.current.style.cursor = 'grabbing';
    e.preventDefault();
  };
  const onMouseMove = (e) => {
    if (isDraggingRef.current && floatingContainerRef.current) {
      floatingContainerRef.current.style.left = `${e.clientX - offsetXRef.current}px`;
      floatingContainerRef.current.style.top = `${e.clientY - offsetYRef.current}px`;
    }
  };
  const onMouseUp = () => {
    if (floatingContainerRef.current) {
      isDraggingRef.current = false;
      floatingContainerRef.current.style.cursor = 'grab';
    }
  };
  const onTouchStart = (e) => {
    if (!floatingContainerRef.current || e.touches.length === 0) return;
    isDraggingRef.current = true;
    const touch = e.touches[0];
    offsetXRef.current = touch.clientX - floatingContainerRef.current.offsetLeft;
    offsetYRef.current = touch.clientY - floatingContainerRef.current.offsetTop;
    floatingContainerRef.current.style.cursor = 'grabbing';
  };
  const onTouchMove = (e) => {
    if (isDraggingRef.current && floatingContainerRef.current && e.touches.length > 0) {
      const touch = e.touches[0];
      floatingContainerRef.current.style.left = `${touch.clientX - offsetXRef.current}px`;
      floatingContainerRef.current.style.top = `${touch.clientY - offsetYRef.current}px`;
    }
  };
  const onTouchEnd = () => {
    if (floatingContainerRef.current) {
      isDraggingRef.current = false;
      floatingContainerRef.current.style.cursor = 'grab';
    }
  };

  // 9. 绑定拖动事件
  useEffect(() => {
    if (!floatingContainerRef.current) return;
    const container = floatingContainerRef.current;
    container.style.position = 'fixed'; // 固定定位
    container.style.left = '10px'; // 初始位置
    container.style.top = '10px';
    container.style.cursor = 'grab';
    container.style.zIndex = 10000;
    container.style.width = '160px'; // 容器大小，视频+按钮布局用

    container.addEventListener('mousedown', onMouseDown);
    document.addEventListener('mousemove', onMouseMove);
    document.addEventListener('mouseup', onMouseUp);

    container.addEventListener('touchstart', onTouchStart);
    document.addEventListener('touchmove', onTouchMove);
    document.addEventListener('touchend', onTouchEnd);

    return () => {
      container.removeEventListener('mousedown', onMouseDown);
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);

      container.removeEventListener('touchstart', onTouchStart);
      document.removeEventListener('touchmove', onTouchMove);
      document.removeEventListener('touchend', onTouchEnd);
    };
  }, []);

  return (
    <>
      <style>{`
        /* 圆形摄像头视频框 */
        #video {
          border-radius: 50%;
          object-fit: cover;
          width: 150px;
          height: 150px;
          background-color: black;
          display: block;
          user-select: none;
          pointer-events: none;
        }
        /* 容器内部布局，保证按钮在视频下面居中 */
        #floating-container {
          user-select: none;
          background: transparent;
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 8px;
          padding: 4px;
          box-sizing: border-box;
        }
        /* 切换摄像头按钮样式 */
        #toggle-switch-camera {
          cursor: pointer;
          width: 36px;
          height: 36px;
          border-radius: 50%;
          border: 1px solid #00e5ff;
          background-color: transparent;
          display: flex;
          justify-content: center;
          align-items: center;
          transition: background-color 0.3s, border-color 0.3s;
        }
        #toggle-switch-camera:hover {
          background-color: rgba(0, 229, 255, 0.15);
          border-color: #00e5ff;
        }
        #toggle-switch-camera img {
          width: 20px;
          height: 20px;
          pointer-events: none;
          user-select: none;
        }
      `}</style>

      <div id="floating-container" ref={floatingContainerRef}>
        {isCameraVisible && (
          <video
            id="video"
            ref={videoRef}
            autoPlay
            playsInline
            muted
            onDragStart={(e) => e.preventDefault()}
          />
        )}
        <button
          id="toggle-switch-camera"
          type="button"
          aria-label="切换摄像头"
          onClick={handleButtonClick}
          onDoubleClick={handleButtonDoubleClick}
        >
          <img src={cameraSwitchIcon} alt="切换摄像头" draggable={false} />
        </button>
      </div>
    </>
  );
}
