// components/WallpaperManager.jsx (修复语法错误)
import React, { useState, useEffect } from 'react';

export const menuWallpapers = {
  home: '/core/client/ai-butler/image/bg-home.jpeg',
  butler: '/core/client/ai-butler/image/bg-butler.jpeg',
  car: '/core/client/ai-butler/image/bg-car.jpeg',
  homeassistant: '/core/client/ai-butler/image/bg-homeassistant.jpeg',
  ops: '/core/client/ai-butler/image/bg-ops.jpeg',
  settings: '/core/client/ai-butler/image/bg-settings.jpeg',
};

const WallpaperManager = ({ selectedMenu, showGlassTransition }) => {
  const [currentWallpaper, setCurrentWallpaper] = useState(menuWallpapers[selectedMenu] || menuWallpapers.home);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const targetWallpaper = menuWallpapers[selectedMenu] || menuWallpapers.home;
    setIsLoading(true);
    setError(null);

    const img = new Image();
    img.onload = () => {
      console.log('壁纸加载成功:', targetWallpaper);
      setCurrentWallpaper(targetWallpaper);
      setIsLoading(false);
    };
    img.onerror = (err) => {
      console.error('壁纸加载失败:', targetWallpaper, err);
      setError(`图片加载失败: ${targetWallpaper}`);
      setIsLoading(false);
    };
    img.src = targetWallpaper;
  }, [selectedMenu]);

  return (
    <>
      {/* 壁纸容器 - 使用正确的内联样式语法 */}
      <div 
        className="app-wallpaper"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          zIndex: -1000, // 设为极低值确保在最底层
          backgroundColor: 'rgba(255, 0, 0, 0.3)', // 半透明红色背景用于调试
          backgroundImage: `url(${currentWallpaper})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          opacity: 1, // 强制不透明
          display: 'block', // 强制显示
          visibility: 'visible', // 强制可见
          pointerEvents: 'none', // 允许点击穿透
          // 添加CSS样式属性的!important效果
          WebkitUserSelect: 'none !important',
          MozUserSelect: 'none !important',
          msUserSelect: 'none !important',
          userSelect: 'none !important',
          // 使用CSS变量和属性覆盖来增强样式优先级
          '--wallpaper-z-index': -1000,
          '--wallpaper-opacity': 1,
        }}
        aria-hidden="true"
      />
      
      {/* 调试信息 */}
      {process.env.NODE_ENV !== 'production' && (
        <div style={{ position: 'fixed', bottom: 10, right: 10, backgroundColor: 'rgba(0,0,0,0.7)', color: 'white', padding: 10, fontSize: 12, zIndex: 1000 }}>
          <p>当前壁纸: {currentWallpaper}</p>
          <p>加载状态: {isLoading ? '加载中...' : '已加载'}</p>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}
    </>
  );
};

export default WallpaperManager;
