// App.jsx
import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import ButlerShowcase from './components/ButlerShowcase';
import { useButler, ButlerProvider } from './components/ButlersContext';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl'; // ä¿ç•™æ‘„åƒå¤´
import WebRTCPlayer from './components/OldWebRTC';

import FloatingContainer from './components/FloatingContainer';
// import SpeechRecognizer from './components/SpeechRecognizer'; // æ³¨é‡Šæ‰SpeechRecognizerç›¸å…³åŠŸèƒ½
import SseListener from './components/SseListener';

import Clock from './components/Clock'; // å¯¼å…¥ä½ æ–°å»ºçš„Clockç»„ä»¶

// æ–°å¢ï¼šå¯¼å…¥å½•éŸ³ç®¡ç†Providerå’ŒHook
import { RecorderProvider, useRecorderContext } from './contexts/RecorderProvider';

// æ–°å¢ï¼šå¯¼å…¥TTSPanelç»„ä»¶
import TTSPanel from './components/TTSPanel';

// æ–°å¢ï¼šå¯¼å…¥å£çº¸ç®¡ç†å™¨
import WallpaperManager from './components/WallpaperManager';

const ItemTypes = {
  PANEL: 'panel',
};

// æ˜ å°„æ¨¡å—idåˆ°ä¸­æ–‡åç§°å’Œè‹±æ–‡ID
const panelTitles = {
  audio: { cn: 'è¯­éŸ³è¯†åˆ«', en: 'ASR' },
  t2audio: { cn: 'è¯­éŸ³åˆæˆ', en: 'TTS' }, // æ–°å¢TTSæ¨¡å—æ ‡é¢˜
  // speech: { cn: 'è¯­éŸ³è¯†åˆ«', en: 'SPEECH' },
  camera: { cn: 'æ‘„åƒå¤´', en: 'CAMERA' }, // æ¢å¤æ‘„åƒå¤´é¢æ¿æ ‡é¢˜
  microphone: { cn: 'éº¦å…‹é£', en: 'MICROPHONE' }, // æ–°å¢éº¦å…‹é£é¢æ¿æ ‡é¢˜
  webrtc: { cn: 'å½“å‰ç®¡å®¶', en: 'AI Butler' },
  // floating: { cn: 'æµ®åŠ¨å®¹å™¨', en: 'FLOATING' },
};

// ä¿®æ”¹ HomeAssistantPanel ç»„ä»¶
function HomeAssistantPanel() {
  const HA_URL = 'http://192.168.168.10:8123';
  const [loading, setLoading] = useState(true);
  const [showOverlay, setShowOverlay] = useState(true);
  const iframeRef = useRef(null);
  
  // è®¡ç®—æ¯›ç»ç’ƒæ•ˆæœçš„èƒŒæ™¯
  const glassStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.1)',
    backdropFilter: 'blur(8px)',
    WebkitBackdropFilter: 'blur(10px)',
    pointerEvents: 'none', // å…è®¸ç‚¹å‡»ç©¿é€åˆ°iframe
    zIndex: 1,
  };

  // ä¸»å®¹å™¨æ ·å¼
  const containerStyle = {
    width: '100%',
    height: 'calc(100vh - 180px)',
    position: 'relative',
    overflow: 'hidden',
    borderRadius: 4,
    backgroundColor: 'rgba(0, 0, 0, 0.05)',
  };
  
  // iframeæ ·å¼ - è°ƒæ•´é€æ˜åº¦
  const iframeStyle = {
    width: '100%',
    height: '100%',
    border: 'none',
    opacity: 0.85, // è°ƒæ•´iframeæ•´ä½“é€æ˜åº¦
    backgroundColor: 'transparent',
    transition: 'opacity 0.5s ease',
  };

  // å¤„ç†iframeåŠ è½½å®Œæˆ
  const handleIframeLoad = () => {
    setLoading(false);
    
    // å»¶è¿Ÿéšè—è¦†ç›–å±‚ï¼Œè®©ç”¨æˆ·æœ‰æ—¶é—´æ„ŸçŸ¥åŠ è½½å®Œæˆ
    setTimeout(() => {
      setShowOverlay(false);
    }, 1000);
  };

  return (
    <div style={containerStyle}>
      {/* åŠ è½½æŒ‡ç¤ºå™¨ */}
      {loading && (
        <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 10 }}>
          <div className="spinner" style={{ width: 50, height: 50, border: '3px solid rgba(0,229,255,0.3)', borderRadius: '50%', borderTopColor: '#00e5ff', animation: 'spin 1s linear infinite' }} />
          <p style={{ marginLeft: 15, color: 'white' }}>åŠ è½½ Home Assistant...</p>
        </div>
      )}
      
      {/* æ¯›ç»ç’ƒæ•ˆæœè¦†ç›–å±‚ */}
      {showOverlay && <div style={glassStyle} />}
      
      {/* Home Assistant iframe */}
      <iframe
        ref={iframeRef}
        src={HA_URL}
        title="Home Assistant"
        style={iframeStyle}
        onLoad={handleIframeLoad}
        sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
      />
    </div>
  );
}

// éº¦å…‹é£æ§åˆ¶é¢æ¿ç»„ä»¶
function MicrophoneControlPanel() {
  const { isRecording, startRecording, stopRecording } = useRecorderContext();

  const toggleRecording = () => {
    if (isRecording) stopRecording();
    else startRecording();
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%' }}>
      <button
        onClick={toggleRecording}
        style={{
          backgroundColor: isRecording ? '#f44336' : '#00e5ff',
          color: 'white',
          border: 'none',
          padding: 15,
          borderRadius: '50%',
          cursor: 'pointer',
          fontWeight: 'bold',
          fontSize: 24,
          width: 60,
          height: 60,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
          transition: 'background-color 0.3s',
        }}
        aria-label={isRecording ? 'åœæ­¢å½•éŸ³' : 'å¼€å§‹å½•éŸ³'}
        title={isRecording ? 'åœæ­¢å½•éŸ³' : 'å¼€å§‹å½•éŸ³'}
      >
        {isRecording ? 'ğŸ”´' : 'ğŸ¤'}
      </button>
      <div style={{ marginTop: 10, color: 'white', fontSize: 16, fontWeight: 'bold' }}>
        {isRecording ? 'å½•éŸ³ä¸­...' : 'ç‚¹å‡»å¼€å§‹å½•éŸ³'}
      </div>
    </div>
  );
}

function DraggableResizablePanel({
  id,
  left,
  top,
  width = 320,
  height = 200,
  zIndex,
  movePanel,
  bringToFront,
  resizePanel,
  children,
}) {
  const dragDropRef = React.useRef(null);

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top, width, height },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), [id, left, top, width, height]);

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!dragDropRef.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        movePanel(item.id, newLeft, newTop, width, height);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  }), [left, top, width, height]);

  drag(drop(dragDropRef));

  const onResize = (event, { size }) => {
    resizePanel(id, size.width, size.height);
  };

  const title = panelTitles[id] || { cn: id.toUpperCase(), en: id.toUpperCase() };

  return (
    <div
      className="panel"
      ref={dragDropRef}
      onMouseDown={() => bringToFront(id)}
