          <span
            className="custom-handle custom-handle-se"
            style={{
              position: 'absolute',
              width: 20,
              height: 20,
              right: 0,
              bottom: 0,
              cursor: 'se-resize',
              backgroundColor: 'rgba(0,0,0,0.2)',
              borderRadius: '0 0 4px 0',
              zIndex: 10,
            }}
          />
        }
      >
        <div
          style={{
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(255, 255, 255, 0)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            userSelect: 'none',
          }}
        >
          {/* æ‹–åŠ¨æ† */}
          <div
            style={{
              height: 30,
              backgroundColor: 'rgba(0, 229, 255, 0.2)', // æ·»åŠ åŠé€æ˜èƒŒæ™¯
              color: 'white',
              cursor: 'grab',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              borderTopLeftRadius: 4,
              borderTopRightRadius: 4,
              fontWeight: 'bold',
              flexShrink: 0,
              lineHeight: 1,
              paddingTop: 2,
              paddingBottom: 2,
              backdropFilter: 'blur(5px)', // æ·»åŠ æ¨¡ç³Šæ•ˆæœ
              WebkitBackdropFilter: 'blur(5px)',
              boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)', // æ·»åŠ é˜´å½±
            }}
          >
            <div style={{ fontSize: (id === 'webrtc' || id === 'audio' || id === 't2audio' || id === 'camera' || id === 'microphone') ? 21 : 12, fontWeight: 'bold', userSelect: 'none' }}>
              {title.cn}
            </div>

            <div style={{ fontSize: 13, fontWeight: 'normal', userSelect: 'none', opacity: 0.7 }}>
              {title.en}
            </div>
          </div>
          <div
            style={{
              flex: 1,
              padding: 12,
              overflow: 'auto',
              userSelect: 'auto',
              position: 'relative',
            }}
          >
            {children}
