import React, { useEffect, useState, useRef } from 'react';
import cameraSwitchIcon from '../assets/icons/camera-switch.png';
import styles from './CameraControl.module.css';

export default function CameraControl() {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState('');

  useEffect(() => {
    async function fetchDevices() {
      if (!navigator.mediaDevices || !navigator.mediaDevices.enumerateDevices) {
        alert('浏览器不支持设备枚举');
        return;
      }
      const allDevices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = allDevices.filter((d) => d.kind === 'videoinput');
      setDevices(videoDevices);
      if (videoDevices.length > 0) setSelectedDeviceId(videoDevices[0].deviceId);
    }
    fetchDevices();
  }, []);

  useEffect(() => {
    if (!selectedDeviceId) return;

    async function startCamera() {
      if (stream) {
        stream.getTracks().forEach((t) => t.stop());
      }
      try {
        const s = await navigator.mediaDevices.getUserMedia({
          video: { deviceId: { exact: selectedDeviceId } },
          audio: false,
        });
        setStream(s);
        if (videoRef.current) {
          videoRef.current.srcObject = s;
        }
      } catch (e) {
        alert('打开摄像头失败: ' + e.message);
      }
    }
    startCamera();

    return () => {
      if (stream) {
        stream.getTracks().forEach((t) => t.stop());
      }
    };
  }, [selectedDeviceId]);

  return (
    <div style={{ marginBottom: 20 }}>
      <h3>Camera Control</h3>
      <select
        value={selectedDeviceId}
        onChange={(e) => setSelectedDeviceId(e.target.value)}
        style={{ marginBottom: 10, padding: 4 }}
      >
        {devices.map((device) => (
          <option key={device.deviceId} value={device.deviceId}>
            {device.label || `Camera ${device.deviceId}`}
          </option>
        ))}
      </select>
      <div>
        <video
          ref={videoRef}
          autoPlay
          playsInline
          muted
          style={{ width: 320, height: 240, backgroundColor: '#000' }}
        />
      </div>
    </div>
  );
}
