import React, { useState, useRef } from 'react';

// 示例管家数据，支持图片和视频（type: 'image' | 'video'）
// 你需要把相应资源放到 public 文件夹对应目录，路径以 / 开头
const butlers = [
  {
    id: 'butler1',
    name: '管家禅师',
    type: 'video',
    src: '/video/豆包禅师.mp4', // 请确保视频文件放在 public/video/sample.mp4
  },
  {
    id: 'butler2',
    name: '管家小粒',
    type: 'video',
    src: '/video/小粒.mp4',
  },
  {
    id: 'butler3',
    name: '管家小贾',
    type: 'video',
    src: '/video/小贾.mp4',
  },
  {
    id: 'butler4',
    name: '管家星期天',
    type: 'video',
    src: '/video/星期天.mp4',
  },
];

export default function ButlerShowcase() {
  // 当前激活管家ID
  const [activeButlerId, setActiveButlerId] = useState(butlers[0].id);

  // 高亮管家ID(鼠标移入)
  const [highlightButlerId, setHighlightButlerId] = useState(null);

  // 是否显示确认对话框
  const [showConfirm, setShowConfirm] = useState(false);

  // 记录当前触发确认的管家ID
  const confirmButlerId = useRef(null);

  // 定时器ID
  const timer = useRef(null);

  // 鼠标进入管家头像，启动3秒定时器
  function handleMouseEnter(id) {
    setHighlightButlerId(id);
    // 清除已有定时器，防止多重启动
    if (timer.current) {
      clearTimeout(timer.current);
      timer.current = null;
    }
    // 3秒后显示确认框
    timer.current = setTimeout(() => {
      confirmButlerId.current = id;
      setShowConfirm(true);
    }, 3000);
  }

  // 鼠标离开头像区域，清除定时器且关闭确认框
  function handleMouseLeave(id) {
    setHighlightButlerId(null);
    if (timer.current) {
      clearTimeout(timer.current);
      timer.current = null;
    }
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 确认切换管家
  function confirmSwitch() {
    if (confirmButlerId.current && confirmButlerId.current !== activeButlerId) {
      setActiveButlerId(confirmButlerId.current);
    }
    setShowConfirm(false);
    confirmButlerId.current = null;
  }

  // 取消切换
  function cancelSwitch() {
    setShowConfirm(false);
    confirmButlerId.current = null;
    setHighlightButlerId(null);
  }

  return (
    <>
      <div
        style={{
          display: 'flex',
          gap: 24,
          padding: 20,
          justifyContent: 'center',
          flexWrap: 'wrap',
          backgroundColor: '#111',
          borderRadius: 12,
          userSelect: 'none',
        }}
      >
        {butlers.map(({ id, name, type, src }) => {
          const isActive = id === activeButlerId;
          const isHighlight = id === highlightButlerId;

          // 亮灯效果 - 当前激活或鼠标悬浮高亮时光晕亮起
          const glowStyle =
            isActive || isHighlight
              ? {
                  boxShadow: '0 0 20px 4px #0ff, inset 0 0 10px #0ff',
                  transition: 'box-shadow 0.3s ease',
                }
              : {
                  filter: 'grayscale(80%) brightness(70%)',
                  transition: 'filter 0.3s ease',
                };

          return (
            <div
              key={id}
              onMouseEnter={() => handleMouseEnter(id)}
              onMouseLeave={() => handleMouseLeave(id)}
              style={{
                position: 'relative',
                width: 120,
                height: 160,
                borderRadius: 20,
                cursor: 'pointer',
                backgroundColor: '#222',
                ...glowStyle,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                padding: 12,
                boxSizing: 'border-box',
                userSelect: 'none',
              }}
            >
              {type === 'image' ? (
                <img
                  src={src}
                  alt={name}
                  draggable={false}
                  style={{
                    width: 96,
                    height: 96,
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: isActive ? '3px solid #0ff' : '3px solid transparent',
                    boxSizing: 'border-box',
                    transition: 'border-color 0.3s ease',
                    userSelect: 'none',
                  }}
                />
              ) : (
                <video
                  src={src}
                  autoPlay
                  loop
                  muted
                  playsInline
                  style={{
                    width: 96,
                    height: 96,
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: isActive ? '3px solid #0ff' : '3px solid transparent',
                    boxSizing: 'border-box',
                    transition: 'border-color 0.3s ease',
                    userSelect: 'none',
                  }}
                />
              )}
              <div
                style={{
                  marginTop: 12,
                  color: isActive ? '#0ff' : '#ccc',
                  fontWeight: 'bold',
                  textAlign: 'center',
                  userSelect: 'none',
                  textShadow: isActive ? '0 0 6px #0ff' : 'none',
                }}
              >
                {name}
              </div>
              {/* 小灯泡装饰 */}
              <div
                style={{
                  position: 'absolute',
                  top: 8,
                  right: 8,
                  width: 14,
                  height: 14,
                  borderRadius: '50%',
                  backgroundColor: isActive || isHighlight ? '#0ff' : '#555',
                  boxShadow: isActive || isHighlight ? '0 0 8px #0ff' : 'none',
                  transition: 'all 0.3s ease',
                }}
              />
            </div>
          );
        })}
      </div>

      {/* 透明确认对话框 */}
      {showConfirm && (
        <div
          onClick={cancelSwitch} // 点击背景取消
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.55)',
            backdropFilter: 'blur(4px)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
            userSelect: 'none',
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()} // 阻止冒泡点击
            style={{
              backgroundColor: 'rgba(0,0,0,0.8)',
              borderRadius: 16,
              padding: 24,
              minWidth: 320,
              maxWidth: '90vw',
              color: '#0ff',
              boxShadow: '0 0 20px #0ff',
              textAlign: 'center',
              userSelect: 'none',
            }}
          >
            <p style={{ fontSize: 18, marginBottom: 20 }}>
              是否切换到管家{' '}
              <strong>{butlers.find((b) => b.id === confirmButlerId.current)?.name}</strong>？
            </p>
            <div style={{ display: 'flex', justifyContent: 'center', gap: 20 }}>
              <button
                onClick={confirmSwitch}
                style={{
                  padding: '8px 20px',
                  borderRadius: 8,
                  border: 'none',
                  backgroundColor: '#0ff',
                  color: '#000',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  userSelect: 'none',
                }}
              >
                确认
              </button>
              <button
                onClick={cancelSwitch}
                style={{
                  padding: '8px 20px',
                  borderRadius: 8,
                  border: '2px solid #0ff',
                  backgroundColor: 'transparent',
                  color: '#0ff',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  userSelect: 'none',
                }}
              >
                取消
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
