// App.jsx
import './index.css';
import React, { useState, useRef, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import ButlerShowcase from './components/ButlerShowcase';
import { useButler, ButlerProvider } from './components/ButlersContext';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl'; // 摄像头组件
import WebRTCPlayer from './components/OldWebRTC'; // 当前管家组件

import SseListener from './components/SseListener';

import Clock from './components/Clock'; // 右上角时间显示

import { RecorderProvider } from './contexts/RecorderProvider';
import TTSPanel from './components/TTSPanel';

import WallpaperManager from './components/WallpaperManager';

const ItemTypes = {
  PANEL: 'panel',
};

const panelTitles = {
  audio: { cn: '语音识别', en: 'ASR' },
  t2audio: { cn: '语音合成', en: 'TTS' },
  camera: { cn: '摄像头', en: 'CAMERA' },
  webrtc: { cn: '当前管家', en: 'AI Butler' },
};

function DraggableResizablePanel({
  id,
  left,
  top,
  width = 320,
  height = 200,
  zIndex,
  movePanel,
  bringToFront,
  resizePanel,
  children,
}) {
  const dragDropRef = React.useRef(null);

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PANEL,
    item: { id, left, top, width, height },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), [id, left, top, width, height]);

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PANEL,
    hover(item, monitor) {
      if (!dragDropRef.current) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      if (!delta) return;

      const newLeft = Math.round(item.left + delta.x);
      const newTop = Math.round(item.top + delta.y);

      if (newLeft !== left || newTop !== top) {
        console.log(`[Drag] Moving panel "${id}" from (${left},${top}) to (${newLeft},${newTop})`);
        movePanel(item.id, newLeft, newTop, width, height);
        item.left = newLeft;
        item.top = newTop;
      }
    },
  }), [left, top, width, height]);

  drag(drop(dragDropRef));

  const onResize = (event, { size }) => {
    console.log(`[Resize] Resizing panel "${id}" to ${size.width}x${size.height}`);
    resizePanel(id, size.width, size.height);
  };

  const title = panelTitles[id] || { cn: id.toUpperCase(), en: id.toUpperCase() };

  return (
    <div
      className="panel"
      ref={dragDropRef}
      onMouseDown={() => {
        console.log(`[Focus] Bringing panel "${id}" to front`);
        bringToFront(id);
      }}
      style={{
        position: 'absolute',
        left,
        top,
        zIndex,
        userSelect: 'none',
        width,
        height,
        opacity: isDragging ? 0.7 : 1,
        boxSizing: 'border-box',
        border: '2px solid red', // 临时边框调试，确认面板占位和可见
        backgroundColor: 'rgba(0,0,0,0.3)', // 方便看清面板背景
      }}
    >
      <ResizableBox
        width={width}
        height={height}
        minConstraints={[150, 100]}
        maxConstraints={[800, 600]}
        onResize={onResize}
        resizeHandles={['se']}
        handle={
          <span
            className="custom-handle custom-handle-se"
            style={{
              position: 'absolute',
              width: 20,
              height: 20,
              right: 0,
              bottom: 0,
              cursor: 'se-resize',
              backgroundColor: 'rgba(255,255,255,0.7)',
              borderRadius: '0 0 4px 0',
              zIndex: 10,
            }}
          />
        }
      >
        <div
          style={{
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(255, 255, 255, 0)',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            userSelect: 'none',
          }}
        >
          <div
            style={{
              height: 30,
              backgroundColor: 'transparent',
              color: 'white',
              cursor: 'grab',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              borderTopLeftRadius: 4,
              borderTopRightRadius: 4,
              fontWeight: 'bold',
              flexShrink: 0,
              lineHeight: 1,
              paddingTop: 2,
              paddingBottom: 2,
            }}
          >
            <div style={{ fontSize: (id === 'webrtc' || id === 'audio' || id === 't2audio') ? 21 : 12, fontWeight: 'bold', userSelect: 'none' }}>
              {title.cn}
            </div>

            <div style={{ fontSize: 13, fontWeight: 'normal', userSelect: 'none', opacity: 0.7 }}>
              {title.en}
            </div>
          </div>
          <div
            style={{
              flex: 1,
              padding: 12,
              overflow: 'auto',
              userSelect: 'auto',
              position: 'relative',
            }}
          >
            {children}
          </div>
        </div>
      </ResizableBox>
    </div>
  );
}

function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      const parsed = JSON.parse(saved);
      console.log('[Init] Loaded panels from localStorage:', parsed);
      return parsed;
    }
  } catch (e) {
    console.warn('[Init] 读取面板位置失败', e);
  }
  const defaults = [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 't2audio', left: 360, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 400, width: 320, height: 240, zIndex: 1 },
  ];
  console.log('[Init] 使用默认面板:', defaults);
  return defaults;
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);

  const [panels, setPanels] = useState(loadPanels);
  const maxZIndex = useRef(1);

  const [selectedMenu, setSelectedMenu] = useState('home');

  const [showGlassTransition, setShowGlassTransition] = useState(false);
  const pendingMenuRef = useRef(null);
  const transitionTimeoutRef = useRef(null);

  const [webrtcConnected, setWebrtcConnected] = useState(false);

  useEffect(() => {
    console.log('[State] Panels updated:', panels);
    localStorage.setItem('panels', JSON.stringify(panels));
  }, [panels]);

  function handleSseMessage(data) {
    console.log('[SSE] 收到消息:', data);
    setSseMessages((prev) => [...prev, data]);
  }

  function movePanel(id, left, top, width, height) {
    const maxLeft = window.innerWidth - width;
    const maxTop = window.innerHeight - height;

    const boundedLeft = Math.min(Math.max(0, left), maxLeft);
    const boundedTop = Math.min(Math.max(0, top), maxTop);

    console.log(`[movePanel] 移动面板"${id}"到位置(${boundedLeft},${boundedTop})`);
    setPanels((prev) =>
      prev.map((p) =>
        p.id === id ? { ...p, left: boundedLeft, top: boundedTop } : p
      )
    );
  }

  function resizePanel(id, width, height) {
    console.log(`[resizePanel] 调整面板"${id}"大小为 ${width}x${height}`);
    setPanels((prev) =>
      prev.map((p) =>
        p.id === id
          ? {
              ...p,
              width,
              height,
            }
          : p
      )
    );
  }

  function bringToFront(id) {
    maxZIndex.current += 1;
    console.log(`[bringToFront] 面板"${id}"提升到最前 zIndex=${maxZIndex.current}`);
    setPanels((prev) =>
      prev.map((p) => (p.id === id ? { ...p, zIndex: maxZIndex.current } : p))
    );
  }

  function DebugActiveButler() {
    const { activeButlerId } = useButler();
    return (
      <div style={{ color: 'white', marginBottom: 12, fontWeight: 'bold' }}>
        当前激活管家ID: {activeButlerId}
      </div>
    );
  }

  const handleMenuClick = (menuKey) => {
    if (showGlassTransition) return;
    pendingMenuRef.current = menuKey;
    setShowGlassTransition(true);
    console.log(`[Menu] 请求切换菜单到 '${menuKey}'，启动毛玻璃过渡动画`);

    if (transitionTimeoutRef.current) clearTimeout(transitionTimeoutRef.current);
    transitionTimeoutRef.current = setTimeout(() => {
      setSelectedMenu(pendingMenuRef.current);
      pendingMenuRef.current = null;
      setShowGlassTransition(false);
      console.log(`[Menu] 完成菜单切换，当前菜单: '${menuKey}'`);
    }, 400);
  };

  const handleWebrtcConnect = () => {
    console.log('[WebRTC] 连接已建立');
    setWebrtcConnected(true);
  };

  const handleWebrtcDisconnect = () => {
    console.log('[WebRTC] 连接已断开');
    setWebrtcConnected(false);
  };

  const defaultPanels = [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 't2audio', left: 360, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
    { id: 'camera', left: 20, top: 400, width: 320, height: 240, zIndex: 1 },
  ];

  const resetAllPanels = () => {
    console.log('[Reset] 重置所有面板到默认');
    localStorage.removeItem('panels');
    setPanels(defaultPanels);
  };

  const resetSinglePanel = (id) => {
    console.log(`[Reset] 重置单个面板 "${id}"`);
    setPanels((prev) =>
      prev.map((p) => {
        if (p.id === id) {
          const defaultPanel = defaultPanels.find((dp) => dp.id === id);
          return defaultPanel || p;
        }
        return p;
      })
    );
  };

  const renderContent = () => {
    switch (selectedMenu) {
      case 'home':
        return (
          <>
            <div style={{ padding: 20 }}>
              <h2>主页</h2>
              <DebugActiveButler />
            </div>
            {panels.map(({ id, left, top, width, height, zIndex }) => {
              if (id !== 'audio' && id !== 't2audio') return null;
              if (id === 'audio') {
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <AudioTranscriber />
                  </DraggableResizablePanel>
                );
              } else if (id === 't2audio') {
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <TTSPanel />
                  </DraggableResizablePanel>
                );
              }
              return null;
            })}
          </>
        );
      case 'butler':
        return (
          <div style={{ padding: 20 }}>
            <h2>管家页面</h2>
            <DebugActiveButler />
            <ButlerShowcase />
          </div>
        );
      case 'car':
        return (
          <div style={{ padding: 20 }}>
            <h2>车机控制</h2>
            <p>这里是车机控制页面</p>
          </div>
        );
      case 'homeassistant':
        return (
          <div style={{ padding: 20, height: 'calc(100vh - 120px)' }}>
            <h2>Home Assistant 控制面板</h2>
            <HomeAssistantPanel />
          </div>
        );
      case 'ops':
        return (
          <div style={{ padding: 20 }}>
            <h2>运维页面</h2>
            <p>这里是运维页面的内容，请根据需求替换。</p>

            <div
              style={{
                marginTop: 20,
                padding: 10,
                border: '1px solid rgba(136, 136, 136, 0.3)',
                borderRadius: 4,
                height: 300,
                overflowY: 'auto',
                backgroundColor: 'rgba(255,255,255,0.05)',
                position: 'relative',
                zIndex: 0,
                whiteSpace: 'pre-wrap',
                fontFamily: 'monospace',
                fontSize: 14,
                color: '#333',
              }}
              aria-live="polite"
              role="log"
            >
              <h4>日志模块 (SSE 消息)</h4>
              {sseMessages.length === 0 ? (
                <p>等待接收消息...</p>
              ) : (
                <ul style={{ paddingLeft: 20, margin: 0 }}>
                  {sseMessages.map((msg, idx) => (
                    <li key={idx} style={{ wordBreak: 'break-word' }}>
                      {msg}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: 20 }}>
            <h2>设置页面</h2>

            <button
              onClick={resetAllPanels}
              style={{
                marginBottom: 20,
                padding: '8px 16px',
                backgroundColor: '#00e5ff',
                color: 'white',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              重置所有面板到默认
            </button>

            <div>
              <h3>重置单个面板位置</h3>
              {panels.map(({ id, left, top, width, height }) => {
                const defaultPanel = defaultPanels.find((dp) => dp.id === id);
                return (
                  <div
                    key={id}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: 10,
                      border: '1px solid rgba(136, 136, 136, 0.3)',
                      borderRadius: 4,
                      padding: 10,
                      maxWidth: 400,
                      backgroundColor: 'rgba(0,123,255,0.05)',
                    }}
                  >
                    <div>
                      <strong>{id}</strong>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        当前：{left},{top}，大小：{width}x{height}
                      </div>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        默认：{defaultPanel?.left},{defaultPanel?.top}，大小：
                        {defaultPanel?.width}x{defaultPanel?.height}
                      </div>
                    </div>
                    <button
                      onClick={() => resetSinglePanel(id)}
                      style={{
                        padding: '4px 10px',
                        borderRadius: 4,
                        border: 'none',
                        backgroundColor: '#28a745',
                        color: 'white',
                        cursor: 'pointer',
                      }}
                    >
                      重置
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  const frostedGlassBtnStyle = {
    width: 60,
    height: 60,
    borderRadius: '50%',
    border: '2px solid #00e5ff',
    backgroundColor: 'rgba(0, 229, 255, 0.15)',
    color: '#00e5ff',
    fontSize: 16,
    fontWeight: 'bold',
    cursor: showGlassTransition ? 'wait' : 'pointer',
    outline: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    userSelect: 'none',
    transition: 'background-color 0.3s, color 0.3s, box-shadow 0.3s',
    backdropFilter: 'blur(10px)',
    WebkitBackdropFilter: 'blur(10px)',
    boxShadow: '0 4px 30px rgba(0, 229, 255, 0.4)',
    pointerEvents: showGlassTransition ? 'none' : 'auto',
  };

  const frostedGlassBtnSelectedStyle = {
    ...frostedGlassBtnStyle,
    backgroundColor: 'rgba(0, 229, 255, 0.35)',
    color: 'white',
    boxShadow: '0 6px 40px rgba(0, 229, 255, 0.7)',
  };

  return (
    <ButlerProvider>
      <RecorderProvider>
        {/* 壁纸管理器 */}
        <WallpaperManager 
          selectedMenu={selectedMenu}
          showGlassTransition={showGlassTransition}
        />
        
        <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
          <Clock /> {/* 右上角时间显示 */}

          <h1 style={{ color: 'white' }}>AI-Sphere-Butler 全能管家</h1>

          {/* SSE 监听 */}
          <SseListener onMessage={handleSseMessage} />

          {/* 菜单导航 */}
          <div
            style={{
              display: 'flex',
              gap: 20,
              marginTop: 20,
              marginBottom: 20,
            }}
            role="menu"
          >
            {[
              { key: 'home', label: '主页' },
              { key: 'butler', label: '管家' },
              { key: 'car', label: '车' },
              { key: 'homeassistant', label: 'HA' },
              { key: 'ops', label: '运维' },
              { key: 'settings', label: '设置' },
            ].map(({ key, label }) => (
              <button
                key={key}
                onClick={() => handleMenuClick(key)}
                style={
                  selectedMenu === key
                    ? frostedGlassBtnSelectedStyle
                    : frostedGlassBtnStyle
                }
                aria-label={label}
                aria-haspopup="true"
                type="button"
              >
                {label}
              </button>
            ))}
          </div>

          {/* 透明毛玻璃过渡动画遮罩 */}
          {showGlassTransition && (
            <div
              className="glass-transition-overlay"
              aria-hidden="true"
              role="presentation"
              style={{
                position: 'fixed',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                backdropFilter: 'blur(15px)',
                WebkitBackdropFilter: 'blur(15px)',
                zIndex: 50,
                transition: 'opacity 0.4s ease',
              }}
            />
          )}

          {/* 渲染菜单页面内容 */}
          {renderContent()}

          {/* 保持WebRTC连接 */}
          {webrtcConnected && selectedMenu !== 'home' && (
            <WebRTCPlayer
              renderVideo={false}
              keepConnection={true}
              onStart={handleWebrtcConnect}
              onStop={handleWebrtcDisconnect}
            />
          )}

          {/* 全局渲染摄像头和当前管家面板 */}
          {panels.map(({ id, left, top, width, height, zIndex }) => {
            if (id !== 'camera' && id !== 'webrtc') return null;

            console.log(`[Render] 渲染全局面板: ${id} @ (${left},${top}), 大小: ${width}x${height}, zIndex: ${zIndex}`);

            switch (id) {
              case 'webrtc':
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <WebRTCPlayer
                      renderVideo={true}
                      keepConnection={true}
                      onStart={handleWebrtcConnect}
                      onStop={handleWebrtcDisconnect}
                    />
                  </DraggableResizablePanel>
                );
              case 'camera':
                return (
                  <DraggableResizablePanel
                    key={id}
                    id={id}
                    left={left}
                    top={top}
                    width={width}
                    height={height}
                    zIndex={zIndex}
                    movePanel={movePanel}
                    bringToFront={bringToFront}
                    resizePanel={resizePanel}
                  >
                    <CameraControl />
                  </DraggableResizablePanel>
                );
              default:
                return null;
            }
          })}
        </div>
      </RecorderProvider>
    </ButlerProvider>
  );
}
