import React, { useEffect } from 'react';
import { useButler } from './ButlersContext';

export default function ButlerShowcase() {
  const { activeButlerId, butlers } = useButler();

  useEffect(() => {
    console.log('ButlerShowcase 激活管家ID:', activeButlerId);
  }, [activeButlerId]);

  const activeButler = butlers.find(b => b.id === activeButlerId);

  return (
    <div style={{ color: 'white', padding: 20 }}>
      <h2>当前激活管家</h2>
      <div>{activeButler ? activeButler.name : '无'}</div>
      {activeButler && activeButler.type === 'video' && (
        <video
          key={activeButler.id}
          src={activeButler.src}
          autoPlay
          loop
          muted
          width={320}
          style={{ borderRadius: 12, marginTop: 12 }}
        />
      )}
    </div>
  );
}
