import './index.css';
import React, { useState, useRef, useEffect } from 'react';

import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

// 组件导入，保持不变
import AudioTranscriber from './components/AudioTranscriber';
import CameraControl from './components/CameraControl';
import WebRTCPlayer from './components/OldWebRTC';
import FloatingContainer from './components/FloatingContainer';
import SpeechRecognizer from './components/SpeechRecognizer';
import SseListener from './components/SseListener';
import ButlerShowcase from './components/ButlerShowcase';
import Clock from './components/Clock';

const ItemTypes = {
  PANEL: 'panel',
};

const panelTitles = {
  audio: { cn: '音频识别', en: 'AUDIO' },
  speech: { cn: '语音识别', en: 'SPEECH' },
  webrtc: { cn: '当前管家', en: 'Butler' },
};

function DraggableResizablePanel({
  id, left, top, width = 320, height = 200, zIndex,
  movePanel, bringToFront, resizePanel, children
}) {
  const dragDropRef = React.useRef(null);

  // React DnD 等逻辑保留，略（可参考你原代码）

  // 这里为简化演示省略useDrag/useDrop细节，实现你原代码逻辑即可
  // 你可直接用你已有DraggableResizablePanel组件代码，不影响视频过渡功能

  // 下面简单渲染，不支持拖拽，方便运行
  return (
    <div
      style={{
        position: 'absolute',
        left,
        top,
        width,
        height,
        zIndex,
        border: '1px solid #00e5ff',
        borderRadius: 4,
        backgroundColor: 'rgba(0,0,0,0.5)',
        color: 'white',
        userSelect: 'none',
        overflow: 'hidden',
      }}
      onMouseDown={() => bringToFront(id)}
    >
      <div
        style={{
          height: 30,
          backgroundColor: 'rgba(0,0,0,0.7)',
          fontWeight: 'bold',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          userSelect: 'none',
          borderTopLeftRadius: 4,
          borderTopRightRadius: 4,
          paddingTop: 2,
          paddingBottom: 2,
          fontSize: 14,
        }}
      >
        <div>{panelTitles[id]?.cn || id}</div>
        <div style={{ fontSize: 10, opacity: 0.7 }}>{panelTitles[id]?.en || id.toUpperCase()}</div>
      </div>
      <div style={{ padding: 10, height: `calc(100% - 30px)`, overflowY: 'auto' }}>
        {children}
      </div>
    </div>
  );
}

function loadPanels() {
  try {
    const saved = localStorage.getItem('panels');
    if (saved) {
      const panels = JSON.parse(saved);
      return panels.filter(p => p.id !== 'camera' && p.id !== 'floating');
    }
  } catch (e) {
    console.warn('读取面板位置失败', e);
  }
  return [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
  ];
}

export default function App() {
  const [sseMessages, setSseMessages] = useState([]);
  const [panels, setPanels] = useState(loadPanels);
  const maxZIndex = useRef(1);
  const [selectedMenu, setSelectedMenu] = useState('home');

  // 新增：控制视频动画是否显示
  const [showLightningVideo, setShowLightningVideo] = useState(false);
  // 暂存目标菜单
  const pendingMenuRef = useRef(null);
  // 视频元素ref
  const videoRef = useRef(null);

  const handleSendText = (text) => {
    console.log('识别到的文本:', text);
  };

  const handleSseMessage = (data) => {
    setSseMessages(prev => [...prev, data]);
  };

  const movePanel = (id, left, top) => {
    setPanels(prev =>
      prev.map(p => (p.id === id ? { ...p, left, top } : p))
    );
  };

  const resizePanel = (id, width, height) => {
    setPanels(prev =>
      prev.map(p => (p.id === id ? { ...p, width, height } : p))
    );
  };

  const bringToFront = (id) => {
    maxZIndex.current += 1;
    setPanels(prev =>
      prev.map(p => (p.id === id ? { ...p, zIndex: maxZIndex.current } : p))
    );
  };

  useEffect(() => {
    localStorage.setItem('panels', JSON.stringify(panels));
  }, [panels]);

  // 点击菜单，播放视频动画，等待结束切换菜单
  const handleMenuClick = (menuKey) => {
    if (menuKey === selectedMenu || showLightningVideo) return;

    pendingMenuRef.current = menuKey;
    setShowLightningVideo(true);

    // React way:监听视频播放结束事件切换菜单，防止定时失灵
    if (videoRef.current) {
      videoRef.current.currentTime = 0;
      videoRef.current.play();
    }
  };

  // 视频播放完毕
  const onVideoEnded = () => {
    setSelectedMenu(pendingMenuRef.current);
    setShowLightningVideo(false);
    pendingMenuRef.current = null;
  };

  const defaultPanels = [
    { id: 'audio', left: 20, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'speech', left: 370, top: 20, width: 320, height: 200, zIndex: 1 },
    { id: 'webrtc', left: 370, top: 320, width: 320, height: 200, zIndex: 1 },
  ];

  const resetAllPanels = () => {
    localStorage.removeItem('panels');
    setPanels(defaultPanels);
  };

  const resetSinglePanel = (id) => {
    setPanels((prev) =>
      prev.map((p) => {
        if (p.id === id) {
          const defaultPanel = defaultPanels.find((dp) => dp.id === id);
          return defaultPanel || p;
        }
        return p;
      })
    );
  };

  const renderContent = () => {
    switch (selectedMenu) {
      case 'home':
        return (
          <>
            {panels.map(({ id, left, top, width, height, zIndex }) => {
              if (id === 'floating') return null;
              switch (id) {
                case 'audio':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <AudioTranscriber />
                    </DraggableResizablePanel>
                  );
                case 'speech':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <SpeechRecognizer onSendText={handleSendText} />
                    </DraggableResizablePanel>
                  );
                case 'webrtc':
                  return (
                    <DraggableResizablePanel
                      key={id}
                      id={id}
                      left={left}
                      top={top}
                      width={width}
                      height={height}
                      zIndex={zIndex}
                      movePanel={movePanel}
                      bringToFront={bringToFront}
                      resizePanel={resizePanel}
                    >
                      <WebRTCPlayer />
                    </DraggableResizablePanel>
                  );
                default:
                  return null;
              }
            })}
          </>
        );
      case 'butler':
        return (
          <div style={{ padding: 20 }}>
            <h2>管家页面</h2>
            <ButlerShowcase />
          </div>
        );
      case 'ops':
        return (
          <div style={{ padding: 20 }}>
            <h2>运维页面</h2>
            <p>这里是运维页面的内容，请根据需求替换。</p>
            <div
              style={{
                marginTop: 20,
                padding: 10,
                border: '1px solid rgba(136, 136, 136, 0.3)',
                borderRadius: 4,
                height: 300,
                overflowY: 'auto',
                backgroundColor: 'rgba(255,255,255,0.05)',
                position: 'relative',
                zIndex: 0,
                whiteSpace: 'pre-wrap',
                fontFamily: 'monospace',
                fontSize: 14,
                color: '#333',
              }}
              aria-live="polite"
              role="log"
            >
              <h4>日志模块 (SSE 消息)</h4>
              {sseMessages.length === 0 ? (
                <p>等待接收消息...</p>
              ) : (
                <ul style={{ paddingLeft: 20, margin: 0 }}>
                  {sseMessages.map((msg, idx) => (
                    <li key={idx} style={{ wordBreak: 'break-word' }}>
                      {msg}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        );
      case 'settings':
        return (
          <div style={{ padding: 20 }}>
            <h2>设置页面</h2>
            <button
              onClick={resetAllPanels}
              style={{
                marginBottom: 20,
                padding: '8px 16px',
                backgroundColor: '#00e5ff',
                color: 'white',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              重置所有面板到默认
            </button>
            <div>
              <h3>重置单个面板位置</h3>
              {panels.map(({ id, left, top, width, height }) => {
                const defaultPanel = defaultPanels.find((dp) => dp.id === id);
                return (
                  <div
                    key={id}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: 10,
                      border: '1px solid rgba(136, 136, 136, 0.3)',
                      borderRadius: 4,
                      padding: 10,
                      maxWidth: 400,
                      backgroundColor: 'rgba(0,123,255,0.05)',
                    }}
                  >
                    <div>
                      <strong>{id}</strong>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        当前：{left},{top}，大小：{width}x{height}
                      </div>
                      <div style={{ fontSize: 12, color: '#555' }}>
                        默认：{defaultPanel?.left},{defaultPanel?.top}，大小：
                        {defaultPanel?.width}x{defaultPanel?.height}
                      </div>
                    </div>
                    <button
                      onClick={() => resetSinglePanel(id)}
                      style={{
                        padding: '4px 10px',
                        borderRadius: 4,
                        border: 'none',
                        backgroundColor: '#28a745',
                        color: 'white',
                        cursor: 'pointer',
                      }}
                    >
                      重置
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: 20, position: 'relative', minHeight: '100vh' }}>
      <Clock />
      <h1 style={{ color: 'white' }}>AI-Sphere-Butler 全能管家</h1>
      <SseListener onMessage={handleSseMessage} />
      <div
        style={{
          display: 'flex',
          gap: 20,
          marginTop: 20,
          marginBottom: 20,
        }}
        role="menu"
      >
        {[
          { key: 'home', label: '主页' },
          { key: 'butler', label: '管家' },
          { key: 'ops', label: '运维' },
          { key: 'settings', label: '设置' },
        ].map(({ key, label }) => (
          <button
            key={key}
            onClick={() => handleMenuClick(key)}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: '2px solid #00e5ff',
              backgroundColor: selectedMenu === key ? '#00e5ff' : '#000000',
              color: selectedMenu === key ? 'white' : '#00e5ff',
              fontSize: 16,
              fontWeight: 'bold',
              cursor: showLightningVideo ? 'wait' : 'pointer',
              outline: 'none',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none',
              transition: 'background-color 0.3s, color 0.3s',
              pointerEvents: showLightningVideo ? 'none' : 'auto',
            }}
            aria-label={label}
            aria-haspopup="true"
          >
            {label}
          </button>
        ))}
      </div>

      {/* 视频过渡动画 */}
      {showLightningVideo && (
        <div className="lightning-video-overlay" aria-hidden="true">
          <video
            ref={videoRef}
            src="/lightning.mp4"
            muted
            autoPlay
            playsInline
            onEnded={onVideoEnded}
            preload="auto"
          />
        </div>
      )}

      {/* 渲染页面内容 */}
      {renderContent()}

      <CameraControl />
    </div>
  );
}
