import React, { useEffect, useRef, useState } from 'react';

export default function OldWebRTC({ renderVideo = true, keepConnection = false }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);
  const pcRef = useRef(null);
  const textareaRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const animationFrameIdRef = useRef(null);
  const reconnectTimerRef = useRef(null);
  const pipVideoRef = useRef(null); // 自定义画中画 video 引用
  const hiddenVideoRef = useRef(null); // 标准画中画隐藏视频元素
  const isPiPModeChangingRef = useRef(false); // 画中画模式切换中标志
  const pipExitReasonRef = useRef(''); // 画中画退出原因
  const isStoppingRef = useRef(false); // 应用正在停止的标志

  const containerRef = useRef(null);

  const [useStun, setUseStun] = useState(false);
  const [started, setStarted] = useState(false);
  const [zoomWidth, setZoomWidth] = useState(600);
  const [videoReady, setVideoReady] = useState(false);
  const [alphaReady, setAlphaReady] = useState(false);
  const [processingMode, setProcessingMode] = useState('rgba');
  const [errorMessage, setErrorMessage] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [connectionAttempts, setConnectionAttempts] = useState(0);
  const [maxConnectionAttempts] = useState(5);
  const [enablePip, setEnablePip] = useState(false); // 自定义画中画开关
  const [isPiPMode, setIsPiPMode] = useState(false); // 标准画中画模式
  const [isLoading, setIsLoading] = useState(false); // 加载状态

  // 标记开始及视频就绪状态，方便外部跳出循环等
  const isStartedRef = useRef(false);
  const isVideoReadyRef = useRef(false);
  
  // 记录画中画启用前的 canvas 状态
  const pipCanvasStreamRef = useRef(null);
  const wasCanvasVisibleRef = useRef(true);
  const isPipActiveRef = useRef(false); // 画中画激活状态

  // 新增：边缘平滑参数
  const [edgeSmoothing, setEdgeSmoothing] = useState(2);
  // 新增：绿幕敏感度参数
  const [greenThreshold, setGreenThreshold] = useState(1.3);

  // 新增：抠图色块选择，默认绿
  const [chromaKeyColor, setChromaKeyColor] = useState('green'); // 'green' | 'blue' | 'red'
  // 新增：色盘选择的自定义颜色
  const [customChromaKeyColor, setCustomChromaKeyColor] = useState('#00FF00'); // 默认绿色
  // 新增：自定义颜色模式开关
  const [useCustomColor, setUseCustomColor] = useState(false);

  const SIGNALING_URL = '/offer';
  const zoomStep = 50;

  const [chatPos, setChatPos] = useState(() => {
    try {
      const saved = localStorage.getItem('chatbox-position');
      if (saved) {
        const pos = JSON.parse(saved);
        if (typeof pos.left === 'number' && typeof pos.top === 'number') {
          return pos;
        }
      }
    } catch {}
    return { left: 20, top: 20 };
  });

  const draggingRef = useRef(false);
  const dragOffsetRef = useRef({ x: 0, y: 0 });

  function onDragStart(e) {
    if (e.target.tagName.toLowerCase() === 'textarea') {
      draggingRef.current = false;
      return;
    }
    draggingRef.current = true;
    const targetRect = e.currentTarget.getBoundingClientRect();
    if (e.type === 'touchstart' && e.touches?.[0]) {
      dragOffsetRef.current = {
        x: e.touches[0].clientX - targetRect.left,
        y: e.touches[0].clientY - targetRect.top,
      };
    } else {
      dragOffsetRef.current = {
        x: e.clientX - targetRect.left,
        y: e.clientY - targetRect.top,
      };
    }
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragMove(e) {
    if (!draggingRef.current) return;
    let clientX, clientY;
    if (e.type === 'touchmove' && e.touches?.[0]) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else if (e.type === 'mousemove') {
      clientX = e.clientX;
      clientY = e.clientY;
    } else {
      return;
    }
    if (!containerRef.current || !e.currentTarget) return;
    const containerRect = containerRef.current.getBoundingClientRect();
    const chatRect = e.currentTarget.getBoundingClientRect();
    const chatWidth = chatRect.width;
    const chatHeight = chatRect.height;
    let newLeft = clientX - containerRect.left - dragOffsetRef.current.x;
    let newTop = clientY - containerRef.current.offsetTop - dragOffsetRef.current.y;
    newLeft = Math.min(Math.max(0, newLeft), containerRect.width - chatWidth);
    newTop = Math.min(Math.max(0, newTop), containerRect.height - chatHeight);
    setChatPos({ left: newLeft, top: newTop });
    e.preventDefault();
    e.stopPropagation();
  }

  function onDragEnd(e) {
    if (draggingRef.current) {
      draggingRef.current = false;
      localStorage.setItem('chatbox-position', JSON.stringify(chatPos));
      e.preventDefault();
      e.stopPropagation();
    }
  }

  function calculateTransparencyPercentage(data) {
    if (!data || data.length === 0) return '0.00';
    let transparentPixels = 0;
    for (let i = 3; i < data.length; i += 4) {
      if (data[i] === 0) {
        transparentPixels++;
      }
    }
    return ((transparentPixels / (data.length / 4)) * 100).toFixed(2);
  }

  // 改进的绿幕抠图算法，增加边缘平滑处理，支持多色抠图（绿、蓝、红）和自定义颜色
  function processVideoFrameWithGreenScreen() {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    if (!video || !canvas || !ctx || !isStartedRef.current || !isVideoReadyRef.current) {
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrameWithGreenScreen);
      return;
    }
    if (video.paused || video.ended) {
      console.log('视频已暂停或结束，继续处理帧...');
      animationFrameIdRef.current = requestAnimationFrame(processVideoFrameWithGreenScreen);
      return;
    }
    try {
      if (canvas.width !== video.videoWidth || canvas.height !== video.videoHeight) {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
      }
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      const frame = ctx.getImageData(0, 0, canvas.width, canvas.height);
      const data = frame.data;

      // 解析自定义颜色为 RGB 值
      const hexToRgb = (hex) => {
        const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
        return result ? {
          r: parseInt(result[1], 16),
          g: parseInt(result[2], 16),
          b: parseInt(result[3], 16)
        } : null;
      };
      
      // 自定义颜色的 RGB 值
      const customRgb = hexToRgb(customChromaKeyColor);
      const customR = customRgb?.r || 0;
      const customG = customRgb?.g || 255;
      const customB = customRgb?.b || 0;
      
      // 选择抠图色块阈值和判断函数
      // 这里使用简易的色块抠图逻辑，根据选择不同色块，调整判定条件
      // 参数可以根据需要微调
      function shouldMakeTransparent(r, g, b) {
        const threshold = 100; // 基础阈值
        const ratio = greenThreshold; // 之前的阈值参数，复用
        
        if (useCustomColor) {
          // 自定义颜色模式：计算与目标颜色的欧氏距离
          const colorDistance = Math.sqrt(
            Math.pow(r - customR, 2) + 
            Math.pow(g - customG, 2) + 
            Math.pow(b - customB, 2)
          );
          // 距离越小越接近目标颜色，越应该透明
          return colorDistance < threshold * ratio;
        }
        
        // 预设颜色模式
        switch (chromaKeyColor) {
          case 'green':
            return g > threshold && r < threshold && b < threshold && g > r * ratio && g > b * ratio;
          case 'blue':
            return b > threshold && r < threshold && g < threshold && b > r * ratio && b > g * ratio;
          case 'red':
            return r > threshold && g < threshold && b < threshold && r > g * ratio && r > b * ratio;
          default:
            return false;
        }
      }

      // 第一遍处理：基本抠图色块检测
      for (let i = 0; i < data.length; i += 4) {
        const r = data[i];
        const g = data[i + 1];
        const b = data[i + 2];

        if (shouldMakeTransparent(r, g, b)) {
          data[i + 3] = 0; // 完全透明
        }
      }

      // 第二遍处理：边缘平滑（羽化效果）
      if (edgeSmoothing > 0) {
        const tempData = new Uint8ClampedArray(data);
        const width = canvas.width;
        const height = canvas.height;
        const radius = edgeSmoothing;

        for (let y = 0; y < height; y++) {
          for (let x = 0; x < width; x++) {
            const i = (y * width + x) * 4;
            if (data[i + 3] === 0) continue; // 已经是透明的，不需要处理

            // 计算周围的透明度
            let transparentNeighbors = 0;
            let totalNeighbors = 0;

            for (let dy = -radius; dy <= radius; dy++) {
              for (let dx = -radius; dx <= radius; dx++) {
                const nx = x + dx;
                const ny = y + dy;

                if (nx < 0 || nx >= width || ny < 0 || ny >= height) continue;

                const ni = (ny * width + nx) * 4;
                if (tempData[ni + 3] === 0) {
                  transparentNeighbors++;
                }
                totalNeighbors++;
              }
            }

            // 如果周围有透明像素，说明这是边缘，应用羽化效果
            if (transparentNeighbors > 0 && totalNeighbors > 0) {
              const ratio = transparentNeighbors / totalNeighbors;
              data[i + 3] = Math.round(data[i + 3] * (1 - ratio));
            }
          }
        }
      }

      ctx.putImageData(frame, 0, 0);

      const transparencyPercentage = calculateTransparencyPercentage(data);
      setDebugInfo(`抠图透明度: ${transparencyPercentage}% | 边缘平滑: ${edgeSmoothing} | 阈值: ${greenThreshold} | 色块: ${useCustomColor ? '自定义' : chromaKeyColor}${useCustomColor ? `(${customChromaKeyColor})` : ''}`);
      setAlphaReady(parseFloat(transparencyPercentage) > 1);
    } catch (e) {
      console.error('抠图处理异常:', e);
      setErrorMessage('视频处理异常，请检查视频源');
    }
    animationFrameIdRef.current = requestAnimationFrame(processVideoFrameWithGreenScreen);
  }

  function fallbackToCanvasDrawing() {
    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
    }
    processVideoFrameWithGreenScreen();
  }

  async function negotiate() {
    const pc = pcRef.current;
    if (!pc) return;
    pc.addTransceiver('video', { direction: 'recvonly' });
    pc.addTransceiver('audio', { direction: 'recvonly' });
    try {
      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      await new Promise((resolve) => {
        if (pc.iceGatheringState === 'complete') {
          resolve();
        } else {
          const checkState = () => {
            if (pc.iceGatheringState === 'complete') {
              pc.removeEventListener('icegatheringstatechange', checkState);
              resolve();
            }
          };
          pc.addEventListener('icegatheringstatechange', checkState);
        }
      });
      const response = await fetch(SIGNALING_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(pc.localDescription),
      });
      if (!response.ok) throw new Error(`服务器响应错误: ${response.status}`);
      const answer = await response.json();
      await pc.setRemoteDescription(answer);
    } catch (e) {
      setErrorMessage(`WebRTC连接失败: ${e.message}`);
      if (connectionAttempts < maxConnectionAttempts) {
        setConnectionAttempts(connectionAttempts + 1);
        reconnectTimerRef.current = setTimeout(() => {
          if (isStartedRef.current) {
            stop();
            start();
          }
        }, 2000);
      } else {
        alert('连接失败，请检查网络连接或刷新页面');
      }
    }
  }

  function zoomIn() {
    setZoomWidth((w) => w + zoomStep);
  }

  function zoomOut() {
    setZoomWidth((w) => (w - zoomStep > 0 ? w - zoomStep : w));
  }

  // 改进的标准画中画实现，增加稳定性和详细日志
  async function togglePictureInPicture() {
    // 如果正在切换模式，不重复操作
    if (isPiPModeChangingRef.current) {
      console.log('忽略画中画切换请求：正在切换中');
      return;
    }
    
    isPiPModeChangingRef.current = true;
    pipExitReasonRef.current = '';
    
    try {
      if (document.pictureInPictureElement) {
        // 如果已有画中画元素，则退出
        isStoppingRef.current = true;
        console.log('尝试退出标准画中画模式');
        await document.exitPictureInPicture();
        console.log('已成功退出标准画中画模式');
        isStoppingRef.current = false;
        setIsPiPMode(false);
      } else {
        // 进入标准画中画模式
        console.log('尝试进入标准画中画模式');
        
        // 检查浏览器是否支持画中画
        if (!document.pictureInPictureEnabled) {
          throw new Error('浏览器不支持画中画功能');
        }
        
        // 创建或获取隐藏的视频元素
        if (!hiddenVideoRef.current) {
          const videoElement = document.createElement('video');
          videoElement.style.display = 'none';
          document.body.appendChild(videoElement);
          hiddenVideoRef.current = videoElement;
          
          // 添加事件监听，处理画中画状态变化
          videoElement.addEventListener('enterpictureinpicture', () => {
            console.log('进入标准画中画模式 - 视频元素状态:', videoElement.readyState);
            console.log('画中画元素尺寸:', videoElement.videoWidth, 'x', videoElement.videoHeight);
            setIsPiPMode(true);
            isPiPModeChangingRef.current = false;
          });
          
          videoElement.addEventListener('leavepictureinpicture', () => {
            console.log('退出标准画中画模式 - 原因:', pipExitReasonRef.current || '未知');
            console.log('画中画退出时视频元素状态:', videoElement.readyState);
            setIsPiPMode(false);
            isPiPModeChangingRef.current = false;
          });
          
          // 添加错误监听
          videoElement.addEventListener('error', (e) => {
            console.error('画中画视频元素错误:', e);
            console.error('错误详情:', videoElement.error);
            setErrorMessage('画中画视频元素错误');
            isPiPModeChangingRef.current = false;
          });
          
          // 添加暂停事件监听
          videoElement.addEventListener('pause', () => {
            console.log('画中画视频暂停');
            if (isPiPMode) {
              pipExitReasonRef.current = '视频暂停';
              // 尝试继续播放
              videoElement.play().catch(err => {
                console.error('画中画视频暂停后尝试播放失败:', err);
              });
            }
          });
          
          // 添加 ended 事件监听
          videoElement.addEventListener('ended', () => {
            console.log('画中画视频播放结束');
            if (isPiPMode) {
              pipExitReasonRef.current = '视频播放结束';
            }
          });
          
          // 添加 loadedmetadata 事件监听
          videoElement.addEventListener('loadedmetadata', () => {
            console.log('画中画视频元数据加载完成:', 
              videoElement.videoWidth, 'x', videoElement.videoHeight, 
              'duration:', videoElement.duration);
          });
          
          // 添加 timeupdate 事件监听，用于检测视频是否在播放
          let lastTime = 0;
          videoElement.addEventListener('timeupdate', () => {
            if (isPiPMode && videoElement.currentTime === lastTime && !videoElement.paused) {
              console.log('画中画视频时间未更新，可能已停止');
              pipExitReasonRef.current = '视频时间未更新';
            }
            lastTime = videoElement.currentTime;
          });
        }
        
        // 确保Canvas已准备好
        if (!canvasRef.current || !ctxRef.current) {
          setErrorMessage('Canvas未初始化，无法启动画中画');
          isPiPModeChangingRef.current = false;
          return;
        }
        
        // 检查Canvas是否有内容
        if (canvasRef.current.width === 0 || canvasRef.current.height === 0) {
          console.error('Canvas尺寸为0，无法启动画中画');
          setErrorMessage('Canvas尺寸为0，无法启动画中画');
          isPiPModeChangingRef.current = false;
          return;
        }
        
        // 获取Canvas流并设置到隐藏视频元素
        const stream = canvasRef.current.captureStream(30);
        hiddenVideoRef.current.srcObject = stream;
        console.log('创建Canvas流成功，帧率:', 30);
        
        // 保存当前流的引用，用于后续检查
        pipCanvasStreamRef.current = stream;
        
        // 检查流是否有效
        if (!stream || stream.getTracks().length === 0) {
          throw new Error('Canvas流为空或无效');
        }
        
        // 播放视频
        console.log('尝试播放画中画视频元素...');
        await hiddenVideoRef.current.play();
        console.log('画中画视频元素播放成功');
        
        // 进入画中画模式
        console.log('尝试请求进入画中画模式...');
        await hiddenVideoRef.current.requestPictureInPicture();
        console.log('成功进入画中画模式');
      }
    } catch (e) {
      console.error('标准画中画错误:', e);
      console.error('错误堆栈:', e.stack);
      setErrorMessage(`标准画中画错误: ${e.message}`);
      pipExitReasonRef.current = e.message;
      
      // 如果标准画中画失败，尝试使用自定义画中画
      if (!isPiPMode) {
        setEnablePip((v) => !v);
      }
    } finally {
      isPiPModeChangingRef.current = false;
    }
  }

  function start() {
    if (isStartedRef.current) return;
    const config = { sdpSemantics: 'unified-plan' };
    if (useStun) {
      config.iceServers = [{ urls: 'stun:stun.l.google.com:19302' }];
    }
    const pc = new RTCPeerConnection(config);
    pcRef.current = pc;
    isStartedRef.current = true;
    setConnectionAttempts(0);
    setIsLoading(true);
    setErrorMessage('');

    pc.addEventListener('connectionstatechange', () => {
      if (pc.connectionState === 'failed' || pc.connectionState === 'disconnected') {
        // 如果处于画中画模式，尝试重新连接而不是完全停止
        if (isPiPMode) {
          console.log('WebRTC连接断开，但处于画中画模式，尝试重新连接');
          setErrorMessage('WebRTC连接断开，尝试重连');
          if (!reconnectTimerRef.current) {
            reconnectTimerRef.current = setTimeout(() => {
              if (isStartedRef.current) {
                // 只重新协商连接，不停止整个应用
                negotiate();
              }
            }, 2000);
          }
        } else if (!isStoppingRef.current) {
          console.log('WebRTC连接断开，尝试重新连接');
          setErrorMessage('WebRTC连接断开，尝试重连');
          if (connectionAttempts < maxConnectionAttempts) {
            setConnectionAttempts(connectionAttempts + 1);
            reconnectTimerRef.current = setTimeout(() => {
              if (isStartedRef.current) {
                stop();
                start();
              }
            }, 2000);
          } else {
            setErrorMessage('连接尝试次数过多，连接已断开');
          }
        }
      } else if (pc.connectionState === 'connected') {
        console.log('WebRTC连接已建立');
        setErrorMessage('');
        setIsLoading(false);
      }
    });

    pc.addEventListener('track', (event) => {
      console.log('收到媒体轨道:', event.track.kind);
      if (event.track.kind === 'video') {
        videoRef.current.srcObject = event.streams[0];
        setVideoReady(true);
        isVideoReadyRef.current = true;
        if (!animationFrameIdRef.current) {
          fallbackToCanvasDrawing();
        }
      } else if (event.track.kind === 'audio') {
        audioRef.current.srcObject = event.streams[0];
      }
    });

    negotiate();
    setStarted(true);
  }

  function stop() {
    if (!isStartedRef.current) return;
    isStoppingRef.current = true; // 标记正在停止
    if (reconnectTimerRef.current) {
      clearTimeout(reconnectTimerRef.current);
      reconnectTimerRef.current = null;
    }
    if (animationFrameIdRef.current) {
      cancelAnimationFrame(animationFrameIdRef.current);
      animationFrameIdRef.current = null;
    }
    
    // 记录画中画退出原因
    if (isPiPMode) {
      pipExitReasonRef.current = '手动停止应用';
    }
    
    // 停止画中画流
    if (pipCanvasStreamRef.current) {
      pipCanvasStreamRef.current.getTracks().forEach(track => track.stop());
      pipCanvasStreamRef.current = null;
    }
    
    // 退出标准画中画模式
    if (document.pictureInPictureElement) {
      document.exitPictureInPicture().catch(e => {
        console.error('退出画中画模式失败:', e);
      });
    }
    
    // 停止媒体轨道
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    if (audioRef.current && audioRef.current.srcObject) {
      audioRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    
    // 关闭 RTCPeerConnection
    if (pcRef.current) {
      pcRef.current.close();
      pcRef.current = null;
    }
    
    // 重置状态
    setStarted(false);
    setVideoReady(false);
    isStartedRef.current = false;
    isVideoReadyRef.current = false;
    setIsLoading(false);
    
    // 如果不需要保持连接，则清理
    if (!keepConnection) {
      setUseStun(false);
      setZoomWidth(600);
      setAlphaReady(false);
      setProcessingMode('rgba');
      setErrorMessage('');
      setDebugInfo('');
      setConnectionAttempts(0);
      setEnablePip(false);
      setIsPiPMode(false);
    }
    
    isStoppingRef.current = false; // 标记停止完成
  }

  // 清理资源
  useEffect(() => {
    return () => {
      stop();
    };
  }, []);

  // 自定义画中画效果
  useEffect(() => {
    if (enablePip && canvasRef.current && videoRef.current) {
      // 保存当前 canvas 状态
      wasCanvasVisibleRef.current = canvasRef.current.style.display !== 'none';
      
      // 创建画中画元素
      if (!pipVideoRef.current) {
        const pipVideo = document.createElement('video');
        pipVideo.style.position = 'fixed';
        pipVideo.style.bottom = '20px';
        pipVideo.style.right = '20px';
        pipVideo.style.width = '200px';
        pipVideo.style.borderRadius = '8px';
        pipVideo.style.boxShadow = '0 4px 15px rgba(0,0,0,0.3)';
        pipVideo.style.zIndex = '1000';
        pipVideo.style.cursor = 'move';
        pipVideo.controls = false;
        pipVideo.muted = true;
        document.body.appendChild(pipVideo);
        pipVideoRef.current = pipVideo;
        
        // 添加拖动功能
        let isDragging = false;
        let offsetX, offsetY;
        
        pipVideo.addEventListener('mousedown', (e) => {
          isDragging = true;
          offsetX = e.clientX - pipVideo.getBoundingClientRect().left;
          offsetY = e.clientY - pipVideo.getBoundingClientRect().top;
          pipVideo.style.cursor = 'grabbing';
          e.preventDefault();
        });
        
        document.addEventListener('mousemove', (e) => {
          if (isDragging) {
            const x = e.clientX - offsetX;
            const y = e.clientY - offsetY;
            pipVideo.style.left = `${x}px`;
            pipVideo.style.top = `${y}px`;
            pipVideo.style.bottom = 'auto';
            pipVideo.style.right = 'auto';
          }
        });
        
        document.addEventListener('mouseup', () => {
          isDragging = false;
          pipVideo.style.cursor = 'move';
        });
        
        // 添加双击关闭
        pipVideo.addEventListener('dblclick', () => {
          setEnablePip(false);
        });
      }
      
      // 设置视频源
      if (canvasRef.current && canvasRef.current.captureStream) {
        const stream = canvasRef.current.captureStream(30);
        pipVideoRef.current.srcObject = stream;
        pipVideoRef.current.play().catch(e => {
          console.error('自定义画中画播放失败:', e);
        });
        isPipActiveRef.current = true;
        
        // 隐藏原 canvas
        if (wasCanvasVisibleRef.current) {
          canvasRef.current.style.display = 'none';
        }
      }
    } else if (!enablePip && pipVideoRef.current && isPipActiveRef.current) {
      // 停止画中画流
      if (pipVideoRef.current.srcObject) {
        pipVideoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
      
      // 移除画中画元素
      if (pipVideoRef.current.parentNode) {
        pipVideoRef.current.parentNode.removeChild(pipVideoRef.current);
      }
      pipVideoRef.current = null;
      isPipActiveRef.current = false;
      
      // 恢复 canvas 显示
      if (wasCanvasVisibleRef.current) {
        canvasRef.current.style.display = '';
      }
    }
  }, [enablePip]);

  // 处理窗口大小变化
  useEffect(() => {
    function handleResize() {
      // 调整聊天框位置，防止溢出
      if (containerRef.current) {
        const containerRect = containerRef.current.getBoundingClientRect();
        const chatWidth = 300; // 假设聊天框宽度
        const chatHeight = 200; // 假设聊天框高度
        
        if (chatPos.left + chatWidth > containerRect.width) {
          setChatPos(prev => ({
            ...prev,
            left: Math.max(0, containerRect.width - chatWidth)
          }));
        }
        
        if (chatPos.top + chatHeight > containerRect.height) {
          setChatPos(prev => ({
            ...prev,
            top: Math.max(0, containerRect.height - chatHeight)
          }));
        }
      }
    }
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [chatPos]);

  // 显示加载状态
  const LoadingIndicator = () => (
    <div className="loading-indicator">
      <div className="spinner"></div>
      <p>正在连接...</p>
    </div>
  );

  // 显示控制面板
  const ControlPanel = () => (
    <div className="control-panel">
      <button onClick={start} disabled={started}>
        开始
      </button>
      <button onClick={stop} disabled={!started}>
        停止
      </button>
      <button onClick={zoomIn}>放大</button>
      <button onClick={zoomOut}>缩小</button>
      <button onClick={togglePictureInPicture}>
        {isPiPMode ? '退出' : '进入'}画中画
      </button>
      <button onClick={() => setEnablePip(!enablePip)}>
        {enablePip ? '关闭' : '开启'}自定义画中画
      </button>
      <label>
        <input
          type="checkbox"
          checked={useStun}
          onChange={() => setUseStun(!useStun)}
        />
        使用STUN服务器
      </label>
    </div>
  );

  // 显示绿幕设置面板
  const ChromaKeySettings = () => (
    <div className="chroma-key-settings">
      <h3>绿幕设置</h3>
      <div>
        <label>边缘平滑:</label>
        <input
          type="range"
          min="0"
          max="10"
          step="1"
          value={edgeSmoothing}
          onChange={(e) => setEdgeSmoothing(Number(e.target.value))}
        />
        <span>{edgeSmoothing}</span>
      </div>
      <div>
        <label>敏感度:</label>
        <input
          type="range"
          min="0.5"
          max="5"
          step="0.1"
          value={greenThreshold}
          onChange={(e) => setGreenThreshold(Number(e.target.value))}
        />
        <span>{greenThreshold.toFixed(1)}</span>
      </div>
      <div>
        <label>抠图色块:</label>
        <select
          value={chromaKeyColor}
          onChange={(e) => setChromaKeyColor(e.target.value)}
          disabled={useCustomColor}
        >
          <option value="green">绿色</option>
          <option value="blue">蓝色</option>
          <option value="red">红色</option>
        </select>
      </div>
      <div>
        <label>
          <input
            type="checkbox"
            checked={useCustomColor}
            onChange={() => setUseCustomColor(!useCustomColor)}
          />
          使用自定义颜色
        </label>
        <input
          type="color"
          value={customChromaKeyColor}
          onChange={(e) => setCustomChromaKeyColor(e.target.value)}
          disabled={!useCustomColor}
        />
      </div>
    </div>
  );

  // 显示聊天框
  const ChatBox = () => (
    <div
      className="chat-box"
      style={{ left: `${chatPos.left}px`, top: `${chatPos.top}px` }}
      onMouseDown={onDragStart}
      onTouchStart={onDragStart}
      onMouseMove={onDragMove}
      onTouchMove={onDragMove}
      onMouseUp={onDragEnd}
      onTouchEnd={onDragEnd}
    >
      <div className="chat-header">
        <h4>聊天</h4>
      </div>
      <div className="chat-messages">
        <p>欢迎使用绿幕抠图系统!</p>
        <p>调整右侧控制面板可以优化抠图效果</p>
      </div>
      <div className="chat-input">
        <textarea
          ref={textareaRef}
          placeholder="输入消息..."
          rows={1}
        ></textarea>
        <button>发送</button>
      </div>
    </div>
  );

  return (
    <div className="webrtc-container" ref={containerRef}>
      <style>
        {`
          .webrtc-container {
            position: relative;
            width: 100%;
            height: 100vh;
            overflow: hidden;
            background-color: #1a1a1a;
            color: white;
            font-family: Arial, sans-serif;
          }
          
          .video-container {
            position: relative;
            width: 100%;
            height: 100%;
            display: flex;
            justify-content: center;
            align-items: center;
          }
          
          video {
            max-width: 100%;
            max-height: 100%;
            object-fit: contain;
            display: none;
          }
          
          canvas {
            max-width: ${zoomWidth}px;
            max-height: 100%;
            box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
            background-color: #000;
          }
          
          .control-panel {
            position: absolute;
            top: 10px;
            left: 10px;
            display: flex;
            flex-wrap: wrap;
            gap: 5px;
            zetta-index: 10;
          }
          
          .control-panel button,
          .control-panel label {
            padding: 5px 10px;
            background-color: #333;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
            font-size: 14px;
          }
          
          .control-panel button:hover,
          .control-panel label:hover {
            background-color: #555;
          }
          
          .control-panel button:disabled {
            background-color: #222;
            color: #666;
            cursor: not-allowed;
          }
          
          .chroma-key-settings {
            position: absolute;
            top: 10px;
            right: 10px;
            width: 250px;
            background-color: rgba(0, 0, 0, 0.7);
            padding: 10px;
            border-radius: 8px;
            z-index: 10;
          }
          
          .chroma-key-settings div {
            margin-bottom: 10px;
            display: flex;
            align-items: center;
            gap: 10px;
          }
          
          .chroma-key-settings label {
            min-width: 80px;
          }
          
          .chroma-key-settings input[type="range"] {
            flex-grow: 1;
          }
          
          .chat-box {
            position: absolute;
            width: 300px;
            height: 250px;
            background-color: rgba(0, 0, 0, 0.7);
            border-radius: 8px;
            display: flex;
            flex-direction: zetta;
            z-index: 10;
            cursor: move;
          }
          
          .chat-header {
            padding: 8px 10px;
            background-color: #222;
            border-top-left-radius: 8px;
            border-top-right-radius: 8px;
          }
          
          .chat-messages {
            flex-grow: 1;
            padding: 10px;
            overflow-y: auto;
          }
          
          .chat-input {
            display: flex;
            padding: 10px;
            border-top: 1px solid #333;
          }
          
          .chat-input textarea {
            flex-grow: 1;
            padding: 5px;
            background-color: #333;
            color: white;
            border: none;
            border-radius: 4px;
            resize: none;
          }
          
          .chat-input button {
            margin-left: 5px;
            padding: 5px 10px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
          }
          
          .chat-input button:hover {
            background-color: #45a049;
          }
          
          .loading-indicator {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            text-align: center;
            z-index: 20;
          }
          
          .spinner {
            width: 40px;
            height: 40px;
            margin: 0 auto 10px;
            border: 4px solid #f3f3f3;
            border-top: 4px solid #3498db;
            border-radius: 50%;
            animation: spin 1s linear infinite;
          }
          
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          
          .debug-info {
            position: absolute;
            bottom: 10px;
            left: 10px;
            background-color: rgba(0, 0, 0, 0.7);
            padding: 5px 10px;
            border-radius: 4px;
            font-size: 12px;
            z-index: 10;
          }
          
          .error-message {
            position: absolute;
            bottom: 10px;
            left: 50%;
            transform: translateX(-50%);
            background-color: rgba(255, 0, 0, 0.7);
            padding: 5px 10px;
            border-radius: 4px;
            font-size: 14px;
            z-index: 10;
          }
        `}
      </style>
      
      <div className="video-container">
        <video ref={videoRef} autoPlay playsInline muted></video>
        <canvas ref={canvasRef}></canvas>
        <audio ref={audioRef} autoPlay></audio>
      </div>
      
      <ControlPanel />
      <ChromaKeySettings />
      <ChatBox />
      
      {isLoading && <LoadingIndicator />}
      
      {errorMessage && (
        <div className="error-message">{errorMessage}</div>
      )}
      
      {debugInfo && (
        <div className="debug-info">{debugInfo}</div>
      )}
    </div>
  );
}
