// components/WallpaperManager.jsx (临时测试版本)
const WallpaperManager = ({ selectedMenu, showGlassTransition }) => {
    return (
      <div 
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          zIndex: -1,
          backgroundColor: 'rgba(0,0,255,0.3)' // 蓝色半透明背景，帮助确认容器位置
        }}
      >
        {/* 使用img标签而非background-image */}
        <img 
          src="https://picsum.photos/1920/1080" // 测试图片
          alt="背景壁纸"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            opacity: 0.8
          }}
        />
      </div>
    );
  };
